                {sanskrit : "धर्मे नष्टे कुलं कृत्स्नमधर्मोऽभिभवत्युत ||१- ४०||", engltranslit : "dharme naShTe kula.n kR^itsnamadharmo.abhibhavatyuta .. 1\-40.."},
                {sanskrit : "अधर्माभिभवात्कृष्ण प्रदुष्यन्ति कुलस्त्रियः |", engltranslit : "adharmAbhibhavAtkR^iShNa praduShyanti kulastriyaH ."},
                {sanskrit : "स्त्रीषु दुष्टासु वार्ष्णेय जायते वर्णसङ्करः ||१- ४१||", engltranslit : "strIShu duShTAsu vArShNeya jAyate varNasaN^karaH .. 1\-41.."},
                {sanskrit : "सङ्करो नरकायैव कुलघ्नानां कुलस्य च |", engltranslit : "saN^karo narakAyaiva kulaghnAnAM kulasya cha ."},
                {sanskrit : "पतन्ति पितरो ह्येषां लुप्तपिण्डोदकक्रियाः ||१- ४२||", engltranslit : "patanti pitaro hyeShAM luptapiNDodakakriyAH .. 1\-42.."},
                {sanskrit : "दोषैरेतैः कुलघ्नानां वर्णसङ्करकारकैः |", engltranslit : "doShairetaiH kulaghnAnAM varNasaN^karakArakaiH ."},
                {sanskrit : "उत्साद्यन्ते जातिधर्माः कुलधर्माश्च शाश्वताः ||१- ४३||", engltranslit : "utsAdyante jAtidharmAH kuladharmAshcha shAshvatAH .. 1\-43.."},
                {sanskrit : "उत्सन्नकुलधर्माणां मनुष्याणां जनार्दन |", engltranslit : "utsannakuladharmANAM manuShyANA.n janArdana ."},
                {sanskrit : "नरके नियतं वासो भवतीत्यनुशुश्रुम ||१- ४४||orनरकेऽनियतं", engltranslit : "narake niyata.n vAso bhavatItyanushushruma .. 1\-44..##or##narake.aniyataM"},
                {sanskrit : "अहो बत महत्पापं कर्तुं व्यवसिता वयम् |", engltranslit : "aho bata mahatpApa.n kartuM vyavasitA vayam.h ."},
                {sanskrit : "यद्राज्यसुखलोभेन हन्तुं स्वजनमुद्यताः ||१- ४५||", engltranslit : "yadrAjyasukhalobhena hantuM svajanamudyatAH .. 1\-45.."},
                {sanskrit : "यदि मामप्रतीकारमशस्त्रं शस्त्रपाणयः |", engltranslit : "yadi mAmapratIkAramashastra.n shastrapANayaH ."},
                {sanskrit : "धार्तराष्ट्रा रणे हन्युस्तन्मे क्षेमतरं भवेत् ||१- ४६||", engltranslit : "dhArtarAShTrA raNe hanyustanme kShemataraM bhavet.h .. 1\-46.."},
                {sanskrit : "सञ्जय उवाच |", engltranslit : "sa~njaya uvAcha ."},
                {sanskrit : "एवमुक्त्वार्जुनः सङ्ख्ये रथोपस्थ उपाविशत् |", engltranslit : "evamuktvArjunaH saN^khye rathopastha upAvishat.h ."},
                {sanskrit : "विसृज्य सशरं चापं शोकसंविग्नमानसः ||१- ४७||", engltranslit : "visR^ijya sashara.n chApa.n shokasa.nvignamAnasaH .. 1\-47.."},
                {sanskrit : "ॐ तत्सदिति श्रीमद्भगवद्गीतासूपनिषत्सु", engltranslit : "AUM tatsaditi shrImadbhagavadgItAsUpaniShatsu"},
                {sanskrit : "ब्रह्मविद्यायां योगशास्त्रे श्रीकृष्णार्जुनसंवादे", engltranslit : "brahmavidyAyA.n yogashAstre shrIkR^iShNArjunasa.nvAde"},
                {sanskrit : "अर्जुनविषादयोगो नाम प्रथमोऽध्यायः ||१||", engltranslit : "arjunaviShAdayogo nAma prathamo.adhyAyaH .. 1.."},
                {sanskrit : "अथ द्वितीयोऽध्यायः | साङ्ख्ययोगः", engltranslit : "atha dvitIyo.adhyAyaH . sAN^khyayogaH"},
                {sanskrit : "सञ्जय उवाच |", engltranslit : "sa~njaya uvAcha ."},
                {sanskrit : "तं तथा कृपयाविष्टमश्रुपूर्णाकुलेक्षणम् |", engltranslit : "ta.n tathA kR^ipayAviShTamashrupUrNAkulekShaNam.h ."},
                {sanskrit : "विषीदन्तमिदं वाक्यमुवाच मधुसूदनः ||२- १||", engltranslit : "viShIdantamida.n vAkyamuvAcha madhusUdanaH .. 2\-1.."},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "कुतस्त्वा कश्मलमिदं विषमे समुपस्थितम् |", engltranslit : "kutastvA kashmalamida.n viShame samupasthitam.h ."},
                {sanskrit : "अनार्यजुष्टमस्वर्ग्यमकीर्तिकरमर्जुन ||२- २||", engltranslit : "anAryajuShTamasvargyamakIrtikaramarjuna .. 2\-2.."},
                {sanskrit : "क्लैब्यं मा स्म गमः पार्थ नैतत्त्वय्युपपद्यते |", engltranslit : "klaibyaM mA sma gamaH pArtha naitattvayyupapadyate ."},
                {sanskrit : "क्षुद्रं हृदयदौर्बल्यं त्यक्त्वोत्तिष्ठ परन्तप ||२- ३||", engltranslit : "kShudra.n hR^idayadaurbalya.n tyaktvottiShTha parantapa .. 2\-3.."},
                {sanskrit : "अर्जुन उवाच |", engltranslit : "arjuna uvAcha ."},
                {sanskrit : "कथं भीष्ममहं सङ्ख्ये द्रोणं च मधुसूदन |", engltranslit : "kathaM bhIShmamaha.n saN^khye droNa.n cha madhusUdana ."},
                {sanskrit : "इषुभिः प्रतियोत्स्यामि पूजार्हावरिसूदन ||२- ४||", engltranslit : "iShubhiH pratiyotsyAmi pUjArhAvarisUdana .. 2\-4.."},
                {sanskrit : "गुरूनहत्वा हि महानुभावान्", engltranslit : "gurUnahatvA hi mahAnubhAvAn.h"},
                {sanskrit : "श्रेयो भोक्तुं भैक्ष्यमपीह लोके |", engltranslit : "shreyo bhoktuM bhaikShyamapIha loke ."},
                {sanskrit : "हत्वार्थकामांस्तु गुरूनिहैव", engltranslit : "hatvArthakAmA.nstu gurUnihaiva"},
                {sanskrit : "भुञ्जीय भोगान् रुधिरप्रदिग्धान् ||२- ५||", engltranslit : "bhuJNjIya bhogAn.h rudhirapradigdhAn.h .. 2\-5.."},
                {sanskrit : "न चैतद्विद्मः कतरन्नो गरीयो", engltranslit : "na chaitadvidmaH kataranno garIyo"},
                {sanskrit : "यद्वा जयेम यदि वा नो जयेयुः |", engltranslit : "yadvA jayema yadi vA no jayeyuH ."},
                {sanskrit : "यानेव हत्वा न जिजीविषामस्- ", engltranslit : "yAneva hatvA na jijIviShAmas\-"},
                {sanskrit : "तेऽवस्थिताः प्रमुखे धार्तराष्ट्राः ||२- ६||", engltranslit : "te.avasthitAH pramukhe dhArtarAShTrAH .. 2\-6.."},
                {sanskrit : "कार्पण्यदोषोपहतस्वभावः", engltranslit : "kArpaNyadoShopahatasvabhAvaH"},
                {sanskrit : "पृच्छामि त्वां धर्मसम्मूढचेताः |", engltranslit : "pR^ichchhAmi tvAM dharmasammUDhachetAH ."},
                {sanskrit : "यच्छ्रेयः स्यान्निश्चितं ब्रूहि तन्मे", engltranslit : "yachchhreyaH syAnnishchitaM brUhi tanme"},
                {sanskrit : "शिष्यस्तेऽहं शाधि मां त्वां प्रपन्नम् ||२- ७||", engltranslit : "shiShyaste.aha.n shAdhi mA.n tvAM prapannam.h .. 2\-7.."},
                {sanskrit : "न हि प्रपश्यामि ममापनुद्याद्", engltranslit : "na hi prapashyAmi mamApanudyAd.h"},
                {sanskrit : "यच्छोकमुच्छोषणमिन्द्रियाणाम् |", engltranslit : "yachchhokamuchchhoShaNamindriyANAm.h ."},
                {sanskrit : "अवाप्य भूमावसपत्नमृद्धं", engltranslit : "avApya bhUmAvasapatnamR^iddha.n"},
                {sanskrit : "राज्यं सुराणामपि चाधिपत्यम् ||२- ८||", engltranslit : "rAjya.n surANAmapi chAdhipatyam.h .. 2\-8.."},
                {sanskrit : "सञ्जय उवाच |", engltranslit : "sa~njaya uvAcha ."},
                {sanskrit : "एवमुक्त्वा हृषीकेशं गुडाकेशः परन्तप |", engltranslit : "evamuktvA hR^iShIkesha.n guDAkeshaH parantapa ."},
                {sanskrit : "न योत्स्य इति गोविन्दमुक्त्वा तूष्णीं बभूव ह ||२- ९||", engltranslit : "na yotsya iti govindamuktvA tUShNIM babhUva ha .. 2\-9.."},
                {sanskrit : "तमुवाच हृषीकेशः प्रहसन्निव भारत |", engltranslit : "tamuvAcha hR^iShIkeshaH prahasanniva bhArata ."},
                {sanskrit : "सेनयोरुभयोर्मध्ये विषीदन्तमिदं वचः ||२- १०||", engltranslit : "senayorubhayormadhye viShIdantamida.n vachaH .. 2\-10.."},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "अशोच्यानन्वशोचस्त्वं प्रज्ञावादांश्च भाषसे |", engltranslit : "ashochyAnanvashochastvaM praGYAvAdA.nshcha bhAShase ."},
                {sanskrit : "गतासूनगतासूंश्च नानुशोचन्ति पण्डिताः ||२- ११||", engltranslit : "gatAsUnagatAsU.nshcha nAnushochanti paNDitAH .. 2\-11.."},
                {sanskrit : "न त्वेवाहं जातु नासं न त्वं नेमे जनाधिपाः |", engltranslit : "na tvevAha.n jAtu nAsa.n na tva.n neme janAdhipAH ."},
                {sanskrit : "न चैव न भविष्यामः सर्वे वयमतः परम् ||२- १२||", engltranslit : "na chaiva na bhaviShyAmaH sarve vayamataH param.h .. 2\-12.."},
                {sanskrit : "देहिनोऽस्मिन्यथा देहे कौमारं यौवनं जरा |", engltranslit : "dehino.asminyathA dehe kaumAra.n yauvana.n jarA ."},
                {sanskrit : "तथा देहान्तरप्राप्तिर्धीरस्तत्र न मुह्यति ||२- १३||", engltranslit : "tathA dehAntaraprAptirdhIrastatra na muhyati .. 2\-13.."},
                {sanskrit : "मात्रास्पर्शास्तु कौन्तेय शीतोष्णसुखदुःखदाः |", engltranslit : "mAtrAsparshAstu kaunteya shItoShNasukhaduHkhadAH ."},
                {sanskrit : "आगमापायिनोऽनित्यास्तांस्तितिक्षस्व भारत ||२- १४||", engltranslit : "AgamApAyino.anityAstA.nstitikShasva bhArata .. 2\-14.."},
                {sanskrit : "यं हि न व्यथयन्त्येते पुरुषं पुरुषर्षभ |", engltranslit : "ya.n hi na vyathayantyete puruShaM puruSharShabha ."},
                {sanskrit : "समदुःखसुखं धीरं सोऽमृतत्वाय कल्पते ||२- १५||", engltranslit : "samaduHkhasukha.n dhIra.n so.amR^itatvAya kalpate .. 2\-15.."},
                {sanskrit : "नासतो विद्यते भावो नाभावो विद्यते सतः |", engltranslit : "nAsato vidyate bhAvo nAbhAvo vidyate sataH ."},
                {sanskrit : "उभयोरपि दृष्टोऽन्तस्त्वनयोस्तत्त्वदर्शिभिः ||२- १६||", engltranslit : "ubhayorapi dR^iShTo.antastvanayostattvadarshibhiH .. 2\-16.."},
                {sanskrit : "अविनाशि तु तद्विद्धि येन सर्वमिदं ततम् |", engltranslit : "avinAshi tu tadviddhi yena sarvamida.n tatam.h ."},
                {sanskrit : "विनाशमव्ययस्यास्य न कश्चित्कर्तुमर्हति ||२- १७||", engltranslit : "vinAshamavyayasyAsya na kashchitkartumarhati .. 2\-17.."},
                {sanskrit : "अन्तवन्त इमे देहा नित्यस्योक्ताः शरीरिणः |", engltranslit : "antavanta ime dehA nityasyoktAH sharIriNaH ."},
                {sanskrit : "अनाशिनोऽप्रमेयस्य तस्माद्युध्यस्व भारत ||२- १८||", engltranslit : "anAshino.aprameyasya tasmAdyudhyasva bhArata .. 2\-18.."},
                {sanskrit : "य एनं वेत्ति हन्तारं यश्चैनं मन्यते हतम् |", engltranslit : "ya ena.n vetti hantAra.n yashchainaM manyate hatam.h ."},
                {sanskrit : "उभौ तौ न विजानीतो नायं हन्ति न हन्यते ||२- १९||", engltranslit : "ubhau tau na vijAnIto nAya.n hanti na hanyate .. 2\-19.."},
                {sanskrit : "न जायते म्रियते वा कदाचिन्", engltranslit : "na jAyate mriyate vA kadAchin.h"},
                {sanskrit : "नायं भूत्वा भविता वा न भूयः |", engltranslit : "nAyaM bhUtvA bhavitA vA na bhUyaH ."},
                {sanskrit : "अजो नित्यः शाश्वतोऽयं पुराणो", engltranslit : "ajo nityaH shAshvato.ayaM purANo"},
                {sanskrit : "न हन्यते हन्यमाने शरीरे ||२- २०||", engltranslit : "na hanyate hanyamAne sharIre .. 2\-20.."},
                {sanskrit : "वेदाविनाशिनं नित्यं य एनमजमव्ययम् |", engltranslit : "vedAvinAshina.n nitya.n ya enamajamavyayam.h ."},
                {sanskrit : "कथं स पुरुषः पार्थ कं घातयति हन्ति कम् ||२- २१||", engltranslit : "katha.n sa puruShaH pArtha ka.n ghAtayati hanti kam.h .. 2\-21.."},
                {sanskrit : "वासांसि जीर्णानि यथा विहाय", engltranslit : "vAsA.nsi jIrNAni yathA vihAya"},
                {sanskrit : "नवानि गृह्णाति नरोऽपराणि |", engltranslit : "navAni gR^ihNAti naro.aparANi ."},
                {sanskrit : "तथा शरीराणि विहाय जीर्णा- ", engltranslit : "tathA sharIrANi vihAya jIrNA\-"},
                {sanskrit : "न्यन्यानि संयाति नवानि देही ||२- २२||", engltranslit : "nyanyAni sa.nyAti navAni dehI .. 2\-22.."},
                {sanskrit : "नैनं छिन्दन्ति शस्त्राणि नैनं दहति पावकः |", engltranslit : "naina.n chhindanti shastrANi naina.n dahati pAvakaH ."},
                {sanskrit : "न चैनं क्लेदयन्त्यापो न शोषयति मारुतः ||२- २३||", engltranslit : "na chaina.n kledayantyApo na shoShayati mArutaH .. 2\-23.."},
                {sanskrit : "अच्छेद्योऽयमदाह्योऽयमक्लेद्योऽशोष्य एव च |", engltranslit : "achchhedyo.ayamadAhyo.ayamakledyo.ashoShya eva cha ."},
                {sanskrit : "नित्यः सर्वगतः स्थाणुरचलोऽयं सनातनः ||२- २४||", engltranslit : "nityaH sarvagataH sthANurachalo.aya.n sanAtanaH .. 2\-24.."},
                {sanskrit : "अव्यक्तोऽयमचिन्त्योऽयमविकार्योऽयमुच्यते |", engltranslit : "avyakto.ayamachintyo.ayamavikAryo.ayamuchyate ."},
                {sanskrit : "तस्मादेवं विदित्वैनं नानुशोचितुमर्हसि ||२- २५||", engltranslit : "tasmAdeva.n viditvaina.n nAnushochitumarhasi .. 2\-25.."},
                {sanskrit : "अथ चैनं नित्यजातं नित्यं वा मन्यसे मृतम् |", engltranslit : "atha chaina.n nityajAta.n nitya.n vA manyase mR^itam.h ."},
                {sanskrit : "तथापि त्वं महाबाहो नैवं शोचितुमर्हसि ||२- २६||", engltranslit : "tathApi tvaM mahAbAho naiva.n shochitumarhasi .. 2\-26.."},
                {sanskrit : "जातस्य हि ध्रुवो मृत्युर्ध्रुवं जन्म मृतस्य च |", engltranslit : "jAtasya hi dhruvo mR^ityurdhruva.n janma mR^itasya cha ."},
                {sanskrit : "तस्मादपरिहार्येऽर्थे न त्वं शोचितुमर्हसि ||२- २७||", engltranslit : "tasmAdaparihArye.arthe na tva.n shochitumarhasi .. 2\-27.."},
                {sanskrit : "अव्यक्तादीनि भूतानि व्यक्तमध्यानि भारत |", engltranslit : "avyaktAdIni bhUtAni vyaktamadhyAni bhArata ."},
                {sanskrit : "अव्यक्तनिधनान्येव तत्र का परिदेवना ||२- २८||", engltranslit : "avyaktanidhanAnyeva tatra kA paridevanA .. 2\-28.."},
                {sanskrit : "आश्चर्यवत्पश्यति कश्चिदेन- ", engltranslit : "Ashcharyavatpashyati kashchidena\-"},
                {sanskrit : "माश्चर्यवद्वदति तथैव चान्यः |", engltranslit : "mAshcharyavadvadati tathaiva chAnyaH ."},
                {sanskrit : "आश्चर्यवच्चैनमन्यः शृणोति", engltranslit : "AshcharyavachchainamanyaH shR^iNoti"},
                {sanskrit : "श्रुत्वाप्येनं वेद न चैव कश्चित् ||२- २९||", engltranslit : "shrutvApyena.n veda na chaiva kashchit.h .. 2\-29.."},
                {sanskrit : "देही नित्यमवध्योऽयं देहे सर्वस्य भारत |", engltranslit : "dehI nityamavadhyo.aya.n dehe sarvasya bhArata ."},
                {sanskrit : "तस्मात्सर्वाणि भूतानि न त्वं शोचितुमर्हसि ||२- ३०||", engltranslit : "tasmAtsarvANi bhUtAni na tva.n shochitumarhasi .. 2\-30.."},
                {sanskrit : "स्वधर्ममपि चावेक्ष्य न विकम्पितुमर्हसि |", engltranslit : "svadharmamapi chAvekShya na vikampitumarhasi ."},
                {sanskrit : "धर्म्याद्धि युद्धाच्छ्रेयोऽन्यत्क्षत्रियस्य न विद्यते ||२- ३१||", engltranslit : "dharmyAddhi yuddhAchchhreyo.anyatkShatriyasya na vidyate .. 2\-31.."},
                {sanskrit : "यदृच्छया चोपपन्नं स्वर्गद्वारमपावृतम् |", engltranslit : "yadR^ichchhayA chopapanna.n svargadvAramapAvR^itam.h ."},
                {sanskrit : "सुखिनः क्षत्रियाः पार्थ लभन्ते युद्धमीदृशम् ||२- ३२||", engltranslit : "sukhinaH kShatriyAH pArtha labhante yuddhamIdR^isham.h .. 2\-32.."},
                {sanskrit : "अथ चेत्त्वमिमं धर्म्यं संग्रामं न करिष्यसि |", engltranslit : "atha chettvamimaM dharmya.n sa.ngrAma.n na kariShyasi ."},
                {sanskrit : "ततः स्वधर्मं कीर्तिं च हित्वा पापमवाप्स्यसि ||२- ३३||", engltranslit : "tataH svadharma.n kIrti.n cha hitvA pApamavApsyasi .. 2\-33.."},
                {sanskrit : "अकीर्तिं चापि भूतानि कथयिष्यन्ति तेऽव्ययाम् |", engltranslit : "akIrti.n chApi bhUtAni kathayiShyanti te.avyayAm.h ."},
                {sanskrit : "सम्भावितस्य चाकीर्तिर्मरणादतिरिच्यते ||२- ३४||", engltranslit : "sambhAvitasya chAkIrtirmaraNAdatirichyate .. 2\-34.."},
                {sanskrit : "भयाद्रणादुपरतं मंस्यन्ते त्वां महारथाः |", engltranslit : "bhayAdraNAduparataM ma.nsyante tvAM mahArathAH ."},
                {sanskrit : "येषां च त्वं बहुमतो भूत्वा यास्यसि लाघवम् ||२- ३५||", engltranslit : "yeShA.n cha tvaM bahumato bhUtvA yAsyasi lAghavam.h .. 2\-35.."},
                {sanskrit : "अवाच्यवादांश्च बहून्वदिष्यन्ति तवाहिताः |", engltranslit : "avAchyavAdA.nshcha bahUnvadiShyanti tavAhitAH ."},
                {sanskrit : "निन्दन्तस्तव सामर्थ्यं ततो दुःखतरं नु किम् ||२- ३६||", engltranslit : "nindantastava sAmarthyaM tato duHkhatara.n nu kim.h .. 2\-36.."},
                {sanskrit : "हतो वा प्राप्स्यसि स्वर्गं जित्वा वा भोक्ष्यसे महीम् |", engltranslit : "hato vA prApsyasi svarga.n jitvA vA bhokShyase mahIm.h ."},
                {sanskrit : "तस्मादुत्तिष्ठ कौन्तेय युद्धाय कृतनिश्चयः ||२- ३७||", engltranslit : "tasmAduttiShTha kaunteya yuddhAya kR^itanishchayaH .. 2\-37.."},
                {sanskrit : "सुखदुःखे समे कृत्वा लाभालाभौ जयाजयौ |", engltranslit : "sukhaduHkhe same kR^itvA lAbhAlAbhau jayAjayau ."},
                {sanskrit : "ततो युद्धाय युज्यस्व नैवं पापमवाप्स्यसि ||२- ३८||", engltranslit : "tato yuddhAya yujyasva naivaM pApamavApsyasi .. 2\-38.."},
                {sanskrit : "एषा तेऽभिहिता साङ्ख्ये बुद्धिर्योगे त्विमां शृणु |", engltranslit : "eShA te.abhihitA sAN^khye buddhiryoge tvimA.n shR^iNu ."},
                {sanskrit : "बुद्ध्या युक्तो यया पार्थ कर्मबन्धं प्रहास्यसि ||२- ३९||", engltranslit : "bud.hdhyA yukto yayA pArtha karmabandhaM prahAsyasi .. 2\-39.."},
                {sanskrit : "नेहाभिक्रमनाशोऽस्ति प्रत्यवायो न विद्यते |", engltranslit : "nehAbhikramanAsho.asti pratyavAyo na vidyate ."},
                {sanskrit : "स्वल्पमप्यस्य धर्मस्य त्रायते महतो भयात् ||२- ४०||", engltranslit : "svalpamapyasya dharmasya trAyate mahato bhayAt.h .. 2\-40.."},
                {sanskrit : "व्यवसायात्मिका बुद्धिरेकेह कुरुनन्दन |", engltranslit : "vyavasAyAtmikA buddhirekeha kurunandana ."},
                {sanskrit : "बहुशाखा ह्यनन्ताश्च बुद्धयोऽव्यवसायिनाम् ||२- ४१||", engltranslit : "bahushAkhA hyanantAshcha buddhayo.avyavasAyinAm.h .. 2\-41.."},
                {sanskrit : "यामिमां पुष्पितां वाचं प्रवदन्त्यविपश्चितः |", engltranslit : "yAmimAM puShpitAM vAchaM pravadantyavipashchitaH ."},
                {sanskrit : "वेदवादरताः पार्थ नान्यदस्तीति वादिनः ||२- ४२||", engltranslit : "vedavAdaratAH pArtha nAnyadastIti vAdinaH .. 2\-42.."},
                {sanskrit : "कामात्मानः स्वर्गपरा जन्मकर्मफलप्रदाम् |", engltranslit : "kAmAtmAnaH svargaparA janmakarmaphalapradAm.h ."},
                {sanskrit : "क्रियाविशेषबहुलां भोगैश्वर्यगतिं प्रति ||२- ४३||", engltranslit : "kriyAvisheShabahulAM bhogaishvaryagatiM prati .. 2\-43.."},
                {sanskrit : "भोगैश्वर्यप्रसक्तानां तयापहृतचेतसाम् |", engltranslit : "bhogaishvaryaprasaktAnA.n tayApahR^itachetasAm.h ."},
                {sanskrit : "व्यवसायात्मिका बुद्धिः समाधौ न विधीयते ||२- ४४||", engltranslit : "vyavasAyAtmikA buddhiH samAdhau na vidhIyate .. 2\-44.."},
                {sanskrit : "त्रैगुण्यविषया वेदा निस्त्रैगुण्यो भवार्जुन |", engltranslit : "traiguNyaviShayA vedA nistraiguNyo bhavArjuna ."},
                {sanskrit : "निर्द्वन्द्वो नित्यसत्त्वस्थो निर्योगक्षेम आत्मवान् ||२- ४५||", engltranslit : "nirdvandvo nityasattvastho niryogakShema AtmavAn.h .. 2\-45.."},
                {sanskrit : "यावानर्थ उदपाने सर्वतः सम्प्लुतोदके |", engltranslit : "yAvAnartha udapAne sarvataH samplutodake ."},
                {sanskrit : "तावान्सर्वेषु वेदेषु ब्राह्मणस्य विजानतः ||२- ४६||", engltranslit : "tAvAnsarveShu vedeShu brAhmaNasya vijAnataH .. 2\-46.."},
                {sanskrit : "कर्मण्येवाधिकारस्ते मा फलेषु कदाचन |", engltranslit : "karmaNyevAdhikAraste mA phaleShu kadAchana ."},
                {sanskrit : "मा कर्मफलहेतुर्भूर्मा ते सङ्गोऽस्त्वकर्मणि ||२- ४७||", engltranslit : "mA karmaphalaheturbhUrmA te saN^go.astvakarmaNi .. 2\-47.."},
                {sanskrit : "योगस्थः कुरु कर्माणि सङ्गं त्यक्त्वा धनञ्जय |", engltranslit : "yogasthaH kuru karmANi saN^ga.n tyaktvA dhana~njaya ."},
                {sanskrit : "सिद्ध्यसिद्ध्योः समो भूत्वा समत्वं योग उच्यते ||२- ४८||", engltranslit : "sid.hdhyasid.hdhyoH samo bhUtvA samatva.n yoga uchyate .. 2\-48.."},
                {sanskrit : "दूरेण ह्यवरं कर्म बुद्धियोगाद्धनञ्जय |", engltranslit : "dUreNa hyavara.n karma buddhiyogAddhana~njaya ."},
                {sanskrit : "बुद्धौ शरणमन्विच्छ कृपणाः फलहेतवः ||२- ४९||", engltranslit : "buddhau sharaNamanvichchha kR^ipaNAH phalahetavaH .. 2\-49.."},
                {sanskrit : "बुद्धियुक्तो जहातीह उभे सुकृतदुष्कृते |", engltranslit : "buddhiyukto jahAtIha ubhe sukR^itaduShkR^ite ."},
                {sanskrit : "तस्माद्योगाय युज्यस्व योगः कर्मसु कौशलम् ||२- ५०||", engltranslit : "tasmAdyogAya yujyasva yogaH karmasu kaushalam.h .. 2\-50.."},
                {sanskrit : "कर्मजं बुद्धियुक्ता हि फलं त्यक्त्वा मनीषिणः |", engltranslit : "karmajaM buddhiyuktA hi phalaM tyaktvA manIShiNaH ."},
                {sanskrit : "जन्मबन्धविनिर्मुक्ताः पदं गच्छन्त्यनामयम् ||२- ५१||", engltranslit : "janmabandhavinirmuktAH padaM gachchhantyanAmayam.h .. 2\-51.."},
                {sanskrit : "यदा ते मोहकलिलं बुद्धिर्व्यतितरिष्यति |", engltranslit : "yadA te mohakalilaM buddhirvyatitariShyati ."},
                {sanskrit : "तदा गन्तासि निर्वेदं श्रोतव्यस्य श्रुतस्य च ||२- ५२||", engltranslit : "tadA gantAsi nirvedaM shrotavyasya shrutasya cha .. 2\-52.."},
                {sanskrit : "श्रुतिविप्रतिपन्ना ते यदा स्थास्यति निश्चला |", engltranslit : "shrutivipratipannA te yadA sthAsyati nishchalA ."},
                {sanskrit : "समाधावचला बुद्धिस्तदा योगमवाप्स्यसि ||२- ५३||", engltranslit : "samAdhAvachalA buddhistadA yogamavApsyasi .. 2\-53.."},
                {sanskrit : "अर्जुन उवाच |", engltranslit : "arjuna uvAcha ."},
                {sanskrit : "स्थितप्रज्ञस्य का भाषा समाधिस्थस्य केशव |", engltranslit : "sthitapraGYasya kA bhAShA samAdhisthasya keshava ."},
                {sanskrit : "स्थितधीः किं प्रभाषेत किमासीत व्रजेत किम् ||२- ५४||", engltranslit : "sthitadhIH kiM prabhASheta kimAsIta vrajeta kim.h .. 2\-54.."},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "प्रजहाति यदा कामान्सर्वान्पार्थ मनोगतान् |", engltranslit : "prajahAti yadA kAmAnsarvAnpArtha manogatAn.h ."},
                {sanskrit : "आत्मन्येवात्मना तुष्टः स्थितप्रज्ञस्तदोच्यते ||२- ५५||", engltranslit : "AtmanyevAtmanA tuShTaH sthitapraGYastadochyate .. 2\-55.."},
                {sanskrit : "दुःखेष्वनुद्विग्नमनाः सुखेषु विगतस्पृहः |", engltranslit : "duHkheShvanudvignamanAH sukheShu vigataspR^ihaH ."},
                {sanskrit : "वीतरागभयक्रोधः स्थितधीर्मुनिरुच्यते ||२- ५६||", engltranslit : "vItarAgabhayakrodhaH sthitadhIrmuniruchyate .. 2\-56.."},
                {sanskrit : "यः सर्वत्रानभिस्नेहस्तत्तत्प्राप्य शुभाशुभम् |", engltranslit : "yaH sarvatrAnabhisnehastattatprApya shubhAshubham.h ."},
                {sanskrit : "नाभिनन्दति न द्वेष्टि तस्य प्रज्ञा प्रतिष्ठिता ||२- ५७||", engltranslit : "nAbhinandati na dveShTi tasya praGYA pratiShThitA .. 2\-57.."},
                {sanskrit : "यदा संहरते चायं कूर्मोऽङ्गानीव सर्वशः |", engltranslit : "yadA sa.nharate chAyaM kUrmo.aN^gAnIva sarvashaH ."},
                {sanskrit : "इन्द्रियाणीन्द्रियार्थेभ्यस्तस्य प्रज्ञा प्रतिष्ठिता ||२- ५८||", engltranslit : "indriyANIndriyArthebhyastasya praGYA pratiShThitA .. 2\-58.."},
                {sanskrit : "विषया विनिवर्तन्ते निराहारस्य देहिनः |", engltranslit : "viShayA vinivartante nirAhArasya dehinaH ."},
                {sanskrit : "रसवर्जं रसोऽप्यस्य परं दृष्ट्वा निवर्तते ||२- ५९||", engltranslit : "rasavarja.n raso.apyasya para.n dR^iShTvA nivartate .. 2\-59.."},
                {sanskrit : "यततो ह्यपि कौन्तेय पुरुषस्य विपश्चितः |", engltranslit : "yatato hyapi kaunteya puruShasya vipashchitaH ."},
                {sanskrit : "इन्द्रियाणि प्रमाथीनि हरन्ति प्रसभं मनः ||२- ६०||", engltranslit : "indriyANi pramAthIni haranti prasabhaM manaH .. 2\-60.."},
                {sanskrit : "तानि सर्वाणि संयम्य युक्त आसीत मत्परः |", engltranslit : "tAni sarvANi sa.nyamya yukta AsIta matparaH ."},
                {sanskrit : "वशे हि यस्येन्द्रियाणि तस्य प्रज्ञा प्रतिष्ठिता ||२- ६१||", engltranslit : "vashe hi yasyendriyANi tasya praGYA pratiShThitA .. 2\-61.."},
                {sanskrit : "ध्यायतो विषयान्पुंसः सङ्गस्तेषूपजायते |", engltranslit : "dhyAyato viShayAnpu.nsaH saN^gasteShUpajAyate ."},
                {sanskrit : "सङ्गात्सञ्जायते कामः कामात्क्रोधोऽभिजायते ||२- ६२||", engltranslit : "saN^gAtsa~njAyate kAmaH kAmAtkrodho.abhijAyate .. 2\-62.."},
                {sanskrit : "क्रोधाद्भवति सम्मोहः सम्मोहात्स्मृतिविभ्रमः |", engltranslit : "krodhAdbhavati sammohaH sammohAtsmR^itivibhramaH ."},
                {sanskrit : "स्मृतिभ्रंशाद् बुद्धिनाशो बुद्धिनाशात्प्रणश्यति ||२- ६३||", engltranslit : "smR^itibhra.nshAd.h buddhinAsho buddhinAshAtpraNashyati .. 2\-63.."},
                {sanskrit : "रागद्वेषविमुक्तैस्तु विषयानिन्द्रियैश्चरन् | orवियुक्तैस्तु", engltranslit : "rAgadveShavimuktaistu viShayAnindriyaishcharan.h . ##or##viyuktaistu"},
                {sanskrit : "आत्मवश्यैर्विधेयात्मा प्रसादमधिगच्छति ||२- ६४||", engltranslit : "AtmavashyairvidheyAtmA prasAdamadhigachchhati .. 2\-64.."},
                {sanskrit : "प्रसादे सर्वदुःखानां हानिरस्योपजायते |", engltranslit : "prasAde sarvaduHkhAnA.n hAnirasyopajAyate ."},
                {sanskrit : "प्रसन्नचेतसो ह्याशु बुद्धिः पर्यवतिष्ठते ||२- ६५||", engltranslit : "prasannachetaso hyAshu buddhiH paryavatiShThate .. 2\-65.."},
                {sanskrit : "नास्ति बुद्धिरयुक्तस्य न चायुक्तस्य भावना |", engltranslit : "nAsti buddhirayuktasya na chAyuktasya bhAvanA ."},
                {sanskrit : "न चाभावयतः शान्तिरशान्तस्य कुतः सुखम् ||२- ६६||", engltranslit : "na chAbhAvayataH shAntirashAntasya kutaH sukham.h .. 2\-66.."},
                {sanskrit : "इन्द्रियाणां हि चरतां यन्मनोऽनुविधीयते |", engltranslit : "indriyANAM hi charatAM yanmano.anuvidhIyate ."},
                {sanskrit : "तदस्य हरति प्रज्ञां वायुर्नावमिवाम्भसि ||२- ६७||", engltranslit : "tadasya harati praGYAM vAyurnAvamivAmbhasi .. 2\-67.."},
                {sanskrit : "तस्माद्यस्य महाबाहो निगृहीतानि सर्वशः |", engltranslit : "tasmAdyasya mahAbAho nigR^ihItAni sarvashaH ."},
                {sanskrit : "इन्द्रियाणीन्द्रियार्थेभ्यस्तस्य प्रज्ञा प्रतिष्ठिता ||२- ६८||", engltranslit : "indriyANIndriyArthebhyastasya praGYA pratiShThitA .. 2\-68.."},
                {sanskrit : "या निशा सर्वभूतानां तस्यां जागर्ति संयमी |", engltranslit : "yA nishA sarvabhUtAnAM tasyA.n jAgarti sa.nyamI ."},
                {sanskrit : "यस्यां जाग्रति भूतानि सा निशा पश्यतो मुनेः ||२- ६९||", engltranslit : "yasyA.n jAgrati bhUtAni sA nishA pashyato muneH .. 2\-69.."},
                {sanskrit : "आपूर्यमाणमचलप्रतिष्ठं", engltranslit : "ApUryamANamachalapratiShTha.n"},
                {sanskrit : "समुद्रमापः प्रविशन्ति यद्वत् |", engltranslit : "samudramApaH pravishanti yadvat.h ."},
                {sanskrit : "तद्वत्कामा यं प्रविशन्ति सर्वे", engltranslit : "tadvatkAmA yaM pravishanti sarve"},
                {sanskrit : "स शान्तिमाप्नोति न कामकामी ||२- ७०||", engltranslit : "sa shAntimApnoti na kAmakAmI .. 2\-70.."},
                {sanskrit : "विहाय कामान्यः सर्वान्पुमांश्चरति निःस्पृहः |", engltranslit : "vihAya kAmAnyaH sarvAnpumA.nshcharati niHspR^ihaH ."},
                {sanskrit : "निर्ममो निरहङ्कारः स शान्तिमधिगच्छति ||२- ७१||", engltranslit : "nirmamo nirahaN^kAraH sa shAntimadhigachchhati .. 2\-71.."},
                {sanskrit : "एषा ब्राह्मी स्थितिः पार्थ नैनां प्राप्य विमुह्यति |", engltranslit : "eShA brAhmI sthitiH pArtha nainAM prApya vimuhyati ."},
                {sanskrit : "स्थित्वास्यामन्तकालेऽपि ब्रह्मनिर्वाणमृच्छति ||२- ७२||", engltranslit : "sthitvAsyAmantakAle.api brahmanirvANamR^ichchhati .. 2\-72.."},
                {sanskrit : "ॐ तत्सदिति श्रीमद्भगवद्गीतासूपनिषत्सु", engltranslit : "AUM tatsaditi shrImadbhagavadgItAsUpaniShatsu"},
                {sanskrit : "ब्रह्मविद्यायां योगशास्त्रे श्रीकृष्णार्जुनसंवादे", engltranslit : "brahmavidyAyA.n yogashAstre shrIkR^iShNArjunasa.nvAde"},
                {sanskrit : "साङ्ख्ययोगो नाम द्वितीयोऽध्यायः ||२||", engltranslit : "sAN^khyayogo nAma dvitIyo.adhyAyaH .. 2.."},
                {sanskrit : "अथ तृतीयोऽध्यायः | कर्मयोगः", engltranslit : "atha tR^itIyo.adhyAyaH . karmayogaH"},
                {sanskrit : "अर्जुन उवाच |", engltranslit : "arjuna uvAcha ."},
                {sanskrit : "ज्यायसी चेत्कर्मणस्ते मता बुद्धिर्जनार्दन |", engltranslit : "jyAyasI chetkarmaNaste matA buddhirjanArdana ."},
                {sanskrit : "तत्किं कर्मणि घोरे मां नियोजयसि केशव ||३- १||", engltranslit : "tatki.n karmaNi ghore mA.n niyojayasi keshava .. 3\-1.."},
                {sanskrit : "व्यामिश्रेणेव वाक्येन बुद्धिं मोहयसीव मे |", engltranslit : "vyAmishreNeva vAkyena buddhiM mohayasIva me ."},
                {sanskrit : "तदेकं वद निश्चित्य येन श्रेयोऽहमाप्नुयाम् ||३- २||", engltranslit : "tadekaM vada nishchitya yena shreyo.ahamApnuyAm.h .. 3\-2.."},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "लोकेऽस्मिन् द्विविधा निष्ठा पुरा प्रोक्ता मयानघ |", engltranslit : "loke.asmin dvividhA niShThA purA proktA mayAnagha ."},
                {sanskrit : "ज्ञानयोगेन साङ्ख्यानां कर्मयोगेन योगिनाम् ||३- ३||", engltranslit : "GYAnayogena sAN^khyAnA.n karmayogena yoginAm.h .. 3\-3.."},
                {sanskrit : "न कर्मणामनारम्भान्नैष्कर्म्यं पुरुषोऽश्नुते |", engltranslit : "na karmaNAmanArambhAnnaiShkarmyaM puruSho.ashnute ."},
                {sanskrit : "न च संन्यसनादेव सिद्धिं समधिगच्छति ||३- ४||", engltranslit : "na cha sa.nnyasanAdeva siddhi.n samadhigachchhati .. 3\-4.."},
                {sanskrit : "न हि कश्चित्क्षणमपि जातु तिष्ठत्यकर्मकृत् |", engltranslit : "na hi kashchitkShaNamapi jAtu tiShThatyakarmakR^it.h ."},
                {sanskrit : "कार्यते ह्यवशः कर्म सर्वः प्रकृतिजैर्गुणैः ||३- ५||", engltranslit : "kAryate hyavashaH karma sarvaH prakR^itijairguNaiH .. 3\-5.."},
                {sanskrit : "कर्मेन्द्रियाणि संयम्य य आस्ते मनसा स्मरन् |", engltranslit : "karmendriyANi sa.nyamya ya Aste manasA smaran.h ."},
                {sanskrit : "इन्द्रियार्थान्विमूढात्मा मिथ्याचारः स उच्यते ||३- ६||", engltranslit : "indriyArthAnvimUDhAtmA mithyAchAraH sa uchyate .. 3\-6.."},
                {sanskrit : "यस्त्विन्द्रियाणि मनसा नियम्यारभतेऽर्जुन |", engltranslit : "yastvindriyANi manasA niyamyArabhate.arjuna ."},
                {sanskrit : "कर्मेन्द्रियैः कर्मयोगमसक्तः स विशिष्यते ||३- ७||", engltranslit : "karmendriyaiH karmayogamasaktaH sa vishiShyate .. 3\-7.."},
                {sanskrit : "नियतं कुरु कर्म त्वं कर्म ज्यायो ह्यकर्मणः |", engltranslit : "niyata.n kuru karma tvaM karma jyAyo hyakarmaNaH ."},
                {sanskrit : "शरीरयात्रापि च ते न प्रसिद्ध्येदकर्मणः ||३- ८||", engltranslit : "sharIrayAtrApi cha te na prasid.hdhyedakarmaNaH .. 3\-8.."},
                {sanskrit : "यज्ञार्थात्कर्मणोऽन्यत्र लोकोऽयं कर्मबन्धनः |", engltranslit : "yaGYArthAtkarmaNo.anyatra loko.aya.n karmabandhanaH ."},
                {sanskrit : "तदर्थं कर्म कौन्तेय मुक्तसङ्गः समाचर ||३- ९||", engltranslit : "tadartha.n karma kaunteya muktasaN^gaH samAchara .. 3\-9.."},
                {sanskrit : "सहयज्ञाः प्रजाः सृष्ट्वा पुरोवाच प्रजापतिः |", engltranslit : "sahayaGYAH prajAH sR^iShTvA purovAcha prajApatiH ."},
                {sanskrit : "अनेन प्रसविष्यध्वमेष वोऽस्त्विष्टकामधुक् ||३- १०||", engltranslit : "anena prasaviShyadhvameSha vo.astviShTakAmadhuk.h .. 3\-10.."},
                {sanskrit : "देवान्भावयतानेन ते देवा भावयन्तु वः |", engltranslit : "devAnbhAvayatAnena te devA bhAvayantu vaH ."},
                {sanskrit : "परस्परं भावयन्तः श्रेयः परमवाप्स्यथ ||३- ११||", engltranslit : "parasparaM bhAvayantaH shreyaH paramavApsyatha .. 3\-11.."},
                {sanskrit : "इष्टान्भोगान्हि वो देवा दास्यन्ते यज्ञभाविताः |", engltranslit : "iShTAnbhogAnhi vo devA dAsyante yaGYabhAvitAH ."},
                {sanskrit : "तैर्दत्तानप्रदायैभ्यो यो भुङ्क्ते स्तेन एव सः ||३- १२||", engltranslit : "tairdattAnapradAyaibhyo yo bhuN^kte stena eva saH .. 3\-12.."},
                {sanskrit : "यज्ञशिष्टाशिनः सन्तो मुच्यन्ते सर्वकिल्बिषैः |", engltranslit : "yaGYashiShTAshinaH santo muchyante sarvakilbiShaiH ."},
                {sanskrit : "भुञ्जते ते त्वघं पापा ये पचन्त्यात्मकारणात् ||३- १३||", engltranslit : "bhuJNjate te tvaghaM pApA ye pachantyAtmakAraNAt.h .. 3\-13.."},
                {sanskrit : "अन्नाद्भवन्ति भूतानि पर्जन्यादन्नसम्भवः |", engltranslit : "annAdbhavanti bhUtAni parjanyAdannasambhavaH ."},
                {sanskrit : "यज्ञाद्भवति पर्जन्यो यज्ञः कर्मसमुद्भवः ||३- १४||", engltranslit : "yaGYAdbhavati parjanyo yaGYaH karmasamudbhavaH .. 3\-14.."},
                {sanskrit : "कर्म ब्रह्मोद्भवं विद्धि ब्रह्माक्षरसमुद्भवम् |", engltranslit : "karma brahmodbhava.n viddhi brahmAkSharasamudbhavam.h ."},
                {sanskrit : "तस्मात्सर्वगतं ब्रह्म नित्यं यज्ञे प्रतिष्ठितम् ||३- १५||", engltranslit : "tasmAtsarvagataM brahma nityaM yaGYe pratiShThitam.h .. 3\-15.."},
                {sanskrit : "एवं प्रवर्तितं चक्रं नानुवर्तयतीह यः |", engltranslit : "evaM pravartita.n chakraM nAnuvartayatIha yaH ."},
                {sanskrit : "अघायुरिन्द्रियारामो मोघं पार्थ स जीवति ||३- १६||", engltranslit : "aghAyurindriyArAmo moghaM pArtha sa jIvati .. 3\-16.."},
                {sanskrit : "यस्त्वात्मरतिरेव स्यादात्मतृप्तश्च मानवः |", engltranslit : "yastvAtmaratireva syAdAtmatR^iptashcha mAnavaH ."},
                {sanskrit : "आत्मन्येव च सन्तुष्टस्तस्य कार्यं न विद्यते ||३- १७||", engltranslit : "Atmanyeva cha santuShTastasya kAryaM na vidyate .. 3\-17.."},
                {sanskrit : "नैव तस्य कृतेनार्थो नाकृतेनेह कश्चन |", engltranslit : "naiva tasya kR^itenArtho nAkR^iteneha kashchana ."},
                {sanskrit : "न चास्य सर्वभूतेषु कश्चिदर्थव्यपाश्रयः ||३- १८||", engltranslit : "na chAsya sarvabhUteShu kashchidarthavyapAshrayaH .. 3\-18.."},
                {sanskrit : "तस्मादसक्तः सततं कार्यं कर्म समाचर |", engltranslit : "tasmAdasaktaH satataM kAryaM karma samAchara ."},
                {sanskrit : "असक्तो ह्याचरन्कर्म परमाप्नोति पूरुषः ||३- १९||", engltranslit : "asakto hyAcharankarma paramApnoti pUruShaH .. 3\-19.."},
                {sanskrit : "कर्मणैव हि संसिद्धिमास्थिता जनकादयः |", engltranslit : "karmaNaiva hi sa.nsiddhimAsthitA janakAdayaH ."},
                {sanskrit : "लोकसंग्रहमेवापि सम्पश्यन्कर्तुमर्हसि ||३- २०||", engltranslit : "lokasa.ngrahamevApi sampashyankartumarhasi .. 3\-20.."},
                {sanskrit : "यद्यदाचरति श्रेष्ठस्तत्तदेवेतरो जनः |", engltranslit : "yadyadAcharati shreShThastattadevetaro janaH ."},
                {sanskrit : "स यत्प्रमाणं कुरुते लोकस्तदनुवर्तते ||३- २१||", engltranslit : "sa yatpramANaM kurute lokastadanuvartate .. 3\-21.."},
                {sanskrit : "न मे पार्थास्ति कर्तव्यं त्रिषु लोकेषु किञ्चन |", engltranslit : "na me pArthAsti kartavyaM triShu lokeShu ki~nchana ."},
                {sanskrit : "नानवाप्तमवाप्तव्यं वर्त एव च कर्मणि ||३- २२||", engltranslit : "nAnavAptamavAptavyaM varta eva cha karmaNi .. 3\-22.."},
                {sanskrit : "यदि ह्यहं न वर्तेयं जातु कर्मण्यतन्द्रितः |", engltranslit : "yadi hyahaM na varteya.n jAtu karmaNyatandritaH ."},
                {sanskrit : "मम वर्त्मानुवर्तन्ते मनुष्याः पार्थ सर्वशः ||३- २३||", engltranslit : "mama vartmAnuvartante manuShyAH pArtha sarvashaH .. 3\-23.."},
                {sanskrit : "उत्सीदेयुरिमे लोका न कुर्यां कर्म चेदहम् |", engltranslit : "utsIdeyurime lokA na kuryA.n karma chedaham.h ."},
                {sanskrit : "सङ्करस्य च कर्ता स्यामुपहन्यामिमाः प्रजाः ||३- २४||", engltranslit : "saN^karasya cha kartA syAmupahanyAmimAH prajAH .. 3\-24.."},
                {sanskrit : "सक्ताः कर्मण्यविद्वांसो यथा कुर्वन्ति भारत |", engltranslit : "saktAH karmaNyavidvA.nso yathA kurvanti bhArata ."},
                {sanskrit : "कुर्याद्विद्वांस्तथासक्तश्चिकीर्षुर्लोकसंग्रहम् ||३- २५||", engltranslit : "kuryAdvidvA.nstathAsaktashchikIrShurlokasa.ngraham.h .. 3\-25.."},
                {sanskrit : "न बुद्धिभेदं जनयेदज्ञानां कर्मसङ्गिनाम् |", engltranslit : "na buddhibheda.n janayedaGYAnA.n karmasaN^ginAm.h ."},
                {sanskrit : "जोषयेत्सर्वकर्माणि विद्वान्युक्तः समाचरन् ||३- २६||", engltranslit : "joShayetsarvakarmANi vidvAnyuktaH samAcharan.h .. 3\-26.."},
                {sanskrit : "प्रकृतेः क्रियमाणानि गुणैः कर्माणि सर्वशः |", engltranslit : "prakR^iteH kriyamANAni guNaiH karmANi sarvashaH ."},
                {sanskrit : "अहङ्कारविमूढात्मा कर्ताहमिति मन्यते ||३- २७||", engltranslit : "ahaN^kAravimUDhAtmA kartAhamiti manyate .. 3\-27.."},
                {sanskrit : "तत्त्ववित्तु महाबाहो गुणकर्मविभागयोः |", engltranslit : "tattvavittu mahAbAho guNakarmavibhAgayoH ."},
                {sanskrit : "गुणा गुणेषु वर्तन्त इति मत्वा न सज्जते ||३- २८||", engltranslit : "guNA guNeShu vartanta iti matvA na sajjate .. 3\-28.."},
                {sanskrit : "प्रकृतेर्गुणसम्मूढाः सज्जन्ते गुणकर्मसु |", engltranslit : "prakR^iterguNasammUDhAH sajjante guNakarmasu ."},
                {sanskrit : "तानकृत्स्नविदो मन्दान्कृत्स्नविन्न विचालयेत् ||३- २९||", engltranslit : "tAnakR^itsnavido mandAnkR^itsnavinna vichAlayet.h .. 3\-29.."},
                {sanskrit : "मयि सर्वाणि कर्माणि संन्यस्याध्यात्मचेतसा |", engltranslit : "mayi sarvANi karmANi sa.nnyasyAdhyAtmachetasA ."},
                {sanskrit : "निराशीर्निर्ममो भूत्वा युध्यस्व विगतज्वरः ||३- ३०||", engltranslit : "nirAshIrnirmamo bhUtvA yudhyasva vigatajvaraH .. 3\-30.."},
                {sanskrit : "ये मे मतमिदं नित्यमनुतिष्ठन्ति मानवाः |", engltranslit : "ye me matamidaM nityamanutiShThanti mAnavAH ."},
                {sanskrit : "श्रद्धावन्तोऽनसूयन्तो मुच्यन्ते तेऽपि कर्मभिः ||३- ३१||", engltranslit : "shraddhAvanto.anasUyanto muchyante te.api karmabhiH .. 3\-31.."},
                {sanskrit : "ये त्वेतदभ्यसूयन्तो नानुतिष्ठन्ति मे मतम् |", engltranslit : "ye tvetadabhyasUyanto nAnutiShThanti me matam.h ."},
                {sanskrit : "सर्वज्ञानविमूढांस्तान्विद्धि नष्टानचेतसः ||३- ३२||", engltranslit : "sarvaGYAnavimUDhA.nstAnviddhi naShTAnachetasaH .. 3\-32.."},
                {sanskrit : "सदृशं चेष्टते स्वस्याः प्रकृतेर्ज्ञानवानपि |", engltranslit : "sadR^isha.n cheShTate svasyAH prakR^iterGYAnavAnapi ."},
                {sanskrit : "प्रकृतिं यान्ति भूतानि निग्रहः किं करिष्यति ||३- ३३||", engltranslit : "prakR^iti.n yAnti bhUtAni nigrahaH kiM kariShyati .. 3\-33.."},
                {sanskrit : "इन्द्रियस्येन्द्रियस्यार्थे रागद्वेषौ व्यवस्थितौ |", engltranslit : "indriyasyendriyasyArthe rAgadveShau vyavasthitau ."},
                {sanskrit : "तयोर्न वशमागच्छेत्तौ ह्यस्य परिपन्थिनौ ||३- ३४||", engltranslit : "tayorna vashamAgachchhettau hyasya paripanthinau .. 3\-34.."},
                {sanskrit : "श्रेयान्स्वधर्मो विगुणः परधर्मात्स्वनुष्ठितात् |", engltranslit : "shreyAnsvadharmo viguNaH paradharmAtsvanuShThitAt.h ."},
                {sanskrit : "स्वधर्मे निधनं श्रेयः परधर्मो भयावहः ||३- ३५||", engltranslit : "svadharme nidhana.n shreyaH paradharmo bhayAvahaH .. 3\-35.."},
                {sanskrit : "अर्जुन उवाच |", engltranslit : "arjuna uvAcha ."},
                {sanskrit : "अथ केन प्रयुक्तोऽयं पापं चरति पूरुषः |", engltranslit : "atha kena prayukto.ayaM pApa.n charati pUruShaH ."},
                {sanskrit : "अनिच्छन्नपि वार्ष्णेय बलादिव नियोजितः ||३- ३६||", engltranslit : "anichchhannapi vArShNeya balAdiva niyojitaH .. 3\-36.."},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "काम एष क्रोध एष रजोगुणसमुद्भवः |", engltranslit : "kAma eSha krodha eSha rajoguNasamudbhavaH ."},
                {sanskrit : "महाशनो महापाप्मा विद्ध्येनमिह वैरिणम् ||३- ३७||", engltranslit : "mahAshano mahApApmA vid.hdhyenamiha vairiNam.h .. 3\-37.."},
                {sanskrit : "धूमेनाव्रियते वह्निर्यथादर्शो मलेन च |", engltranslit : "dhUmenAvriyate vahniryathAdarsho malena cha ."},
                {sanskrit : "यथोल्बेनावृतो गर्भस्तथा तेनेदमावृतम् ||३- ३८||", engltranslit : "yatholbenAvR^ito garbhastathA tenedamAvR^itam.h .. 3\-38.."},
                {sanskrit : "आवृतं ज्ञानमेतेन ज्ञानिनो नित्यवैरिणा |", engltranslit : "AvR^itaM GYAnametena GYAnino nityavairiNA ."},
                {sanskrit : "कामरूपेण कौन्तेय दुष्पूरेणानलेन च ||३- ३९||", engltranslit : "kAmarUpeNa kaunteya duShpUreNAnalena cha .. 3\-39.."},
                {sanskrit : "इन्द्रियाणि मनो बुद्धिरस्याधिष्ठानमुच्यते |", engltranslit : "indriyANi mano buddhirasyAdhiShThAnamuchyate ."},
                {sanskrit : "एतैर्विमोहयत्येष ज्ञानमावृत्य देहिनम् ||३- ४०||", engltranslit : "etairvimohayatyeSha GYAnamAvR^itya dehinam.h .. 3\-40.."},
                {sanskrit : "तस्मात्त्वमिन्द्रियाण्यादौ नियम्य भरतर्षभ |", engltranslit : "tasmAttvamindriyANyAdau niyamya bharatarShabha ."},
                {sanskrit : "पाप्मानं प्रजहि ह्येनं ज्ञानविज्ञाननाशनम् ||३- ४१||", engltranslit : "pApmAnaM prajahi hyenaM GYAnaviGYAnanAshanam.h .. 3\-41.."},
                {sanskrit : "इन्द्रियाणि पराण्याहुरिन्द्रियेभ्यः परं मनः |", engltranslit : "indriyANi parANyAhurindriyebhyaH paraM manaH ."},
                {sanskrit : "मनसस्तु परा बुद्धिर्यो बुद्धेः परतस्तु सः ||३- ४२||", engltranslit : "manasastu parA buddhiryo buddheH paratastu saH .. 3\-42.."},
                {sanskrit : "एवं बुद्धेः परं बुद्ध्वा संस्तभ्यात्मानमात्मना |", engltranslit : "evaM buddheH paraM buddhvA sa.nstabhyAtmAnamAtmanA ."},
                {sanskrit : "जहि शत्रुं महाबाहो कामरूपं दुरासदम् ||३- ४३||", engltranslit : "jahi shatruM mahAbAho kAmarUpaM durAsadam.h .. 3\-43.."},
                {sanskrit : "ॐ तत्सदिति श्रीमद्भगवद्गीतासूपनिषत्सु", engltranslit : "AUM tatsaditi shrImadbhagavadgItAsUpaniShatsu"},
                {sanskrit : "ब्रह्मविद्यायां योगशास्त्रे श्रीकृष्णार्जुनसंवादे", engltranslit : "brahmavidyAyA.n yogashAstre shrIkR^iShNArjunasa.nvAde"},
                {sanskrit : "कर्मयोगो नाम तृतीयोऽध्यायः ||३||", engltranslit : "karmayogo nAma tR^itIyo.adhyAyaH .. 3.."},
                {sanskrit : "अथ चतुर्थोऽध्यायः | ज्ञानकर्मसंन्यासयोगः", engltranslit : "atha chaturtho.adhyAyaH . GYAnakarmasa.nnyAsayogaH"},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "इमं विवस्वते योगं प्रोक्तवानहमव्ययम् |", engltranslit : "imaM vivasvate yogaM proktavAnahamavyayam.h ."},
                {sanskrit : "विवस्वान्मनवे प्राह मनुरिक्ष्वाकवेऽब्रवीत् ||४- १||", engltranslit : "vivasvAnmanave prAha manurikShvAkave.abravIt.h .. 4\-1.."},
                {sanskrit : "एवं परम्पराप्राप्तमिमं राजर्षयो विदुः |", engltranslit : "evaM paramparAprAptamimaM rAjarShayo viduH ."},
                {sanskrit : "स कालेनेह महता योगो नष्टः परन्तप ||४- २||", engltranslit : "sa kAleneha mahatA yogo naShTaH parantapa .. 4\-2.."},
                {sanskrit : "स एवायं मया तेऽद्य योगः प्रोक्तः पुरातनः |", engltranslit : "sa evAyaM mayA te.adya yogaH proktaH purAtanaH ."},
                {sanskrit : "भक्तोऽसि मे सखा चेति रहस्यं ह्येतदुत्तमम् ||४- ३||", engltranslit : "bhakto.asi me sakhA cheti rahasyaM hyetaduttamam.h .. 4\-3.."},
                {sanskrit : "अर्जुन उवाच |", engltranslit : "arjuna uvAcha ."},
                {sanskrit : "अपरं भवतो जन्म परं जन्म विवस्वतः |", engltranslit : "aparaM bhavato janma para.n janma vivasvataH ."},
                {sanskrit : "कथमेतद्विजानीयां त्वमादौ प्रोक्तवानिति ||४- ४||", engltranslit : "kathametadvijAnIyA.n tvamAdau proktavAniti .. 4\-4.."},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "बहूनि मे व्यतीतानि जन्मानि तव चार्जुन |", engltranslit : "bahUni me vyatItAni janmAni tava chArjuna ."},
                {sanskrit : "तान्यहं वेद सर्वाणि न त्वं वेत्थ परन्तप ||४- ५||", engltranslit : "tAnyahaM veda sarvANi na tvaM vettha parantapa .. 4\-5.."},
                {sanskrit : "अजोऽपि सन्नव्ययात्मा भूतानामीश्वरोऽपि सन् |", engltranslit : "ajo.api sannavyayAtmA bhUtAnAmIshvaro.api san.h ."},
                {sanskrit : "प्रकृतिं स्वामधिष्ठाय सम्भवाम्यात्ममायया ||४- ६||", engltranslit : "prakR^iti.n svAmadhiShThAya sambhavAmyAtmamAyayA .. 4\-6.."},
                {sanskrit : "यदा यदा हि धर्मस्य ग्लानिर्भवति भारत |", engltranslit : "yadA yadA hi dharmasya glAnirbhavati bhArata ."},
                {sanskrit : "अभ्युत्थानमधर्मस्य तदात्मानं सृजाम्यहम् ||४- ७||", engltranslit : "abhyutthAnamadharmasya tadAtmAnaM sR^ijAmyaham.h .. 4\-7.."},
                {sanskrit : "परित्राणाय साधूनां विनाशाय च दुष्कृताम् |", engltranslit : "paritrANAya sAdhUnA.n vinAshAya cha duShkR^itAm.h ."},
                {sanskrit : "धर्मसंस्थापनार्थाय सम्भवामि युगे युगे ||४- ८||", engltranslit : "dharmasa.nsthApanArthAya sambhavAmi yuge yuge .. 4\-8.."},
                {sanskrit : "जन्म कर्म च मे दिव्यमेवं यो वेत्ति तत्त्वतः |", engltranslit : "janma karma cha me divyamevaM yo vetti tattvataH ."},
                {sanskrit : "त्यक्त्वा देहं पुनर्जन्म नैति मामेति सोऽर्जुन ||४- ९||", engltranslit : "tyaktvA dehaM punarjanma naiti mAmeti so.arjuna .. 4\-9.."},
                {sanskrit : "वीतरागभयक्रोधा मन्मया मामुपाश्रिताः |", engltranslit : "vItarAgabhayakrodhA manmayA mAmupAshritAH ."},
                {sanskrit : "बहवो ज्ञानतपसा पूता मद्भावमागताः ||४- १०||", engltranslit : "bahavo GYAnatapasA pUtA madbhAvamAgatAH .. 4\-10.."},
                {sanskrit : "ये यथा मां प्रपद्यन्ते तांस्तथैव भजाम्यहम् |", engltranslit : "ye yathA mAM prapadyante tA.nstathaiva bhajAmyaham.h ."},
                {sanskrit : "मम वर्त्मानुवर्तन्ते मनुष्याः पार्थ सर्वशः ||४- ११||", engltranslit : "mama vartmAnuvartante manuShyAH pArtha sarvashaH .. 4\-11.."},
                {sanskrit : "काङ्क्षन्तः कर्मणां सिद्धिं यजन्त इह देवताः |", engltranslit : "kAN^kShantaH karmaNA.n siddhi.n yajanta iha devatAH ."},
                {sanskrit : "क्षिप्रं हि मानुषे लोके सिद्धिर्भवति कर्मजा ||४- १२||", engltranslit : "kShipra.n hi mAnuShe loke siddhirbhavati karmajA .. 4\-12.."},
                {sanskrit : "चातुर्वर्ण्यं मया सृष्टं गुणकर्मविभागशः |", engltranslit : "chAturvarNyaM mayA sR^iShTa.n guNakarmavibhAgashaH ."},
                {sanskrit : "तस्य कर्तारमपि मां विद्ध्यकर्तारमव्ययम् ||४- १३||", engltranslit : "tasya kartAramapi mA.n vid.hdhyakartAramavyayam.h .. 4\-13.."},
                {sanskrit : "न मां कर्माणि लिम्पन्ति न मे कर्मफले स्पृहा |", engltranslit : "na mA.n karmANi limpanti na me karmaphale spR^ihA ."},
                {sanskrit : "इति मां योऽभिजानाति कर्मभिर्न स बध्यते ||४- १४||", engltranslit : "iti mA.n yo.abhijAnAti karmabhirna sa badhyate .. 4\-14.."},
                {sanskrit : "एवं ज्ञात्वा कृतं कर्म पूर्वैरपि मुमुक्षुभिः |", engltranslit : "eva.n GYAtvA kR^itaM karma pUrvairapi mumukShubhiH ."},
                {sanskrit : "कुरु कर्मैव तस्मात्त्वं पूर्वैः पूर्वतरं कृतम् ||४- १५||", engltranslit : "kuru karmaiva tasmAttvaM pUrvaiH pUrvatara.n kR^itam.h .. 4\-15.."},
                {sanskrit : "किं कर्म किमकर्मेति कवयोऽप्यत्र मोहिताः |", engltranslit : "ki.n karma kimakarmeti kavayo.apyatra mohitAH ."},
                {sanskrit : "तत्ते कर्म प्रवक्ष्यामि यज्ज्ञात्वा मोक्ष्यसेऽशुभात् ||४- १६||", engltranslit : "tatte karma pravakShyAmi yajGYAtvA mokShyase.ashubhAt.h .. 4\-16.."},
                {sanskrit : "कर्मणो ह्यपि बोद्धव्यं बोद्धव्यं च विकर्मणः |", engltranslit : "karmaNo hyapi boddhavyaM boddhavya.n cha vikarmaNaH ."},
                {sanskrit : "अकर्मणश्च बोद्धव्यं गहना कर्मणो गतिः ||४- १७||", engltranslit : "akarmaNashcha boddhavya.n gahanA karmaNo gatiH .. 4\-17.."},
                {sanskrit : "कर्मण्यकर्म यः पश्येदकर्मणि च कर्म यः |", engltranslit : "karmaNyakarma yaH pashyedakarmaNi cha karma yaH ."},
                {sanskrit : "स बुद्धिमान्मनुष्येषु स युक्तः कृत्स्नकर्मकृत् ||४- १८||", engltranslit : "sa buddhimAnmanuShyeShu sa yuktaH kR^itsnakarmakR^it.h .. 4\-18.."},
                {sanskrit : "यस्य सर्वे समारम्भाः कामसङ्कल्पवर्जिताः |", engltranslit : "yasya sarve samArambhAH kAmasaN^kalpavarjitAH ."},
                {sanskrit : "ज्ञानाग्निदग्धकर्माणं तमाहुः पण्डितं बुधाः ||४- १९||", engltranslit : "GYAnAgnidagdhakarmANa.n tamAhuH paNDitaM budhAH .. 4\-19.."},
                {sanskrit : "त्यक्त्वा कर्मफलासङ्गं नित्यतृप्तो निराश्रयः |", engltranslit : "tyaktvA karmaphalAsaN^ga.n nityatR^ipto nirAshrayaH ."},
                {sanskrit : "कर्मण्यभिप्रवृत्तोऽपि नैव किञ्चित्करोति सः ||४- २०||", engltranslit : "karmaNyabhipravR^itto.api naiva ki~nchitkaroti saH .. 4\-20.."},
                {sanskrit : "निराशीर्यतचित्तात्मा त्यक्तसर्वपरिग्रहः |", engltranslit : "nirAshIryatachittAtmA tyaktasarvaparigrahaH ."},
                {sanskrit : "शारीरं केवलं कर्म कुर्वन्नाप्नोति किल्बिषम् ||४- २१||", engltranslit : "shArIraM kevalaM karma kurvannApnoti kilbiSham.h .. 4\-21.."},
                {sanskrit : "यदृच्छालाभसन्तुष्टो द्वन्द्वातीतो विमत्सरः |", engltranslit : "yadR^ichchhAlAbhasantuShTo dvandvAtIto vimatsaraH ."},
                {sanskrit : "समः सिद्धावसिद्धौ च कृत्वापि न निबध्यते ||४- २२||", engltranslit : "samaH siddhAvasiddhau cha kR^itvApi na nibadhyate .. 4\-22.."},
                {sanskrit : "गतसङ्गस्य मुक्तस्य ज्ञानावस्थितचेतसः |", engltranslit : "gatasaN^gasya muktasya GYAnAvasthitachetasaH ."},
                {sanskrit : "यज्ञायाचरतः कर्म समग्रं प्रविलीयते ||४- २३||", engltranslit : "yaGYAyAcharataH karma samagraM pravilIyate .. 4\-23.."},
                {sanskrit : "ब्रह्मार्पणं ब्रह्म हविर्ब्रह्माग्नौ ब्रह्मणा हुतम् |", engltranslit : "brahmArpaNaM brahma havirbrahmAgnau brahmaNA hutam.h ."},
                {sanskrit : "ब्रह्मैव तेन गन्तव्यं ब्रह्मकर्मसमाधिना ||४- २४||", engltranslit : "brahmaiva tena gantavyaM brahmakarmasamAdhinA .. 4\-24.."},
                {sanskrit : "दैवमेवापरे यज्ञं योगिनः पर्युपासते |", engltranslit : "daivamevApare yaGYa.n yoginaH paryupAsate ."},
                {sanskrit : "ब्रह्माग्नावपरे यज्ञं यज्ञेनैवोपजुह्वति ||४- २५||", engltranslit : "brahmAgnAvapare yaGYaM yaGYenaivopajuhvati .. 4\-25.."},
                {sanskrit : "श्रोत्रादीनीन्द्रियाण्यन्ये संयमाग्निषु जुह्वति |", engltranslit : "shrotrAdInIndriyANyanye sa.nyamAgniShu juhvati ."},
                {sanskrit : "शब्दादीन्विषयानन्य इन्द्रियाग्निषु जुह्वति ||४- २६||", engltranslit : "shabdAdInviShayAnanya indriyAgniShu juhvati .. 4\-26.."},
                {sanskrit : "सर्वाणीन्द्रियकर्माणि प्राणकर्माणि चापरे |", engltranslit : "sarvANIndriyakarmANi prANakarmANi chApare ."},
                {sanskrit : "आत्मसंयमयोगाग्नौ जुह्वति ज्ञानदीपिते ||४- २७||", engltranslit : "Atmasa.nyamayogAgnau juhvati GYAnadIpite .. 4\-27.."},
                {sanskrit : "द्रव्ययज्ञास्तपोयज्ञा योगयज्ञास्तथापरे |", engltranslit : "dravyayaGYAstapoyaGYA yogayaGYAstathApare ."},
                {sanskrit : "स्वाध्यायज्ञानयज्ञाश्च यतयः संशितव्रताः ||४- २८||", engltranslit : "svAdhyAyaGYAnayaGYAshcha yatayaH sa.nshitavratAH .. 4\-28.."},
                {sanskrit : "अपाने जुह्वति प्राणं प्राणेऽपानं तथापरे |", engltranslit : "apAne juhvati prANaM prANe.apAnaM tathApare ."},
                {sanskrit : "प्राणापानगती रुद्ध्वा प्राणायामपरायणाः ||४- २९||", engltranslit : "prANApAnagatI rud.hdhvA prANAyAmaparAyaNAH .. 4\-29.."},
                {sanskrit : "अपरे नियताहाराः प्राणान्प्राणेषु जुह्वति |", engltranslit : "apare niyatAhArAH prANAnprANeShu juhvati ."},
                {sanskrit : "सर्वेऽप्येते यज्ञविदो यज्ञक्षपितकल्मषाः ||४- ३०||", engltranslit : "sarve.apyete yaGYavido yaGYakShapitakalmaShAH .. 4\-30.."},
                {sanskrit : "यज्ञशिष्टामृतभुजो यान्ति ब्रह्म सनातनम् |", engltranslit : "yaGYashiShTAmR^itabhujo yAnti brahma sanAtanam.h ."},
                {sanskrit : "नायं लोकोऽस्त्ययज्ञस्य कुतोऽन्यः कुरुसत्तम ||४- ३१||", engltranslit : "nAyaM loko.astyayaGYasya kuto.anyaH kurusattama .. 4\-31.."},
                {sanskrit : "एवं बहुविधा यज्ञा वितता ब्रह्मणो मुखे |", engltranslit : "evaM bahuvidhA yaGYA vitatA brahmaNo mukhe ."},
                {sanskrit : "कर्मजान्विद्धि तान्सर्वानेवं ज्ञात्वा विमोक्ष्यसे ||४- ३२||", engltranslit : "karmajAnviddhi tAnsarvAnevaM GYAtvA vimokShyase .. 4\-32.."},
                {sanskrit : "श्रेयान्द्रव्यमयाद्यज्ञाज्ज्ञानयज्ञः परन्तप |", engltranslit : "shreyAndravyamayAdyaGYAjGYAnayaGYaH parantapa ."},
                {sanskrit : "सर्वं कर्माखिलं पार्थ ज्ञाने परिसमाप्यते ||४- ३३||", engltranslit : "sarva.n karmAkhilaM pArtha GYAne parisamApyate .. 4\-33.."},
                {sanskrit : "तद्विद्धि प्रणिपातेन परिप्रश्नेन सेवया |", engltranslit : "tadviddhi praNipAtena pariprashnena sevayA ."},
                {sanskrit : "उपदेक्ष्यन्ति ते ज्ञानं ज्ञानिनस्तत्त्वदर्शिनः ||४- ३४||", engltranslit : "upadekShyanti te GYAnaM GYAninastattvadarshinaH .. 4\-34.."},
                {sanskrit : "यज्ज्ञात्वा न पुनर्मोहमेवं यास्यसि पाण्डव |", engltranslit : "yajGYAtvA na punarmohamevaM yAsyasi pANDava ."},
                {sanskrit : "येन भूतान्यशेषेण द्रक्ष्यस्यात्मन्यथो मयि ||४- ३५||var अशेषाणि", engltranslit : "yena bhUtAnyasheSheNa drakShyasyAtmanyatho mayi .. 4\-35.. ## var ## asheShANi"},
                {sanskrit : "अपि चेदसि पापेभ्यः सर्वेभ्यः पापकृत्तमः |", engltranslit : "api chedasi pApebhyaH sarvebhyaH pApakR^ittamaH ."},
                {sanskrit : "सर्वं ज्ञानप्लवेनैव वृजिनं सन्तरिष्यसि ||४- ३६||", engltranslit : "sarva.n GYAnaplavenaiva vR^ijinaM santariShyasi .. 4\-36.."},
                {sanskrit : "यथैधांसि समिद्धोऽग्निर्भस्मसात्कुरुतेऽर्जुन |", engltranslit : "yathaidhA.nsi samiddho.agnirbhasmasAtkurute.arjuna ."},
                {sanskrit : "ज्ञानाग्निः सर्वकर्माणि भस्मसात्कुरुते तथा ||४- ३७||", engltranslit : "GYAnAgniH sarvakarmANi bhasmasAtkurute tathA .. 4\-37.."},
                {sanskrit : "न हि ज्ञानेन सदृशं पवित्रमिह विद्यते |", engltranslit : "na hi GYAnena sadR^ishaM pavitramiha vidyate ."},
                {sanskrit : "तत्स्वयं योगसंसिद्धः कालेनात्मनि विन्दति ||४- ३८||", engltranslit : "tatsvayaM yogasa.nsiddhaH kAlenAtmani vindati .. 4\-38.."},
                {sanskrit : "श्रद्धावाँल्लभते ज्ञानं तत्परः संयतेन्द्रियः |", engltranslit : "shraddhAvA.Nllabhate GYAnaM tatparaH sa.nyatendriyaH ."},
                {sanskrit : "ज्ञानं लब्ध्वा परां शान्तिमचिरेणाधिगच्छति ||४- ३९||", engltranslit : "GYAnaM labdhvA parAM shAntimachireNAdhigachchhati .. 4\-39.."},
                {sanskrit : "अज्ञश्चाश्रद्दधानश्च संशयात्मा विनश्यति |", engltranslit : "aGYashchAshraddadhAnashcha sa.nshayAtmA vinashyati ."},
                {sanskrit : "नायं लोकोऽस्ति न परो न सुखं संशयात्मनः ||४- ४०||", engltranslit : "nAyaM loko.asti na paro na sukhaM sa.nshayAtmanaH .. 4\-40.."},
                {sanskrit : "योगसंन्यस्तकर्माणं ज्ञानसञ्छिन्नसंशयम् |", engltranslit : "yogasa.nnyastakarmANaM GYAnasa~nchhinnasa.nshayam.h ."},
                {sanskrit : "आत्मवन्तं न कर्माणि निबध्नन्ति धनञ्जय ||४- ४१||", engltranslit : "AtmavantaM na karmANi nibadhnanti dhana~njaya .. 4\-41.."},
                {sanskrit : "तस्मादज्ञानसम्भूतं हृत्स्थं ज्ञानासिनात्मनः |", engltranslit : "tasmAdaGYAnasambhUtaM hR^itstha.n GYAnAsinAtmanaH ."},
                {sanskrit : "छित्त्वैनं संशयं योगमातिष्ठोत्तिष्ठ भारत ||४- ४२||", engltranslit : "chhittvainaM sa.nshayaM yogamAtiShThottiShTha bhArata .. 4\-42.."},
                {sanskrit : "ॐ तत्सदिति श्रीमद्भगवद्गीतासूपनिषत्सु", engltranslit : "AUM tatsaditi shrImadbhagavadgItAsUpaniShatsu"},
                {sanskrit : "ब्रह्मविद्यायां योगशास्त्रे श्रीकृष्णार्जुनसंवादे", engltranslit : "brahmavidyAyA.n yogashAstre shrIkR^iShNArjunasa.nvAde"},
                {sanskrit : "ज्ञानकर्मसंन्यासयोगो नाम चतुर्थोऽध्यायः ||४||", engltranslit : "GYAnakarmasa.nnyAsayogo nAma chaturtho.adhyAyaH .. 4.."},
                {sanskrit : "अथ पञ्चमोऽध्यायः | संन्यासयोगः", engltranslit : "atha pa~nchamo.adhyAyaH . sa.nnyAsayogaH"},
                {sanskrit : "अर्जुन उवाच |", engltranslit : "arjuna uvAcha ."},
                {sanskrit : "संन्यासं कर्मणां कृष्ण पुनर्योगं च शंससि |", engltranslit : "sa.nnyAsaM karmaNA.n kR^iShNa punaryoga.n cha sha.nsasi ."},
                {sanskrit : "यच्छ्रेय एतयोरेकं तन्मे ब्रूहि सुनिश्चितम् ||५- १||", engltranslit : "yachchhreya etayorekaM tanme brUhi sunishchitam.h .. 5\-1.."},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "संन्यासः कर्मयोगश्च निःश्रेयसकरावुभौ |", engltranslit : "sa.nnyAsaH karmayogashcha niHshreyasakarAvubhau ."},
                {sanskrit : "तयोस्तु कर्मसंन्यासात्कर्मयोगो विशिष्यते ||५- २||", engltranslit : "tayostu karmasa.nnyAsAtkarmayogo vishiShyate .. 5\-2.."},
                {sanskrit : "ज्ञेयः स नित्यसंन्यासी यो न द्वेष्टि न काङ्क्षति |", engltranslit : "GYeyaH sa nityasa.nnyAsI yo na dveShTi na kAN^kShati ."},
                {sanskrit : "निर्द्वन्द्वो हि महाबाहो सुखं बन्धात्प्रमुच्यते ||५- ३||", engltranslit : "nirdvandvo hi mahAbAho sukhaM bandhAtpramuchyate .. 5\-3.."},
                {sanskrit : "साङ्ख्ययोगौ पृथग्बालाः प्रवदन्ति न पण्डिताः |", engltranslit : "sAN^khyayogau pR^ithagbAlAH pravadanti na paNDitAH ."},
                {sanskrit : "एकमप्यास्थितः सम्यगुभयोर्विन्दते फलम् ||५- ४||", engltranslit : "ekamapyAsthitaH samyagubhayorvindate phalam.h .. 5\-4.."},
                {sanskrit : "यत्साङ्ख्यैः प्राप्यते स्थानं तद्योगैरपि गम्यते |", engltranslit : "yatsAN^khyaiH prApyate sthAnaM tadyogairapi gamyate ."},
                {sanskrit : "एकं साङ्ख्यं च योगं च यः पश्यति स पश्यति ||५- ५||", engltranslit : "ekaM sAN^khya.n cha yoga.n cha yaH pashyati sa pashyati .. 5\-5.."},
                {sanskrit : "संन्यासस्तु महाबाहो दुःखमाप्तुमयोगतः |", engltranslit : "sa.nnyAsastu mahAbAho duHkhamAptumayogataH ."},
                {sanskrit : "योगयुक्तो मुनिर्ब्रह्म नचिरेणाधिगच्छति ||५- ६||", engltranslit : "yogayukto munirbrahma nachireNAdhigachchhati .. 5\-6.."},
                {sanskrit : "योगयुक्तो विशुद्धात्मा विजितात्मा जितेन्द्रियः |", engltranslit : "yogayukto vishuddhAtmA vijitAtmA jitendriyaH ."},
                {sanskrit : "सर्वभूतात्मभूतात्मा कुर्वन्नपि न लिप्यते ||५- ७||", engltranslit : "sarvabhUtAtmabhUtAtmA kurvannapi na lipyate .. 5\-7.."},
                {sanskrit : "नैव किञ्चित्करोमीति युक्तो मन्येत तत्त्ववित् |", engltranslit : "naiva ki~nchitkaromIti yukto manyeta tattvavit.h ."},
                {sanskrit : "पश्यञ्शृण्वन्स्पृशञ्जिघ्रन्नश्नन्गच्छन्स्वपञ्श्वसन् ||५- ८||", engltranslit : "pashyaJNshR^iNvanspR^ishaJNjighrannashnangachchhansvapa~nshvasan.h .. 5\-8.."},
                {sanskrit : "प्रलपन्विसृजन्गृह्णन्नुन्मिषन्निमिषन्नपि |", engltranslit : "pralapanvisR^ijangR^ihNannunmiShannimiShannapi ."},
                {sanskrit : "इन्द्रियाणीन्द्रियार्थेषु वर्तन्त इति धारयन् ||५- ९||", engltranslit : "indriyANIndriyArtheShu vartanta iti dhArayan.h .. 5\-9.."},
                {sanskrit : "ब्रह्मण्याधाय कर्माणि सङ्गं त्यक्त्वा करोति यः |", engltranslit : "brahmaNyAdhAya karmANi saN^ga.n tyaktvA karoti yaH ."},
                {sanskrit : "लिप्यते न स पापेन पद्मपत्रमिवाम्भसा ||५- १०||", engltranslit : "lipyate na sa pApena padmapatramivAmbhasA .. 5\-10.."},
                {sanskrit : "कायेन मनसा बुद्ध्या केवलैरिन्द्रियैरपि |", engltranslit : "kAyena manasA bud.hdhyA kevalairindriyairapi ."},
                {sanskrit : "योगिनः कर्म कुर्वन्ति सङ्गं त्यक्त्वात्मशुद्धये ||५- ११||", engltranslit : "yoginaH karma kurvanti saN^ga.n tyaktvAtmashuddhaye .. 5\-11.."},
                {sanskrit : "युक्तः कर्मफलं त्यक्त्वा शान्तिमाप्नोति नैष्ठिकीम् |", engltranslit : "yuktaH karmaphalaM tyaktvA shAntimApnoti naiShThikIm.h ."},
                {sanskrit : "अयुक्तः कामकारेण फले सक्तो निबध्यते ||५- १२||", engltranslit : "ayuktaH kAmakAreNa phale sakto nibadhyate .. 5\-12.."},
                {sanskrit : "सर्वकर्माणि मनसा संन्यस्यास्ते सुखं वशी |", engltranslit : "sarvakarmANi manasA sa.nnyasyAste sukhaM vashI ."},
                {sanskrit : "नवद्वारे पुरे देही नैव कुर्वन्न कारयन् ||५- १३||", engltranslit : "navadvAre pure dehI naiva kurvanna kArayan.h .. 5\-13.."},
                {sanskrit : "न कर्तृत्वं न कर्माणि लोकस्य सृजति प्रभुः |", engltranslit : "na kartR^itva.n na karmANi lokasya sR^ijati prabhuH ."},
                {sanskrit : "न कर्मफलसंयोगं स्वभावस्तु प्रवर्तते ||५- १४||", engltranslit : "na karmaphalasa.nyogaM svabhAvastu pravartate .. 5\-14.."},
                {sanskrit : "नादत्ते कस्यचित्पापं न चैव सुकृतं विभुः |", engltranslit : "nAdatte kasyachitpApaM na chaiva sukR^itaM vibhuH ."},
                {sanskrit : "अज्ञानेनावृतं ज्ञानं तेन मुह्यन्ति जन्तवः ||५- १५||", engltranslit : "aGYAnenAvR^itaM GYAnaM tena muhyanti jantavaH .. 5\-15.."},
                {sanskrit : "ज्ञानेन तु तदज्ञानं येषां नाशितमात्मनः |", engltranslit : "GYAnena tu tadaGYAnaM yeShA.n nAshitamAtmanaH ."},
                {sanskrit : "तेषामादित्यवज्ज्ञानं प्रकाशयति तत्परम् ||५- १६||", engltranslit : "teShAmAdityavajGYAnaM prakAshayati tatparam.h .. 5\-16.."},
                {sanskrit : "तद्बुद्धयस्तदात्मानस्तन्निष्ठास्तत्परायणाः |", engltranslit : "tad.hbuddhayastadAtmAnastanniShThAstatparAyaNAH ."},
                {sanskrit : "गच्छन्त्यपुनरावृत्तिं ज्ञाननिर्धूतकल्मषाः ||५- १७||", engltranslit : "gachchhantyapunarAvR^itti.n GYAnanirdhUtakalmaShAH .. 5\-17.."},
                {sanskrit : "विद्याविनयसम्पन्ने ब्राह्मणे गवि हस्तिनि |", engltranslit : "vidyAvinayasampanne brAhmaNe gavi hastini ."},
                {sanskrit : "शुनि चैव श्वपाके च पण्डिताः समदर्शिनः ||५- १८||", engltranslit : "shuni chaiva shvapAke cha paNDitAH samadarshinaH .. 5\-18.."},
                {sanskrit : "इहैव तैर्जितः सर्गो येषां साम्ये स्थितं मनः |", engltranslit : "ihaiva tairjitaH sargo yeShA.n sAmye sthitaM manaH ."},
                {sanskrit : "निर्दोषं हि समं ब्रह्म तस्माद् ब्रह्मणि ते स्थिताः ||५- १९||", engltranslit : "nirdoShaM hi samaM brahma tasmAd.h brahmaNi te sthitAH .. 5\-19.."},
                {sanskrit : "न प्रहृष्येत्प्रियं प्राप्य नोद्विजेत्प्राप्य चाप्रियम् |", engltranslit : "na prahR^iShyetpriyaM prApya nodvijetprApya chApriyam.h ."},
                {sanskrit : "स्थिरबुद्धिरसम्मूढो ब्रह्मविद् ब्रह्मणि स्थितः ||५- २०||", engltranslit : "sthirabuddhirasammUDho brahmavid.h brahmaNi sthitaH .. 5\-20.."},
                {sanskrit : "बाह्यस्पर्शेष्वसक्तात्मा विन्दत्यात्मनि यत्सुखम् |", engltranslit : "bAhyasparsheShvasaktAtmA vindatyAtmani yatsukham.h ."},
                {sanskrit : "स ब्रह्मयोगयुक्तात्मा सुखमक्षयमश्नुते ||५- २१||", engltranslit : "sa brahmayogayuktAtmA sukhamakShayamashnute .. 5\-21.."},
                {sanskrit : "ये हि संस्पर्शजा भोगा दुःखयोनय एव ते |", engltranslit : "ye hi sa.nsparshajA bhogA duHkhayonaya eva te ."},
                {sanskrit : "आद्यन्तवन्तः कौन्तेय न तेषु रमते बुधः ||५- २२||", engltranslit : "AdyantavantaH kaunteya na teShu ramate budhaH .. 5\-22.."},
                {sanskrit : "शक्नोतीहैव यः सोढुं प्राक्शरीरविमोक्षणात् |", engltranslit : "shaknotIhaiva yaH soDhuM prAk{},sharIravimokShaNAt.h ."},
                {sanskrit : "कामक्रोधोद्भवं वेगं स युक्तः स सुखी नरः ||५- २३||", engltranslit : "kAmakrodhodbhavaM vegaM sa yuktaH sa sukhI naraH .. 5\-23.."},
                {sanskrit : "योऽन्तःसुखोऽन्तरारामस्तथान्तर्ज्योतिरेव यः |", engltranslit : "yo.antaHsukho.antarArAmastathAntarjyotireva yaH ."},
                {sanskrit : "स योगी ब्रह्मनिर्वाणं ब्रह्मभूतोऽधिगच्छति ||५- २४||", engltranslit : "sa yogI brahmanirvANaM brahmabhUto.adhigachchhati .. 5\-24.."},
                {sanskrit : "लभन्ते ब्रह्मनिर्वाणमृषयः क्षीणकल्मषाः |", engltranslit : "labhante brahmanirvANamR^iShayaH kShINakalmaShAH ."},
                {sanskrit : "छिन्नद्वैधा यतात्मानः सर्वभूतहिते रताः ||५- २५||", engltranslit : "chhinnadvaidhA yatAtmAnaH sarvabhUtahite ratAH .. 5\-25.."},
                {sanskrit : "कामक्रोधवियुक्तानां यतीनां यतचेतसाम् |", engltranslit : "kAmakrodhaviyuktAnA.n yatInA.n yatachetasAm.h ."},
                {sanskrit : "अभितो ब्रह्मनिर्वाणं वर्तते विदितात्मनाम् ||५- २६||", engltranslit : "abhito brahmanirvANaM vartate viditAtmanAm.h .. 5\-26.."},
                {sanskrit : "स्पर्शान्कृत्वा बहिर्बाह्यांश्चक्षुश्चैवान्तरे भ्रुवोः |", engltranslit : "sparshAnkR^itvA bahirbAhyA.nshchakShushchaivAntare bhruvoH ."},
                {sanskrit : "प्राणापानौ समौ कृत्वा नासाभ्यन्तरचारिणौ ||५- २७||", engltranslit : "prANApAnau samau kR^itvA nAsAbhyantarachAriNau .. 5\-27.."},
                {sanskrit : "यतेन्द्रियमनोबुद्धिर्मुनिर्मोक्षपरायणः |", engltranslit : "yatendriyamanobuddhirmunirmokShaparAyaNaH ."},
                {sanskrit : "विगतेच्छाभयक्रोधो यः सदा मुक्त एव सः ||५- २८||", engltranslit : "vigatechchhAbhayakrodho yaH sadA mukta eva saH .. 5\-28.."},
                {sanskrit : "भोक्तारं यज्ञतपसां सर्वलोकमहेश्वरम् |", engltranslit : "bhoktAraM yaGYatapasA.n sarvalokamaheshvaram.h ."},
                {sanskrit : "सुहृदं सर्वभूतानां ज्ञात्वा मां शान्तिमृच्छति ||५- २९||", engltranslit : "suhR^idaM sarvabhUtAnA.n GYAtvA mA.n shAntimR^ichchhati .. 5\-29.."},
                {sanskrit : "ॐ तत्सदिति श्रीमद्भगवद्गीतासूपनिषत्सु", engltranslit : "AUM tatsaditi shrImadbhagavadgItAsUpaniShatsu"},
                {sanskrit : "ब्रह्मविद्यायां योगशास्त्रे श्रीकृष्णार्जुनसंवादे", engltranslit : "brahmavidyAyA.n yogashAstre shrIkR^iShNArjunasa.nvAde"},
                {sanskrit : "संन्यासयोगो नाम पञ्चमोऽध्यायः ||५||", engltranslit : "sa.nnyAsayogo nAma pa~nchamo.adhyAyaH .. 5.."},
                {sanskrit : "अथ षष्ठोऽध्यायः | आत्मसंयमयोगः", engltranslit : "atha ShaShTho.adhyAyaH . Atmasa.nyamayogaH"},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "अनाश्रितः कर्मफलं कार्यं कर्म करोति यः |", engltranslit : "anAshritaH karmaphalaM kArya.n karma karoti yaH ."},
                {sanskrit : "स संन्यासी च योगी च न निरग्निर्न चाक्रियः ||६- १||", engltranslit : "sa sa.nnyAsI cha yogI cha na niragnirna chAkriyaH .. 6\-1.."},
                {sanskrit : "यं संन्यासमिति प्राहुर्योगं तं विद्धि पाण्डव |", engltranslit : "yaM sa.nnyAsamiti prAhuryogaM taM viddhi pANDava ."},
                {sanskrit : "न ह्यसंन्यस्तसङ्कल्पो योगी भवति कश्चन ||६- २||", engltranslit : "na hyasa.nnyastasaN^kalpo yogI bhavati kashchana .. 6\-2.."},
                {sanskrit : "आरुरुक्षोर्मुनेर्योगं कर्म कारणमुच्यते |", engltranslit : "ArurukShormuneryogaM karma kAraNamuchyate ."},
                {sanskrit : "योगारूढस्य तस्यैव शमः कारणमुच्यते ||६- ३||", engltranslit : "yogArUDhasya tasyaiva shamaH kAraNamuchyate .. 6\-3.."},
                {sanskrit : "यदा हि नेन्द्रियार्थेषु न कर्मस्वनुषज्जते |", engltranslit : "yadA hi nendriyArtheShu na karmasvanuShajjate ."},
                {sanskrit : "सर्वसङ्कल्पसंन्यासी योगारूढस्तदोच्यते ||६- ४||", engltranslit : "sarvasaN^kalpasa.nnyAsI yogArUDhastadochyate .. 6\-4.."},
                {sanskrit : "उद्धरेदात्मनात्मानं नात्मानमवसादयेत् |", engltranslit : "uddharedAtmanAtmAnaM nAtmAnamavasAdayet.h ."},
                {sanskrit : "आत्मैव ह्यात्मनो बन्धुरात्मैव रिपुरात्मनः ||६- ५||", engltranslit : "Atmaiva hyAtmano bandhurAtmaiva ripurAtmanaH .. 6\-5.."},
                {sanskrit : "बन्धुरात्मात्मनस्तस्य येनात्मैवात्मना जितः |", engltranslit : "bandhurAtmAtmanastasya yenAtmaivAtmanA jitaH ."},
                {sanskrit : "अनात्मनस्तु शत्रुत्वे वर्तेतात्मैव शत्रुवत् ||६- ६||", engltranslit : "anAtmanastu shatrutve vartetAtmaiva shatruvat.h .. 6\-6.."},
                {sanskrit : "जितात्मनः प्रशान्तस्य परमात्मा समाहितः |", engltranslit : "jitAtmanaH prashAntasya paramAtmA samAhitaH ."},
                {sanskrit : "शीतोष्णसुखदुःखेषु तथा मानापमानयोः ||६- ७||", engltranslit : "shItoShNasukhaduHkheShu tathA mAnApamAnayoH .. 6\-7.."},
                {sanskrit : "ज्ञानविज्ञानतृप्तात्मा कूटस्थो विजितेन्द्रियः |", engltranslit : "GYAnaviGYAnatR^iptAtmA kUTastho vijitendriyaH ."},
                {sanskrit : "युक्त इत्युच्यते योगी समलोष्टाश्मकाञ्चनः ||६- ८||", engltranslit : "yukta ityuchyate yogI samaloShTAshmakA~nchanaH .. 6\-8.."},
                {sanskrit : "सुहृन्मित्रार्युदासीनमध्यस्थद्वेष्यबन्धुषु |", engltranslit : "suhR^inmitrAryudAsInamadhyasthadveShyabandhuShu ."},
                {sanskrit : "साधुष्वपि च पापेषु समबुद्धिर्विशिष्यते ||६- ९||", engltranslit : "sAdhuShvapi cha pApeShu samabuddhirvishiShyate .. 6\-9.."},
                {sanskrit : "योगी युञ्जीत सततमात्मानं रहसि स्थितः |", engltranslit : "yogI yuJNjIta satatamAtmAnaM rahasi sthitaH ."},
                {sanskrit : "एकाकी यतचित्तात्मा निराशीरपरिग्रहः ||६- १०||", engltranslit : "ekAkI yatachittAtmA nirAshIraparigrahaH .. 6\-10.."},
                {sanskrit : "शुचौ देशे प्रतिष्ठाप्य स्थिरमासनमात्मनः |", engltranslit : "shuchau deshe pratiShThApya sthiramAsanamAtmanaH ."},
                {sanskrit : "नात्युच्छ्रितं नातिनीचं चैलाजिनकुशोत्तरम् ||६- ११||", engltranslit : "nAtyuchchhritaM nAtinIcha.n chailAjinakushottaram.h .. 6\-11.."},
                {sanskrit : "तत्रैकाग्रं मनः कृत्वा यतचित्तेन्द्रियक्रियः |", engltranslit : "tatraikAgraM manaH kR^itvA yatachittendriyakriyaH ."},
                {sanskrit : "उपविश्यासने युञ्ज्याद्योगमात्मविशुद्धये ||६- १२||", engltranslit : "upavishyAsane yuJNjyAdyogamAtmavishuddhaye .. 6\-12.."},
                {sanskrit : "समं कायशिरोग्रीवं धारयन्नचलं स्थिरः |", engltranslit : "samaM kAyashirogrIvaM dhArayannachalaM sthiraH ."},
                {sanskrit : "सम्प्रेक्ष्य नासिकाग्रं स्वं दिशश्चानवलोकयन् ||६- १३||", engltranslit : "samprekShya nAsikAgraM svaM dishashchAnavalokayan.h .. 6\-13.."},
                {sanskrit : "प्रशान्तात्मा विगतभीर्ब्रह्मचारिव्रते स्थितः |", engltranslit : "prashAntAtmA vigatabhIrbrahmachArivrate sthitaH ."},
                {sanskrit : "मनः संयम्य मच्चित्तो युक्त आसीत मत्परः ||६- १४||", engltranslit : "manaH sa.nyamya machchitto yukta AsIta matparaH .. 6\-14.."},
                {sanskrit : "युञ्जन्नेवं सदात्मानं योगी नियतमानसः |", engltranslit : "yuJNjannevaM sadAtmAnaM yogI niyatamAnasaH ."},
                {sanskrit : "शान्तिं निर्वाणपरमां मत्संस्थामधिगच्छति ||६- १५||", engltranslit : "shAnti.n nirvANaparamAM matsa.nsthAmadhigachchhati .. 6\-15.."},
                {sanskrit : "नात्यश्नतस्तु योगोऽस्ति न चैकान्तमनश्नतः |", engltranslit : "nAtyashnatastu yogo.asti na chaikAntamanashnataH ."},
                {sanskrit : "न चातिस्वप्नशीलस्य जाग्रतो नैव चार्जुन ||६- १६||", engltranslit : "na chAtisvapnashIlasya jAgrato naiva chArjuna .. 6\-16.."},
                {sanskrit : "युक्ताहारविहारस्ययुक्तचेष्टस्य कर्मसु |", engltranslit : "yuktAhAravihArasyayuktacheShTasya karmasu ."},
                {sanskrit : "युक्तस्वप्नावबोधस्य योगो भवति दुःखहा ||६- १७||", engltranslit : "yuktasvapnAvabodhasya yogo bhavati duHkhahA .. 6\-17.."},
                {sanskrit : "यदा विनियतं चित्तमात्मन्येवावतिष्ठते |", engltranslit : "yadA viniyata.n chittamAtmanyevAvatiShThate ."},
                {sanskrit : "निःस्पृहः सर्वकामेभ्यो युक्त इत्युच्यते तदा ||६- १८||", engltranslit : "niHspR^ihaH sarvakAmebhyo yukta ityuchyate tadA .. 6\-18.."},
                {sanskrit : "यथा दीपो निवातस्थो नेङ्गते सोपमा स्मृता |", engltranslit : "yathA dIpo nivAtastho neN^gate sopamA smR^itA ."},
                {sanskrit : "योगिनो यतचित्तस्य युञ्जतो योगमात्मनः ||६- १९||", engltranslit : "yogino yatachittasya yuJNjato yogamAtmanaH .. 6\-19.."},
                {sanskrit : "यत्रोपरमते चित्तं निरुद्धं योगसेवया |", engltranslit : "yatroparamate chittaM niruddhaM yogasevayA ."},
                {sanskrit : "यत्र चैवात्मनात्मानं पश्यन्नात्मनि तुष्यति ||६- २०||", engltranslit : "yatra chaivAtmanAtmAnaM pashyannAtmani tuShyati .. 6\-20.."},
                {sanskrit : "सुखमात्यन्तिकं यत्तद् बुद्धिग्राह्यमतीन्द्रियम् |", engltranslit : "sukhamAtyantikaM yattad.h buddhigrAhyamatIndriyam.h ."},
                {sanskrit : "वेत्ति यत्र न चैवायं स्थितश्चलति तत्त्वतः ||६- २१||", engltranslit : "vetti yatra na chaivAyaM sthitashchalati tattvataH .. 6\-21.."},
                {sanskrit : "यं लब्ध्वा चापरं लाभं मन्यते नाधिकं ततः |", engltranslit : "yaM labdhvA chAparaM lAbhaM manyate nAdhikaM tataH ."},
                {sanskrit : "यस्मिन्स्थितो न दुःखेन गुरुणापि विचाल्यते ||६- २२||", engltranslit : "yasminsthito na duHkhena guruNApi vichAlyate .. 6\-22.."},
                {sanskrit : "तं विद्याद् दुःखसंयोगवियोगं योगसंज्ञितम् |", engltranslit : "taM vidyAd.h duHkhasa.nyogaviyogaM yogasa.nGYitam.h ."},
                {sanskrit : "स निश्चयेन योक्तव्यो योगोऽनिर्विण्णचेतसा ||६- २३||", engltranslit : "sa nishchayena yoktavyo yogo.anirviNNachetasA .. 6\-23.."},
                {sanskrit : "सङ्कल्पप्रभवान्कामांस्त्यक्त्वा सर्वानशेषतः |", engltranslit : "saN^kalpaprabhavAnkAmA.nstyaktvA sarvAnasheShataH ."},
                {sanskrit : "मनसैवेन्द्रियग्रामं विनियम्य समन्ततः ||६- २४||", engltranslit : "manasaivendriyagrAmaM viniyamya samantataH .. 6\-24.."},
                {sanskrit : "शनैः शनैरुपरमेद् बुद्ध्या धृतिगृहीतया |", engltranslit : "shanaiH shanairuparamed.h bud.hdhyA dhR^itigR^ihItayA ."},
                {sanskrit : "आत्मसंस्थं मनः कृत्वा न किञ्चिदपि चिन्तयेत् ||६- २५||", engltranslit : "Atmasa.nsthaM manaH kR^itvA na ki~nchidapi chintayet.h .. 6\-25.."},
                {sanskrit : "यतो यतो निश्चरति मनश्चञ्चलमस्थिरम् |", engltranslit : "yato yato nishcharati manashcha~nchalamasthiram.h ."},
                {sanskrit : "ततस्ततो नियम्यैतदात्मन्येव वशं नयेत् ||६- २६||", engltranslit : "tatastato niyamyaitadAtmanyeva vashaM nayet.h .. 6\-26.."},
                {sanskrit : "प्रशान्तमनसं ह्येनं योगिनं सुखमुत्तमम् |", engltranslit : "prashAntamanasaM hyenaM yoginaM sukhamuttamam.h ."},
                {sanskrit : "उपैति शान्तरजसं ब्रह्मभूतमकल्मषम् ||६- २७||", engltranslit : "upaiti shAntarajasaM brahmabhUtamakalmaSham.h .. 6\-27.."},
                {sanskrit : "युञ्जन्नेवं सदात्मानं योगी विगतकल्मषः |", engltranslit : "yuJNjannevaM sadAtmAnaM yogI vigatakalmaShaH ."},
                {sanskrit : "सुखेन ब्रह्मसंस्पर्शमत्यन्तं सुखमश्नुते ||६- २८||", engltranslit : "sukhena brahmasa.nsparshamatyantaM sukhamashnute .. 6\-28.."},
                {sanskrit : "सर्वभूतस्थमात्मानं सर्वभूतानि चात्मनि |", engltranslit : "sarvabhUtasthamAtmAnaM sarvabhUtAni chAtmani ."},
                {sanskrit : "ईक्षते योगयुक्तात्मा सर्वत्र समदर्शनः ||६- २९||", engltranslit : "IkShate yogayuktAtmA sarvatra samadarshanaH .. 6\-29.."},
                {sanskrit : "यो मां पश्यति सर्वत्र सर्वं च मयि पश्यति |", engltranslit : "yo mAM pashyati sarvatra sarva.n cha mayi pashyati ."},
                {sanskrit : "तस्याहं न प्रणश्यामि स च मे न प्रणश्यति ||६- ३०||", engltranslit : "tasyAhaM na praNashyAmi sa cha me na praNashyati .. 6\-30.."},
                {sanskrit : "सर्वभूतस्थितं यो मां भजत्येकत्वमास्थितः |", engltranslit : "sarvabhUtasthitaM yo mAM bhajatyekatvamAsthitaH ."},
                {sanskrit : "सर्वथा वर्तमानोऽपि स योगी मयि वर्तते ||६- ३१||", engltranslit : "sarvathA vartamAno.api sa yogI mayi vartate .. 6\-31.."},
                {sanskrit : "आत्मौपम्येन सर्वत्र समं पश्यति योऽर्जुन |", engltranslit : "Atmaupamyena sarvatra samaM pashyati yo.arjuna ."},
                {sanskrit : "सुखं वा यदि वा दुःखं स योगी परमो मतः ||६- ३२||", engltranslit : "sukhaM vA yadi vA duHkhaM sa yogI paramo mataH .. 6\-32.."},
                {sanskrit : "अर्जुन उवाच |", engltranslit : "arjuna uvAcha ."},
                {sanskrit : "योऽयं योगस्त्वया प्रोक्तः साम्येन मधुसूदन |", engltranslit : "yo.ayaM yogastvayA proktaH sAmyena madhusUdana ."},
                {sanskrit : "एतस्याहं न पश्यामि चञ्चलत्वात्स्थितिं स्थिराम् ||६- ३३||", engltranslit : "etasyAhaM na pashyAmi cha~nchalatvAtsthiti.n sthirAm.h .. 6\-33.."},
                {sanskrit : "चञ्चलं हि मनः कृष्ण प्रमाथि बलवद् दृढम् |", engltranslit : "cha~nchalaM hi manaH kR^iShNa pramAthi balavad.h dR^iDham.h ."},
                {sanskrit : "तस्याहं निग्रहं मन्ये वायोरिव सुदुष्करम् ||६- ३४||", engltranslit : "tasyAhaM nigrahaM manye vAyoriva suduShkaram.h .. 6\-34.."},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "असंशयं महाबाहो मनो दुर्निग्रहं चलम् |", engltranslit : "asa.nshayaM mahAbAho mano durnigraha.n chalam.h ."},
                {sanskrit : "अभ्यासेन तु कौन्तेय वैराग्येण च गृह्यते ||६- ३५||", engltranslit : "abhyAsena tu kaunteya vairAgyeNa cha gR^ihyate .. 6\-35.."},
                {sanskrit : "असंयतात्मना योगो दुष्प्राप इति मे मतिः |", engltranslit : "asa.nyatAtmanA yogo duShprApa iti me matiH ."},
                {sanskrit : "वश्यात्मना तु यतता शक्योऽवाप्तुमुपायतः ||६- ३६||", engltranslit : "vashyAtmanA tu yatatA shakyo.avAptumupAyataH .. 6\-36.."},
                {sanskrit : "अर्जुन उवाच |", engltranslit : "arjuna uvAcha ."},
                {sanskrit : "अयतिः श्रद्धयोपेतो योगाच्चलितमानसः |", engltranslit : "ayatiH shraddhayopeto yogAchchalitamAnasaH ."},
                {sanskrit : "अप्राप्य योगसंसिद्धिं कां गतिं कृष्ण गच्छति ||६- ३७||", engltranslit : "aprApya yogasa.nsiddhi.n kA.n gati.n kR^iShNa gachchhati .. 6\-37.."},
                {sanskrit : "कच्चिन्नोभयविभ्रष्टश्छिन्नाभ्रमिव नश्यति |", engltranslit : "kachchinnobhayavibhraShTashchhinnAbhramiva nashyati ."},
                {sanskrit : "अप्रतिष्ठो महाबाहो विमूढो ब्रह्मणः पथि ||६- ३८||", engltranslit : "apratiShTho mahAbAho vimUDho brahmaNaH pathi .. 6\-38.."},
                {sanskrit : "एतन्मे संशयं कृष्ण छेत्तुमर्हस्यशेषतः |", engltranslit : "etanme sa.nshayaM kR^iShNa chhettumarhasyasheShataH ."},
                {sanskrit : "त्वदन्यः संशयस्यास्य छेत्ता न ह्युपपद्यते ||६- ३९||", engltranslit : "tvadanyaH sa.nshayasyAsya chhettA na hyupapadyate .. 6\-39.."},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "पार्थ नैवेह नामुत्र विनाशस्तस्य विद्यते |", engltranslit : "pArtha naiveha nAmutra vinAshastasya vidyate ."},
                {sanskrit : "न हि कल्याणकृत्कश्चिद् दुर्गतिं तात गच्छति ||६- ४०||", engltranslit : "na hi kalyANakR^itkashchid.h durgati.n tAta gachchhati .. 6\-40.."},
                {sanskrit : "प्राप्य पुण्यकृतां लोकानुषित्वा शाश्वतीः समाः |", engltranslit : "prApya puNyakR^itA.n lokAnuShitvA shAshvatIH samAH ."},
                {sanskrit : "शुचीनां श्रीमतां गेहे योगभ्रष्टोऽभिजायते ||६- ४१||", engltranslit : "shuchInA.n shrImatA.n gehe yogabhraShTo.abhijAyate .. 6\-41.."},
                {sanskrit : "अथवा योगिनामेव कुले भवति धीमताम् |", engltranslit : "athavA yoginAmeva kule bhavati dhImatAm.h ."},
                {sanskrit : "एतद्धि दुर्लभतरं लोके जन्म यदीदृशम् ||६- ४२||", engltranslit : "etaddhi durlabhataraM loke janma yadIdR^isham.h .. 6\-42.."},
                {sanskrit : "तत्र तं बुद्धिसंयोगं लभते पौर्वदेहिकम् |", engltranslit : "tatra taM buddhisa.nyogaM labhate paurvadehikam.h ."},
                {sanskrit : "यतते च ततो भूयः संसिद्धौ कुरुनन्दन ||६- ४३||", engltranslit : "yatate cha tato bhUyaH sa.nsiddhau kurunandana .. 6\-43.."},
                {sanskrit : "पूर्वाभ्यासेन तेनैव ह्रियते ह्यवशोऽपि सः |", engltranslit : "pUrvAbhyAsena tenaiva hriyate hyavasho.api saH ."},
                {sanskrit : "जिज्ञासुरपि योगस्य शब्दब्रह्मातिवर्तते ||६- ४४||", engltranslit : "jiGYAsurapi yogasya shabdabrahmAtivartate .. 6\-44.."},
                {sanskrit : "प्रयत्नाद्यतमानस्तु योगी संशुद्धकिल्बिषः |", engltranslit : "prayatnAdyatamAnastu yogI sa.nshuddhakilbiShaH ."},
                {sanskrit : "अनेकजन्मसंसिद्धस्ततो याति परां गतिम् ||६- ४५||", engltranslit : "anekajanmasa.nsiddhastato yAti parAM gatim.h .. 6\-45.."},
                {sanskrit : "तपस्विभ्योऽधिको योगी ज्ञानिभ्योऽपि मतोऽधिकः |", engltranslit : "tapasvibhyo.adhiko yogI GYAnibhyo.api mato.adhikaH ."},
                {sanskrit : "कर्मिभ्यश्चाधिको योगी तस्माद्योगी भवार्जुन ||६- ४६||", engltranslit : "karmibhyashchAdhiko yogI tasmAdyogI bhavArjuna .. 6\-46.."},
                {sanskrit : "योगिनामपि सर्वेषां मद्गतेनान्तरात्मना |", engltranslit : "yoginAmapi sarveShAM mad.hgatenAntarAtmanA ."},
                {sanskrit : "श्रद्धावान्भजते यो मां स मे युक्ततमो मतः ||६- ४७||", engltranslit : "shraddhAvAnbhajate yo mA.n sa me yuktatamo mataH .. 6\-47.."},
                {sanskrit : "ॐ तत्सदिति श्रीमद्भगवद्गीतासूपनिषत्सु", engltranslit : "AUM tatsaditi shrImadbhagavadgItAsUpaniShatsu"},
                {sanskrit : "ब्रह्मविद्यायां योगशास्त्रे श्रीकृष्णार्जुनसंवादे", engltranslit : "brahmavidyAyA.n yogashAstre shrIkR^iShNArjunasa.nvAde"},
                {sanskrit : "आत्मसंयमयोगो नाम षष्ठोऽध्यायः ||६||", engltranslit : "Atmasa.nyamayogo nAma ShaShTho.adhyAyaH .. 6.."},
                {sanskrit : "अथ सप्तमोऽध्यायः | ज्ञानविज्ञानयोगः", engltranslit : "atha saptamo.adhyAyaH . GYAnaviGYAnayogaH"},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "मय्यासक्तमनाः पार्थ योगं युञ्जन्मदाश्रयः |", engltranslit : "mayyAsaktamanAH pArtha yogaM yuJNjanmadAshrayaH ."},
                {sanskrit : "असंशयं समग्रं मां यथा ज्ञास्यसि तच्छृणु ||७- १||", engltranslit : "asa.nshayaM samagraM mA.n yathA GYAsyasi tachchhR^iNu .. 7\-1.."},
                {sanskrit : "ज्ञानं तेऽहं सविज्ञानमिदं वक्ष्याम्यशेषतः |", engltranslit : "GYAnaM te.ahaM saviGYAnamidaM vakShyAmyasheShataH ."},
                {sanskrit : "यज्ज्ञात्वा नेह भूयोऽन्यज्ज्ञातव्यमवशिष्यते ||७- २||", engltranslit : "yajGYAtvA neha bhUyo.anyajGYAtavyamavashiShyate .. 7\-2.."},
                {sanskrit : "मनुष्याणां सहस्रेषु कश्चिद्यतति सिद्धये |", engltranslit : "manuShyANA.n sahasreShu kashchidyatati siddhaye ."},
                {sanskrit : "यततामपि सिद्धानां कश्चिन्मां वेत्ति तत्त्वतः ||७- ३||", engltranslit : "yatatAmapi siddhAnA.n kashchinmA.n vetti tattvataH .. 7\-3.."},
                {sanskrit : "भूमिरापोऽनलो वायुः खं मनो बुद्धिरेव च |", engltranslit : "bhUmirApo.analo vAyuH khaM mano buddhireva cha ."},
                {sanskrit : "अहंकार इतीयं मे भिन्ना प्रकृतिरष्टधा ||७- ४||", engltranslit : "aha.nkAra itIyaM me bhinnA prakR^itiraShTadhA .. 7\-4.."},
                {sanskrit : "अपरेयमितस्त्वन्यां प्रकृतिं विद्धि मे पराम् |", engltranslit : "apareyamitastvanyAM prakR^iti.n viddhi me parAm.h ."},
                {sanskrit : "जीवभूतां महाबाहो ययेदं धार्यते जगत् ||७- ५||", engltranslit : "jIvabhUtAM mahAbAho yayedaM dhAryate jagat.h .. 7\-5.."},
                {sanskrit : "एतद्योनीनि भूतानि सर्वाणीत्युपधारय |", engltranslit : "etadyonIni bhUtAni sarvANItyupadhAraya ."},
                {sanskrit : "अहं कृत्स्नस्य जगतः प्रभवः प्रलयस्तथा ||७- ६||", engltranslit : "ahaM kR^itsnasya jagataH prabhavaH pralayastathA .. 7\-6.."},
                {sanskrit : "मत्तः परतरं नान्यत्किञ्चिदस्ति धनञ्जय |", engltranslit : "mattaH parataraM nAnyatki~nchidasti dhana~njaya ."},
                {sanskrit : "मयि सर्वमिदं प्रोतं सूत्रे मणिगणा इव ||७- ७||", engltranslit : "mayi sarvamidaM protaM sUtre maNigaNA iva .. 7\-7.."},
                {sanskrit : "रसोऽहमप्सु कौन्तेय प्रभास्मि शशिसूर्ययोः |", engltranslit : "raso.ahamapsu kaunteya prabhAsmi shashisUryayoH ."},
                {sanskrit : "प्रणवः सर्ववेदेषु शब्दः खे पौरुषं नृषु ||७- ८||", engltranslit : "praNavaH sarvavedeShu shabdaH khe pauruShaM nR^iShu .. 7\-8.."},
                {sanskrit : "पुण्यो गन्धः पृथिव्यां च तेजश्चास्मि विभावसौ |", engltranslit : "puNyo gandhaH pR^ithivyA.n cha tejashchAsmi vibhAvasau ."},
                {sanskrit : "जीवनं सर्वभूतेषु तपश्चास्मि तपस्विषु ||७- ९||", engltranslit : "jIvanaM sarvabhUteShu tapashchAsmi tapasviShu .. 7\-9.."},
                {sanskrit : "बीजं मां सर्वभूतानां विद्धि पार्थ सनातनम् |", engltranslit : "bIjaM mA.n sarvabhUtAnA.n viddhi pArtha sanAtanam.h ."},
                {sanskrit : "बुद्धिर्बुद्धिमतामस्मि तेजस्तेजस्विनामहम् ||७- १०||", engltranslit : "buddhirbuddhimatAmasmi tejastejasvinAmaham.h .. 7\-10.."},
                {sanskrit : "बलं बलवतां चाहं कामरागविवर्जितम् |", engltranslit : "balaM balavatA.n chAhaM kAmarAgavivarjitam.h ."},
                {sanskrit : "धर्माविरुद्धो भूतेषु कामोऽस्मि भरतर्षभ ||७- ११||", engltranslit : "dharmAviruddho bhUteShu kAmo.asmi bharatarShabha .. 7\-11.."},
                {sanskrit : "ये चैव सात्त्विका भावा राजसास्तामसाश्च ये |", engltranslit : "ye chaiva sAttvikA bhAvA rAjasAstAmasAshcha ye ."},
                {sanskrit : "मत्त एवेति तान्विद्धि न त्वहं तेषु ते मयि ||७- १२||", engltranslit : "matta eveti tAnviddhi na tvahaM teShu te mayi .. 7\-12.."},
                {sanskrit : "त्रिभिर्गुणमयैर्भावैरेभिः सर्वमिदं जगत् |", engltranslit : "tribhirguNamayairbhAvairebhiH sarvamida.n jagat.h ."},
                {sanskrit : "मोहितं नाभिजानाति मामेभ्यः परमव्ययम् ||७- १३||", engltranslit : "mohitaM nAbhijAnAti mAmebhyaH paramavyayam.h .. 7\-13.."},
                {sanskrit : "दैवी ह्येषा गुणमयी मम माया दुरत्यया |", engltranslit : "daivI hyeShA guNamayI mama mAyA duratyayA ."},
                {sanskrit : "मामेव ये प्रपद्यन्ते मायामेतां तरन्ति ते ||७- १४||", engltranslit : "mAmeva ye prapadyante mAyAmetAM taranti te .. 7\-14.."},
                {sanskrit : "न मां दुष्कृतिनो मूढाः प्रपद्यन्ते नराधमाः |", engltranslit : "na mA.n duShkR^itino mUDhAH prapadyante narAdhamAH ."},
                {sanskrit : "माययापहृतज्ञाना आसुरं भावमाश्रिताः ||७- १५||", engltranslit : "mAyayApahR^itaGYAnA AsuraM bhAvamAshritAH .. 7\-15.."},
                {sanskrit : "चतुर्विधा भजन्ते मां जनाः सुकृतिनोऽर्जुन |", engltranslit : "chaturvidhA bhajante mA.n janAH sukR^itino.arjuna ."},
                {sanskrit : "आर्तो जिज्ञासुरर्थार्थी ज्ञानी च भरतर्षभ ||७- १६||", engltranslit : "Arto jiGYAsurarthArthI GYAnI cha bharatarShabha .. 7\-16.."},
                {sanskrit : "तेषां ज्ञानी नित्ययुक्त एकभक्तिर्विशिष्यते |", engltranslit : "teShA.n GYAnI nityayukta ekabhaktirvishiShyate ."},
                {sanskrit : "प्रियो हि ज्ञानिनोऽत्यर्थमहं स च मम प्रियः ||७- १७||", engltranslit : "priyo hi GYAnino.atyarthamahaM sa cha mama priyaH .. 7\-17.."},
                {sanskrit : "उदाराः सर्व एवैते ज्ञानी त्वात्मैव मे मतम् |", engltranslit : "udArAH sarva evaite GYAnI tvAtmaiva me matam.h ."},
                {sanskrit : "आस्थितः स हि युक्तात्मा मामेवानुत्तमां गतिम् ||७- १८||", engltranslit : "AsthitaH sa hi yuktAtmA mAmevAnuttamA.n gatim.h .. 7\-18.."},
                {sanskrit : "बहूनां जन्मनामन्ते ज्ञानवान्मां प्रपद्यते |", engltranslit : "bahUnA.n janmanAmante GYAnavAnmAM prapadyate ."},
                {sanskrit : "वासुदेवः सर्वमिति स महात्मा सुदुर्लभः ||७- १९||", engltranslit : "vAsudevaH sarvamiti sa mahAtmA sudurlabhaH .. 7\-19.."},
                {sanskrit : "कामैस्तैस्तैर्हृतज्ञानाः प्रपद्यन्तेऽन्यदेवताः |", engltranslit : "kAmaistaistairhR^itaGYAnAH prapadyante.anyadevatAH ."},
                {sanskrit : "तं तं नियममास्थाय प्रकृत्या नियताः स्वया ||७- २०||", engltranslit : "taM taM niyamamAsthAya prakR^ityA niyatAH svayA .. 7\-20.."},
                {sanskrit : "यो यो यां यां तनुं भक्तः श्रद्धयार्चितुमिच्छति |", engltranslit : "yo yo yA.n yA.n tanuM bhaktaH shraddhayArchitumichchhati ."},
                {sanskrit : "तस्य तस्याचलां श्रद्धां तामेव विदधाम्यहम् ||७- २१||", engltranslit : "tasya tasyAchalA.n shraddhA.n tAmeva vidadhAmyaham.h .. 7\-21.."},
                {sanskrit : "स तया श्रद्धया युक्तस्तस्याराधनमीहते |", engltranslit : "sa tayA shraddhayA yuktastasyArAdhanamIhate ."},
                {sanskrit : "लभते च ततः कामान्मयैव विहितान्हि तान् ||७- २२||", engltranslit : "labhate cha tataH kAmAnmayaiva vihitAnhi tAn.h .. 7\-22.."},
                {sanskrit : "अन्तवत्तु फलं तेषां तद्भवत्यल्पमेधसाम् |", engltranslit : "antavattu phalaM teShA.n tadbhavatyalpamedhasAm.h ."},
                {sanskrit : "देवान्देवयजो यान्ति मद्भक्ता यान्ति मामपि ||७- २३||", engltranslit : "devAndevayajo yAnti madbhaktA yAnti mAmapi .. 7\-23.."},
                {sanskrit : "अव्यक्तं व्यक्तिमापन्नं मन्यन्ते मामबुद्धयः |", engltranslit : "avyaktaM vyaktimApannaM manyante mAmabuddhayaH ."},
                {sanskrit : "परं भावमजानन्तो ममाव्ययमनुत्तमम् ||७- २४||", engltranslit : "paraM bhAvamajAnanto mamAvyayamanuttamam.h .. 7\-24.."},
                {sanskrit : "नाहं प्रकाशः सर्वस्य योगमायासमावृतः |", engltranslit : "nAhaM prakAshaH sarvasya yogamAyAsamAvR^itaH ."},
                {sanskrit : "मूढोऽयं नाभिजानाति लोको मामजमव्ययम् ||७- २५||", engltranslit : "mUDho.ayaM nAbhijAnAti loko mAmajamavyayam.h .. 7\-25.."},
                {sanskrit : "वेदाहं समतीतानि वर्तमानानि चार्जुन |", engltranslit : "vedAhaM samatItAni vartamAnAni chArjuna ."},
                {sanskrit : "भविष्याणि च भूतानि मां तु वेद न कश्चन ||७- २६||", engltranslit : "bhaviShyANi cha bhUtAni mA.n tu veda na kashchana .. 7\-26.."},
                {sanskrit : "इच्छाद्वेषसमुत्थेन द्वन्द्वमोहेन भारत |", engltranslit : "ichchhAdveShasamutthena dvandvamohena bhArata ."},
                {sanskrit : "सर्वभूतानि सम्मोहं सर्गे यान्ति परन्तप ||७- २७||", engltranslit : "sarvabhUtAni sammohaM sarge yAnti parantapa .. 7\-27.."},
                {sanskrit : "येषां त्वन्तगतं पापं जनानां पुण्यकर्मणाम् |", engltranslit : "yeShA.n tvantagataM pApa.n janAnAM puNyakarmaNAm.h ."},
                {sanskrit : "ते द्वन्द्वमोहनिर्मुक्ता भजन्ते मां दृढव्रताः ||७- २८||", engltranslit : "te dvandvamohanirmuktA bhajante mA.n dR^iDhavratAH .. 7\-28.."},
                {sanskrit : "जरामरणमोक्षाय मामाश्रित्य यतन्ति ये |", engltranslit : "jarAmaraNamokShAya mAmAshritya yatanti ye ."},
                {sanskrit : "ते ब्रह्म तद्विदुः कृत्स्नमध्यात्मं कर्म चाखिलम् ||७- २९||", engltranslit : "te brahma tadviduH kR^itsnamadhyAtmaM karma chAkhilam.h .. 7\-29.."},
                {sanskrit : "साधिभूताधिदैवं मां साधियज्ञं च ये विदुः |", engltranslit : "sAdhibhUtAdhidaivaM mA.n sAdhiyaGYa.n cha ye viduH ."},
                {sanskrit : "प्रयाणकालेऽपि च मां ते विदुर्युक्तचेतसः ||७- ३०||", engltranslit : "prayANakAle.api cha mA.n te viduryuktachetasaH .. 7\-30.."},
                {sanskrit : "ॐ तत्सदिति श्रीमद्भगवद्गीतासूपनिषत्सु", engltranslit : "AUM tatsaditi shrImadbhagavadgItAsUpaniShatsu"},
                {sanskrit : "ब्रह्मविद्यायां योगशास्त्रे श्रीकृष्णार्जुनसंवादे", engltranslit : "brahmavidyAyA.n yogashAstre shrIkR^iShNArjunasa.nvAde"},
                {sanskrit : "ज्ञानविज्ञानयोगो नाम सप्तमोऽध्यायः ||७||", engltranslit : "GYAnaviGYAnayogo nAma saptamo.adhyAyaH .. 7.."},
                {sanskrit : "अथ अष्टमोऽध्यायः | अक्षरब्रह्मयोगः", engltranslit : "atha aShTamo.adhyAyaH . akSharabrahmayogaH"},
                {sanskrit : "अर्जुन उवाच |", engltranslit : "arjuna uvAcha ."},
                {sanskrit : "किं तद् ब्रह्म किमध्यात्मं किं कर्म पुरुषोत्तम |", engltranslit : "ki.n tad.h brahma kimadhyAtma.n kiM karma puruShottama ."},
                {sanskrit : "अधिभूतं च किं प्रोक्तमधिदैवं किमुच्यते ||८- १||", engltranslit : "adhibhUta.n cha kiM proktamadhidaivaM kimuchyate .. 8\-1.."},
                {sanskrit : "अधियज्ञः कथं कोऽत्र देहेऽस्मिन्मधुसूदन |", engltranslit : "adhiyaGYaH kathaM ko.atra dehe.asminmadhusUdana ."},
                {sanskrit : "प्रयाणकाले च कथं ज्ञेयोऽसि नियतात्मभिः ||८- २||", engltranslit : "prayANakAle cha kathaM GYeyo.asi niyatAtmabhiH .. 8\-2.."},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "अक्षरं ब्रह्म परमं स्वभावोऽध्यात्ममुच्यते |", engltranslit : "akSharaM brahma paramaM svabhAvo.adhyAtmamuchyate ."},
                {sanskrit : "भूतभावोद्भवकरो विसर्गः कर्मसंज्ञितः ||८- ३||", engltranslit : "bhUtabhAvodbhavakaro visargaH karmasa.nGYitaH .. 8\-3.."},
                {sanskrit : "अधिभूतं क्षरो भावः पुरुषश्चाधिदैवतम् |", engltranslit : "adhibhUtaM kSharo bhAvaH puruShashchAdhidaivatam.h ."},
                {sanskrit : "अधियज्ञोऽहमेवात्र देहे देहभृतां वर ||८- ४||", engltranslit : "adhiyaGYo.ahamevAtra dehe dehabhR^itA.n vara .. 8\-4.."},
                {sanskrit : "अन्तकाले च मामेव स्मरन्मुक्त्वा कलेवरम् |", engltranslit : "antakAle cha mAmeva smaranmuktvA kalevaram.h ."},
                {sanskrit : "यः प्रयाति स मद्भावं याति नास्त्यत्र संशयः ||८- ५||", engltranslit : "yaH prayAti sa madbhAvaM yAti nAstyatra sa.nshayaH .. 8\-5.."},
                {sanskrit : "यं यं वापि स्मरन्भावं त्यजत्यन्ते कलेवरम् |", engltranslit : "yaM yaM vApi smaranbhAvaM tyajatyante kalevaram.h ."},
                {sanskrit : "तं तमेवैति कौन्तेय सदा तद्भावभावितः ||८- ६||", engltranslit : "taM tamevaiti kaunteya sadA tadbhAvabhAvitaH .. 8\-6.."},
                {sanskrit : "तस्मात्सर्वेषु कालेषु मामनुस्मर युध्य च |", engltranslit : "tasmAtsarveShu kAleShu mAmanusmara yudhya cha ."},
                {sanskrit : "मय्यर्पितमनोबुद्धिर्मामेवैष्यस्यसंशयः ||८- ७||orसंशयम्", engltranslit : "mayyarpitamanobuddhirmAmevaiShyasyasa.nshayaH .. 8\-7.. ##or##sa.nshayam"},
                {sanskrit : "अभ्यासयोगयुक्तेन चेतसा नान्यगामिना |", engltranslit : "abhyAsayogayuktena chetasA nAnyagAminA ."},
                {sanskrit : "परमं पुरुषं दिव्यं याति पार्थानुचिन्तयन् ||८- ८||", engltranslit : "paramaM puruShaM divya.n yAti pArthAnuchintayan.h .. 8\-8.."},
                {sanskrit : "कविं पुराणमनुशासितार- ", engltranslit : "kaviM purANamanushAsitAra\-"},
                {sanskrit : "मणोरणीयंसमनुस्मरेद्यः |", engltranslit : "maNoraNIya.nsamanusmaredyaH ."},
                {sanskrit : "सर्वस्य धातारमचिन्त्यरूप- ", engltranslit : "sarvasya dhAtAramachintyarUpa\-"},
                {sanskrit : "मादित्यवर्णं तमसः परस्तात् ||८- ९||", engltranslit : "mAdityavarNaM tamasaH parastAt.h .. 8\-9.."},
                {sanskrit : "प्रयाणकाले मनसाऽचलेन", engltranslit : "prayANakAle manasA.achalena"},
                {sanskrit : "भक्त्या युक्तो योगबलेन चैव |", engltranslit : "bhaktyA yukto yogabalena chaiva ."},
                {sanskrit : "भ्रुवोर्मध्ये प्राणमावेश्य सम्यक्", engltranslit : "bhruvormadhye prANamAveshya samyak.h"},
                {sanskrit : "स तं परं पुरुषमुपैति दिव्यम् ||८- १०||", engltranslit : "sa taM paraM puruShamupaiti divyam.h .. 8\-10.."},
                {sanskrit : "यदक्षरं वेदविदो वदन्ति", engltranslit : "yadakSharaM vedavido vadanti"},
                {sanskrit : "विशन्ति यद्यतयो वीतरागाः |", engltranslit : "vishanti yadyatayo vItarAgAH ."},
                {sanskrit : "यदिच्छन्तो ब्रह्मचर्यं चरन्ति", engltranslit : "yadichchhanto brahmacharya.n charanti"},
                {sanskrit : "तत्ते पदं संग्रहेण प्रवक्ष्ये ||८- ११||", engltranslit : "tatte padaM sa.ngraheNa pravakShye .. 8\-11.."},
                {sanskrit : "सर्वद्वाराणि संयम्य मनो हृदि निरुध्य च |", engltranslit : "sarvadvArANi sa.nyamya mano hR^idi nirudhya cha ."},
                {sanskrit : "मूध्न्यार्धायात्मनः प्राणमास्थितो योगधारणाम् ||८- १२||", engltranslit : "mUdhnyA.rdhAyAtmanaH prANamAsthito yogadhAraNAm.h .. 8\-12.."},
                {sanskrit : "ओमित्येकाक्षरं ब्रह्म व्याहरन्मामनुस्मरन् |", engltranslit : "omityekAkSharaM brahma vyAharanmAmanusmaran.h ."},
                {sanskrit : "यः प्रयाति त्यजन्देहं स याति परमां गतिम् ||८- १३||", engltranslit : "yaH prayAti tyajandehaM sa yAti paramA.n gatim.h .. 8\-13.."},
                {sanskrit : "अनन्यचेताः सततं यो मां स्मरति नित्यशः |", engltranslit : "ananyachetAH satataM yo mA.n smarati nityashaH ."},
                {sanskrit : "तस्याहं सुलभः पार्थ नित्ययुक्तस्य योगिनः ||८- १४||", engltranslit : "tasyAhaM sulabhaH pArtha nityayuktasya yoginaH .. 8\-14.."},
                {sanskrit : "मामुपेत्य पुनर्जन्म दुःखालयमशाश्वतम् |", engltranslit : "mAmupetya punarjanma duHkhAlayamashAshvatam.h ."},
                {sanskrit : "नाप्नुवन्ति महात्मानः संसिद्धिं परमां गताः ||८- १५||", engltranslit : "nApnuvanti mahAtmAnaH sa.nsiddhiM paramA.n gatAH .. 8\-15.."},
                {sanskrit : "आब्रह्मभुवनाल्लोकाः पुनरावर्तिनोऽर्जुन |", engltranslit : "AbrahmabhuvanAllokAH punarAvartino.arjuna ."},
                {sanskrit : "मामुपेत्य तु कौन्तेय पुनर्जन्म न विद्यते ||८- १६||", engltranslit : "mAmupetya tu kaunteya punarjanma na vidyate .. 8\-16.."},
                {sanskrit : "सहस्रयुगपर्यन्तमहर्यद् ब्रह्मणो विदुः |", engltranslit : "sahasrayugaparyantamaharyad.h brahmaNo viduH ."},
                {sanskrit : "रात्रिं युगसहस्रान्तां तेऽहोरात्रविदो जनाः ||८- १७||", engltranslit : "rAtri.n yugasahasrAntA.n te.ahorAtravido janAH .. 8\-17.."},
                {sanskrit : "अव्यक्ताद् व्यक्तयः सर्वाः प्रभवन्त्यहरागमे |", engltranslit : "avyaktAd.h vyaktayaH sarvAH prabhavantyaharAgame ."},
                {sanskrit : "रात्र्यागमे प्रलीयन्ते तत्रैवाव्यक्तसंज्ञके ||८- १८||", engltranslit : "rAtryAgame pralIyante tatraivAvyaktasa.nGYake .. 8\-18.."},
                {sanskrit : "भूतग्रामः स एवायं भूत्वा भूत्वा प्रलीयते |", engltranslit : "bhUtagrAmaH sa evAyaM bhUtvA bhUtvA pralIyate ."},
                {sanskrit : "रात्र्यागमेऽवशः पार्थ प्रभवत्यहरागमे ||८- १९||", engltranslit : "rAtryAgame.avashaH pArtha prabhavatyaharAgame .. 8\-19.."},
                {sanskrit : "परस्तस्मात्तु भावोऽन्योऽव्यक्तोऽव्यक्तात्सनातनः |", engltranslit : "parastasmAttu bhAvo.anyo.avyakto.avyaktAtsanAtanaH ."},
                {sanskrit : "यः स सर्वेषु भूतेषु नश्यत्सु न विनश्यति ||८- २०||", engltranslit : "yaH sa sarveShu bhUteShu nashyatsu na vinashyati .. 8\-20.."},
                {sanskrit : "अव्यक्तोऽक्षर इत्युक्तस्तमाहुः परमां गतिम् |", engltranslit : "avyakto.akShara ityuktastamAhuH paramA.n gatim.h ."},
                {sanskrit : "यं प्राप्य न निवर्तन्ते तद्धाम परमं मम ||८- २१||", engltranslit : "yaM prApya na nivartante taddhAma paramaM mama .. 8\-21.."},
                {sanskrit : "पुरुषः स परः पार्थ भक्त्या लभ्यस्त्वनन्यया |", engltranslit : "puruShaH sa paraH pArtha bhaktyA labhyastvananyayA ."},
                {sanskrit : "यस्यान्तःस्थानि भूतानि येन सर्वमिदं ततम् ||८- २२||", engltranslit : "yasyAntaHsthAni bhUtAni yena sarvamidaM tatam.h .. 8\-22.."},
                {sanskrit : "यत्र काले त्वनावृत्तिमावृत्तिं चैव योगिनः |", engltranslit : "yatra kAle tvanAvR^ittimAvR^itti.n chaiva yoginaH ."},
                {sanskrit : "प्रयाता यान्ति तं कालं वक्ष्यामि भरतर्षभ ||८- २३||", engltranslit : "prayAtA yAnti taM kAlaM vakShyAmi bharatarShabha .. 8\-23.."},
                {sanskrit : "अग्निर्ज्योतिरहः शुक्लः षण्मासा उत्तरायणम् |", engltranslit : "agnirjyotirahaH shuklaH ShaNmAsA uttarAyaNam.h ."},
                {sanskrit : "तत्र प्रयाता गच्छन्ति ब्रह्म ब्रह्मविदो जनाः ||८- २४||", engltranslit : "tatra prayAtA gachchhanti brahma brahmavido janAH .. 8\-24.."},
                {sanskrit : "धूमो रात्रिस्तथा कृष्णः षण्मासा दक्षिणायनम् |", engltranslit : "dhUmo rAtristathA kR^iShNaH ShaNmAsA dakShiNAyanam.h ."},
                {sanskrit : "तत्र चान्द्रमसं ज्योतिर्योगी प्राप्य निवर्तते ||८- २५||", engltranslit : "tatra chAndramasa.n jyotiryogI prApya nivartate .. 8\-25.."},
                {sanskrit : "शुक्लकृष्णे गती ह्येते जगतः शाश्वते मते |", engltranslit : "shuklakR^iShNe gatI hyete jagataH shAshvate mate ."},
                {sanskrit : "एकया यात्यनावृत्तिमन्ययावर्तते पुनः ||८- २६||", engltranslit : "ekayA yAtyanAvR^ittimanyayAvartate punaH .. 8\-26.."},
                {sanskrit : "नैते सृती पार्थ जानन्योगी मुह्यति कश्चन |", engltranslit : "naite sR^itI pArtha jAnanyogI muhyati kashchana ."},
                {sanskrit : "तस्मात्सर्वेषु कालेषु योगयुक्तो भवार्जुन ||८- २७||", engltranslit : "tasmAtsarveShu kAleShu yogayukto bhavArjuna .. 8\-27.."},
                {sanskrit : "वेदेषु यज्ञेषु तपःसु चैव", engltranslit : "vedeShu yaGYeShu tapaHsu chaiva"},
                {sanskrit : "दानेषु यत्पुण्यफलं प्रदिष्टम् |", engltranslit : "dAneShu yatpuNyaphalaM pradiShTam.h ."},
                {sanskrit : "अत्येति तत्सर्वमिदं विदित्वा", engltranslit : "atyeti tatsarvamidaM viditvA"},
                {sanskrit : "योगी परं स्थानमुपैति चाद्यम् ||८- २८||", engltranslit : "yogI paraM sthAnamupaiti chAdyam.h .. 8\-28.."},
                {sanskrit : "ॐ तत्सदिति श्रीमद्भगवद्गीतासूपनिषत्सु", engltranslit : "AUM tatsaditi shrImadbhagavadgItAsUpaniShatsu"},
                {sanskrit : "ब्रह्मविद्यायां योगशास्त्रे श्रीकृष्णार्जुनसंवादे", engltranslit : "brahmavidyAyA.n yogashAstre shrIkR^iShNArjunasa.nvAde"},
                {sanskrit : "अक्षरब्रह्मयोगो नामाष्टमोऽध्यायः ||८||", engltranslit : "akSharabrahmayogo nAmAShTamo.adhyAyaH .. 8.."},
                {sanskrit : "अथ नवमोऽध्यायः | राजविद्याराजगुह्ययोगः", engltranslit : "atha navamo.adhyAyaH . rAjavidyArAjaguhyayogaH"},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "इदं तु ते गुह्यतमं प्रवक्ष्याम्यनसूयवे |", engltranslit : "idaM tu te guhyatamaM pravakShyAmyanasUyave ."},
                {sanskrit : "ज्ञानं विज्ञानसहितं यज्ज्ञात्वा मोक्ष्यसेऽशुभात् ||९- १||", engltranslit : "GYAnaM viGYAnasahitaM yajGYAtvA mokShyase.ashubhAt.h .. 9\-1.."},
                {sanskrit : "राजविद्या राजगुह्यं पवित्रमिदमुत्तमम् |", engltranslit : "rAjavidyA rAjaguhyaM pavitramidamuttamam.h ."},
                {sanskrit : "प्रत्यक्षावगमं धर्म्यं सुसुखं कर्तुमव्ययम् ||९- २||", engltranslit : "pratyakShAvagamaM dharmya.n susukhaM kartumavyayam.h .. 9\-2.."},
                {sanskrit : "अश्रद्दधानाः पुरुषा धर्मस्यास्य परन्तप |", engltranslit : "ashraddadhAnAH puruShA dharmasyAsya parantapa ."},
                {sanskrit : "अप्राप्य मां निवर्तन्ते मृत्युसंसारवर्त्मनि ||९- ३||", engltranslit : "aprApya mA.n nivartante mR^ityusa.nsAravartmani .. 9\-3.."},
                {sanskrit : "मया ततमिदं सर्वं जगदव्यक्तमूर्तिना |", engltranslit : "mayA tatamidaM sarva.n jagadavyaktamUrtinA ."},
                {sanskrit : "मत्स्थानि सर्वभूतानि न चाहं तेष्ववस्थितः ||९- ४||", engltranslit : "matsthAni sarvabhUtAni na chAhaM teShvavasthitaH .. 9\-4.."},
                {sanskrit : "न च मत्स्थानि भूतानि पश्य मे योगमैश्वरम् |", engltranslit : "na cha matsthAni bhUtAni pashya me yogamaishvaram.h ."},
                {sanskrit : "भूतभृन्न च भूतस्थो ममात्मा भूतभावनः ||९- ५||", engltranslit : "bhUtabhR^inna cha bhUtastho mamAtmA bhUtabhAvanaH .. 9\-5.."},
                {sanskrit : "यथाकाशस्थितो नित्यं वायुः सर्वत्रगो महान् |", engltranslit : "yathAkAshasthito nitya.n vAyuH sarvatrago mahAn.h ."},
                {sanskrit : "तथा सर्वाणि भूतानि मत्स्थानीत्युपधारय ||९- ६||", engltranslit : "tathA sarvANi bhUtAni matsthAnItyupadhAraya .. 9\-6.."},
                {sanskrit : "सर्वभूतानि कौन्तेय प्रकृतिं यान्ति मामिकाम् |", engltranslit : "sarvabhUtAni kaunteya prakR^iti.n yAnti mAmikAm.h ."},
                {sanskrit : "कल्पक्षये पुनस्तानि कल्पादौ विसृजाम्यहम् ||९- ७||", engltranslit : "kalpakShaye punastAni kalpAdau visR^ijAmyaham.h .. 9\-7.."},
                {sanskrit : "प्रकृतिं स्वामवष्टभ्य विसृजामि पुनः पुनः |", engltranslit : "prakR^iti.n svAmavaShTabhya visR^ijAmi punaH punaH ."},
                {sanskrit : "भूतग्राममिमं कृत्स्नमवशं प्रकृतेर्वशात् ||९- ८||", engltranslit : "bhUtagrAmamimaM kR^itsnamavashaM prakR^itervashAt.h .. 9\-8.."},
                {sanskrit : "न च मां तानि कर्माणि निबध्नन्ति धनञ्जय |", engltranslit : "na cha mA.n tAni karmANi nibadhnanti dhana~njaya ."},
                {sanskrit : "उदासीनवदासीनमसक्तं तेषु कर्मसु ||९- ९||", engltranslit : "udAsInavadAsInamasakta.n teShu karmasu .. 9\-9.."},
                {sanskrit : "मयाध्यक्षेण प्रकृतिः सूयते सचराचरम् |", engltranslit : "mayAdhyakSheNa prakR^itiH sUyate sacharAcharam.h ."},
                {sanskrit : "हेतुनानेन कौन्तेय जगद्विपरिवर्तते ||९- १०||", engltranslit : "hetunAnena kaunteya jagadviparivartate .. 9\-10.."},
                {sanskrit : "अवजानन्ति मां मूढा मानुषीं तनुमाश्रितम् |", engltranslit : "avajAnanti mAM mUDhA mAnuShI.n tanumAshritam.h ."},
                {sanskrit : "परं भावमजानन्तो मम भूतमहेश्वरम् ||९- ११||", engltranslit : "paraM bhAvamajAnanto mama bhUtamaheshvaram.h .. 9\-11.."},
                {sanskrit : "मोघाशा मोघकर्माणो मोघज्ञाना विचेतसः |", engltranslit : "moghAshA moghakarmANo moghaGYAnA vichetasaH ."},
                {sanskrit : "राक्षसीमासुरीं चैव प्रकृतिं मोहिनीं श्रिताः ||९- १२||", engltranslit : "rAkShasImAsurI.n chaiva prakR^itiM mohinI.n shritAH .. 9\-12.."},
                {sanskrit : "महात्मानस्तु मां पार्थ दैवीं प्रकृतिमाश्रिताः |", engltranslit : "mahAtmAnastu mAM pArtha daivIM prakR^itimAshritAH ."},
                {sanskrit : "भजन्त्यनन्यमनसो ज्ञात्वा भूतादिमव्ययम् ||९- १३||", engltranslit : "bhajantyananyamanaso GYAtvA bhUtAdimavyayam.h .. 9\-13.."},
                {sanskrit : "सततं कीर्तयन्तो मां यतन्तश्च दृढव्रताः |", engltranslit : "satataM kIrtayanto mA.n yatantashcha dR^iDhavratAH ."},
                {sanskrit : "नमस्यन्तश्च मां भक्त्या नित्ययुक्ता उपासते ||९- १४||", engltranslit : "namasyantashcha mAM bhaktyA nityayuktA upAsate .. 9\-14.."},
                {sanskrit : "ज्ञानयज्ञेन चाप्यन्ये यजन्तो मामुपासते |", engltranslit : "GYAnayaGYena chApyanye yajanto mAmupAsate ."},
                {sanskrit : "एकत्वेन पृथक्त्वेन बहुधा विश्वतोमुखम् ||९- १५||", engltranslit : "ekatvena pR^ithaktvena bahudhA vishvatomukham.h .. 9\-15.."},
                {sanskrit : "अहं क्रतुरहं यज्ञः स्वधाहमहमौषधम् |", engltranslit : "ahaM kraturahaM yaGYaH svadhAhamahamauShadham.h ."},
                {sanskrit : "मन्त्रोऽहमहमेवाज्यमहमग्निरहं हुतम् ||९- १६||", engltranslit : "mantro.ahamahamevAjyamahamagnirahaM hutam.h .. 9\-16.."},
                {sanskrit : "पिताहमस्य जगतो माता धाता पितामहः |", engltranslit : "pitAhamasya jagato mAtA dhAtA pitAmahaH ."},
                {sanskrit : "वेद्यं पवित्रमोंकार ऋक्साम यजुरेव च ||९- १७||", engltranslit : "vedyaM pavitramo.nkAra R^iksAma yajureva cha .. 9\-17.."},
                {sanskrit : "गतिर्भर्ता प्रभुः साक्षी निवासः शरणं सुहृत् |", engltranslit : "gatirbhartA prabhuH sAkShI nivAsaH sharaNa.n suhR^it.h ."},
                {sanskrit : "प्रभवः प्रलयः स्थानं निधानं बीजमव्ययम् ||९- १८||", engltranslit : "prabhavaH pralayaH sthAnaM nidhAnaM bIjamavyayam.h .. 9\-18.."},
                {sanskrit : "तपाम्यहमहं वर्षं निगृह्णाम्युत्सृजामि च |", engltranslit : "tapAmyahamahaM varSha.n nigR^ihNAmyutsR^ijAmi cha ."},
                {sanskrit : "अमृतं चैव मृत्युश्च सदसच्चाहमर्जुन ||९- १९||", engltranslit : "amR^ita.n chaiva mR^ityushcha sadasachchAhamarjuna .. 9\-19.."},
                {sanskrit : "त्रैविद्या मां सोमपाः पूतपापा", engltranslit : "traividyA mA.n somapAH pUtapApA"},
                {sanskrit : "यज्ञैरिष्ट्वा स्वर्गतिं प्रार्थयन्ते |", engltranslit : "yaGYairiShTvA svargatiM prArthayante ."},
                {sanskrit : "ते पुण्यमासाद्य सुरेन्द्रलोक- ", engltranslit : "te puNyamAsAdya surendraloka\-"},
                {sanskrit : "मश्नन्ति दिव्यान्दिवि देवभोगान् ||९- २०||", engltranslit : "mashnanti divyAndivi devabhogAn.h .. 9\-20.."},
                {sanskrit : "ते तं भुक्त्वा स्वर्गलोकं विशालं", engltranslit : "te taM bhuktvA svargalokaM vishAlaM"},
                {sanskrit : "क्षीणे पुण्ये मर्त्यलोकं विशन्ति |", engltranslit : "kShINe puNye martyalokaM vishanti ."},
                {sanskrit : "एवं त्रयीधर्ममनुप्रपन्ना", engltranslit : "evaM trayIdharmamanuprapannA"},
                {sanskrit : "गतागतं कामकामा लभन्ते ||९- २१||", engltranslit : "gatAgataM kAmakAmA labhante .. 9\-21.."},
                {sanskrit : "अनन्याश्चिन्तयन्तो मां ये जनाः पर्युपासते |", engltranslit : "ananyAshchintayanto mA.n ye janAH paryupAsate ."},
                {sanskrit : "तेषां नित्याभियुक्तानां योगक्षेमं वहाम्यहम् ||९- २२||", engltranslit : "teShA.n nityAbhiyuktAnA.n yogakShemaM vahAmyaham.h .. 9\-22.."},
                {sanskrit : "येऽप्यन्यदेवता भक्ता यजन्ते श्रद्धयान्विताः |", engltranslit : "ye.apyanyadevatA bhaktA yajante shraddhayAnvitAH ."},
                {sanskrit : "तेऽपि मामेव कौन्तेय यजन्त्यविधिपूर्वकम् ||९- २३||", engltranslit : "te.api mAmeva kaunteya yajantyavidhipUrvakam.h .. 9\-23.."},
                {sanskrit : "अहं हि सर्वयज्ञानां भोक्ता च प्रभुरेव च |", engltranslit : "ahaM hi sarvayaGYAnAM bhoktA cha prabhureva cha ."},
                {sanskrit : "न तु मामभिजानन्ति तत्त्वेनातश्च्यवन्ति ते ||९- २४||", engltranslit : "na tu mAmabhijAnanti tattvenAtashchyavanti te .. 9\-24.."},
                {sanskrit : "यान्ति देवव्रता देवान्पितॄन्यान्ति पितृव्रताः |", engltranslit : "yAnti devavratA devAnpitR^InyAnti pitR^ivratAH ."},
                {sanskrit : "भूतानि यान्ति भूतेज्या यान्ति मद्याजिनोऽपि माम् ||९- २५||", engltranslit : "bhUtAni yAnti bhUtejyA yAnti madyAjino.api mAm.h .. 9\-25.."},
                {sanskrit : "पत्रं पुष्पं फलं तोयं यो मे भक्त्या प्रयच्छति |", engltranslit : "patraM puShpaM phalaM toyaM yo me bhaktyA prayachchhati ."},
                {sanskrit : "तदहं भक्त्युपहृतमश्नामि प्रयतात्मनः ||९- २६||", engltranslit : "tadahaM bhaktyupahR^itamashnAmi prayatAtmanaH .. 9\-26.."},
                {sanskrit : "यत्करोषि यदश्नासि यज्जुहोषि ददासि यत् |", engltranslit : "yatkaroShi yadashnAsi yajjuhoShi dadAsi yat.h ."},
                {sanskrit : "यत्तपस्यसि कौन्तेय तत्कुरुष्व मदर्पणम् ||९- २७||", engltranslit : "yattapasyasi kaunteya tatkuruShva madarpaNam.h .. 9\-27.."},
                {sanskrit : "शुभाशुभफलैरेवं मोक्ष्यसे कर्मबन्धनैः |", engltranslit : "shubhAshubhaphalairevaM mokShyase karmabandhanaiH ."},
                {sanskrit : "संन्यासयोगयुक्तात्मा विमुक्तो मामुपैष्यसि ||९- २८||", engltranslit : "sa.nnyAsayogayuktAtmA vimukto mAmupaiShyasi .. 9\-28.."},
                {sanskrit : "समोऽहं सर्वभूतेषु न मे द्वेष्योऽस्ति न प्रियः |", engltranslit : "samo.ahaM sarvabhUteShu na me dveShyo.asti na priyaH ."},
                {sanskrit : "ये भजन्ति तु मां भक्त्या मयि ते तेषु चाप्यहम् ||९- २९||", engltranslit : "ye bhajanti tu mAM bhaktyA mayi te teShu chApyaham.h .. 9\-29.."},
                {sanskrit : "अपि चेत्सुदुराचारो भजते मामनन्यभाक् |", engltranslit : "api chetsudurAchAro bhajate mAmananyabhAk.h ."},
                {sanskrit : "साधुरेव स मन्तव्यः सम्यग्व्यवसितो हि सः ||९- ३०||", engltranslit : "sAdhureva sa mantavyaH samyagvyavasito hi saH .. 9\-30.."},
                {sanskrit : "क्षिप्रं भवति धर्मात्मा शश्वच्छान्तिं निगच्छति |", engltranslit : "kShipraM bhavati dharmAtmA shashvachchhAnti.n nigachchhati ."},
                {sanskrit : "कौन्तेय प्रतिजानीहि न मे भक्तः प्रणश्यति ||९- ३१||", engltranslit : "kaunteya pratijAnIhi na me bhaktaH praNashyati .. 9\-31.."},
                {sanskrit : "मां हि पार्थ व्यपाश्रित्य येऽपि स्युः पापयोनयः |", engltranslit : "mA.n hi pArtha vyapAshritya ye.api syuH pApayonayaH ."},
                {sanskrit : "स्त्रियो वैश्यास्तथा शूद्रास्तेऽपि यान्ति परां गतिम् ||९- ३२||", engltranslit : "striyo vaishyAstathA shUdrAste.api yAnti parAM gatim.h .. 9\-32.."},
                {sanskrit : "किं पुनर्ब्राह्मणाः पुण्या भक्ता राजर्षयस्तथा |", engltranslit : "kiM punarbrAhmaNAH puNyA bhaktA rAjarShayastathA ."},
                {sanskrit : "अनित्यमसुखं लोकमिमं प्राप्य भजस्व माम् ||९- ३३||", engltranslit : "anityamasukhaM lokamimaM prApya bhajasva mAm.h .. 9\-33.."},
                {sanskrit : "मन्मना भव मद्भक्तो मद्याजी मां नमस्कुरु |", engltranslit : "manmanA bhava madbhakto madyAjI mA.n namaskuru ."},
                {sanskrit : "मामेवैष्यसि युक्त्वैवमात्मानं मत्परायणः ||९- ३४||", engltranslit : "mAmevaiShyasi yuktvaivamAtmAnaM matparAyaNaH .. 9\-34.."},
                {sanskrit : "ॐ तत्सदिति श्रीमद्भगवद्गीतासूपनिषत्सु", engltranslit : "AUM tatsaditi shrImadbhagavadgItAsUpaniShatsu"},
                {sanskrit : "ब्रह्मविद्यायां योगशास्त्रे श्रीकृष्णार्जुनसंवादे", engltranslit : "brahmavidyAyA.n yogashAstre shrIkR^iShNArjunasa.nvAde"},
                {sanskrit : "राजविद्याराजगुह्ययोगो नाम नवमोऽध्यायः ||९||", engltranslit : "rAjavidyArAjaguhyayogo nAma navamo.adhyAyaH .. 9.."},
                {sanskrit : "अथ दशमोऽध्यायः | विभूतियोगः", engltranslit : "atha dashamo.adhyAyaH . vibhUtiyogaH"},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "भूय एव महाबाहो शृणु मे परमं वचः |", engltranslit : "bhUya eva mahAbAho shR^iNu me paramaM vachaH ."},
                {sanskrit : "यत्तेऽहं प्रीयमाणाय वक्ष्यामि हितकाम्यया ||१०- १||", engltranslit : "yatte.ahaM prIyamANAya vakShyAmi hitakAmyayA .. 10\-1.."},
                {sanskrit : "न मे विदुः सुरगणाः प्रभवं न महर्षयः |", engltranslit : "na me viduH suragaNAH prabhavaM na maharShayaH ."},
                {sanskrit : "अहमादिर्हि देवानां महर्षीणां च सर्वशः ||१०- २||", engltranslit : "ahamAdirhi devAnAM maharShINA.n cha sarvashaH .. 10\-2.."},
                {sanskrit : "यो मामजमनादिं च वेत्ति लोकमहेश्वरम् |", engltranslit : "yo mAmajamanAdi.n cha vetti lokamaheshvaram.h ."},
                {sanskrit : "असम्मूढः स मर्त्येषु सर्वपापैः प्रमुच्यते ||१०- ३||", engltranslit : "asammUDhaH sa martyeShu sarvapApaiH pramuchyate .. 10\-3.."},
                {sanskrit : "बुद्धिर्ज्ञानमसम्मोहः क्षमा सत्यं दमः शमः |", engltranslit : "buddhirGYAnamasammohaH kShamA satya.n damaH shamaH ."},
                {sanskrit : "सुखं दुःखं भवोऽभावो भयं चाभयमेव च ||१०- ४||", engltranslit : "sukhaM duHkhaM bhavo.abhAvo bhaya.n chAbhayameva cha .. 10\-4.."},
                {sanskrit : "अहिंसा समता तुष्टिस्तपो दानं यशोऽयशः |", engltranslit : "ahi.nsA samatA tuShTistapo dAnaM yasho.ayashaH ."},
                {sanskrit : "भवन्ति भावा भूतानां मत्त एव पृथग्विधाः ||१०- ५||", engltranslit : "bhavanti bhAvA bhUtAnAM matta eva pR^ithagvidhAH .. 10\-5.."},
                {sanskrit : "महर्षयः सप्त पूर्वे चत्वारो मनवस्तथा |", engltranslit : "maharShayaH sapta pUrve chatvAro manavastathA ."},
                {sanskrit : "मद्भावा मानसा जाता येषां लोक इमाः प्रजाः ||१०- ६||", engltranslit : "madbhAvA mAnasA jAtA yeShA.n loka imAH prajAH .. 10\-6.."},
                {sanskrit : "एतां विभूतिं योगं च मम यो वेत्ति तत्त्वतः |", engltranslit : "etA.n vibhUti.n yoga.n cha mama yo vetti tattvataH ."},
                {sanskrit : "सोऽविकम्पेन योगेन युज्यते नात्र संशयः ||१०- ७||", engltranslit : "so.avikampena yogena yujyate nAtra sa.nshayaH .. 10\-7.."},
                {sanskrit : "अहं सर्वस्य प्रभवो मत्तः सर्वं प्रवर्तते |", engltranslit : "ahaM sarvasya prabhavo mattaH sarvaM pravartate ."},
                {sanskrit : "इति मत्वा भजन्ते मां बुधा भावसमन्विताः ||१०- ८||", engltranslit : "iti matvA bhajante mAM budhA bhAvasamanvitAH .. 10\-8.."},
                {sanskrit : "मच्चित्ता मद्गतप्राणा बोधयन्तः परस्परम् |", engltranslit : "machchittA madgataprANA bodhayantaH parasparam.h ."},
                {sanskrit : "कथयन्तश्च मां नित्यं तुष्यन्ति च रमन्ति च ||१०- ९||", engltranslit : "kathayantashcha mA.n nitya.n tuShyanti cha ramanti cha .. 10\-9.."},
                {sanskrit : "तेषां सततयुक्तानां भजतां प्रीतिपूर्वकम् |", engltranslit : "teShA.n satatayuktAnAM bhajatAM prItipUrvakam.h ."},
                {sanskrit : "ददामि बुद्धियोगं तं येन मामुपयान्ति ते ||१०- १०||", engltranslit : "dadAmi buddhiyogaM taM yena mAmupayAnti te .. 10\-10.."},
                {sanskrit : "तेषामेवानुकम्पार्थमहमज्ञानजं तमः |", engltranslit : "teShAmevAnukampArthamahamaGYAnajaM tamaH ."},
                {sanskrit : "नाशयाम्यात्मभावस्थो ज्ञानदीपेन भास्वता ||१०- ११||", engltranslit : "nAshayAmyAtmabhAvastho GYAnadIpena bhAsvatA .. 10\-11.."},
                {sanskrit : "अर्जुन उवाच |", engltranslit : "arjuna uvAcha ."},
                {sanskrit : "परं ब्रह्म परं धाम पवित्रं परमं भवान् |", engltranslit : "paraM brahma paraM dhAma pavitraM paramaM bhavAn.h ."},
                {sanskrit : "पुरुषं शाश्वतं दिव्यमादिदेवमजं विभुम् ||१०- १२||", engltranslit : "puruShaM shAshvataM divyamAdidevamajaM vibhum.h .. 10\-12.."},
                {sanskrit : "आहुस्त्वामृषयः सर्वे देवर्षिर्नारदस्तथा |", engltranslit : "AhustvAmR^iShayaH sarve devarShirnAradastathA ."},
                {sanskrit : "असितो देवलो व्यासः स्वयं चैव ब्रवीषि मे ||१०- १३||", engltranslit : "asito devalo vyAsaH svaya.n chaiva bravIShi me .. 10\-13.."},
                {sanskrit : "सर्वमेतदृतं मन्ये यन्मां वदसि केशव |", engltranslit : "sarvametadR^itaM manye yanmA.n vadasi keshava ."},
                {sanskrit : "न हि ते भगवन्व्यक्तिं विदुर्देवा न दानवाः ||१०- १४||", engltranslit : "na hi te bhagavanvyakti.n vidurdevA na dAnavAH .. 10\-14.."},
                {sanskrit : "स्वयमेवात्मनात्मानं वेत्थ त्वं पुरुषोत्तम |", engltranslit : "svayamevAtmanAtmAnaM vettha tvaM puruShottama ."},
                {sanskrit : "भूतभावन भूतेश देवदेव जगत्पते ||१०- १५||", engltranslit : "bhUtabhAvana bhUtesha devadeva jagatpate .. 10\-15.."},
                {sanskrit : "वक्तुमर्हस्यशेषेण दिव्या ह्यात्मविभूतयः |", engltranslit : "vaktumarhasyasheSheNa divyA hyAtmavibhUtayaH ."},
                {sanskrit : "याभिर्विभूतिभिर्लोकानिमांस्त्वं व्याप्य तिष्ठसि ||१०- १६||", engltranslit : "yAbhirvibhUtibhirlokAnimA.nstva.n vyApya tiShThasi .. 10\-16.."},
                {sanskrit : "कथं विद्यामहं योगिंस्त्वां सदा परिचिन्तयन् |", engltranslit : "kathaM vidyAmahaM yogi.nstvA.n sadA parichintayan.h ."},
                {sanskrit : "केषु केषु च भावेषु चिन्त्योऽसि भगवन्मया ||१०- १७||", engltranslit : "keShu keShu cha bhAveShu chintyo.asi bhagavanmayA .. 10\-17.."},
                {sanskrit : "विस्तरेणात्मनो योगं विभूतिं च जनार्दन |", engltranslit : "vistareNAtmano yogaM vibhUti.n cha janArdana ."},
                {sanskrit : "भूयः कथय तृप्तिर्हि शृण्वतो नास्ति मेऽमृतम् ||१०- १८||", engltranslit : "bhUyaH kathaya tR^iptirhi shR^iNvato nAsti me.amR^itam.h .. 10\-18.."},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "हन्त ते कथयिष्यामि दिव्या ह्यात्मविभूतयः |", engltranslit : "hanta te kathayiShyAmi divyA hyAtmavibhUtayaH ."},
                {sanskrit : "प्राधान्यतः कुरुश्रेष्ठ नास्त्यन्तो विस्तरस्य मे ||१०- १९||", engltranslit : "prAdhAnyataH kurushreShTha nAstyanto vistarasya me .. 10\-19.."},
                {sanskrit : "अहमात्मा गुडाकेश सर्वभूताशयस्थितः |", engltranslit : "ahamAtmA guDAkesha sarvabhUtAshayasthitaH ."},
                {sanskrit : "अहमादिश्च मध्यं च भूतानामन्त एव च ||१०- २०||", engltranslit : "ahamAdishcha madhya.n cha bhUtAnAmanta eva cha .. 10\-20.."},
                {sanskrit : "आदित्यानामहं विष्णुर्ज्योतिषां रविरंशुमान् |", engltranslit : "AdityAnAmahaM viShNurjyotiShA.n ravira.nshumAn.h ."},
                {sanskrit : "मरीचिर्मरुतामस्मि नक्षत्राणामहं शशी ||१०- २१||", engltranslit : "marIchirmarutAmasmi nakShatrANAmahaM shashI .. 10\-21.."},
                {sanskrit : "वेदानां सामवेदोऽस्मि देवानामस्मि वासवः |", engltranslit : "vedAnA.n sAmavedo.asmi devAnAmasmi vAsavaH ."},
                {sanskrit : "इन्द्रियाणां मनश्चास्मि भूतानामस्मि चेतना ||१०- २२||", engltranslit : "indriyANAM manashchAsmi bhUtAnAmasmi chetanA .. 10\-22.."},
                {sanskrit : "रुद्राणां शङ्करश्चास्मि वित्तेशो यक्षरक्षसाम् |", engltranslit : "rudrANA.n shaN^karashchAsmi vittesho yakSharakShasAm.h ."},
                {sanskrit : "वसूनां पावकश्चास्मि मेरुः शिखरिणामहम् ||१०- २३||", engltranslit : "vasUnAM pAvakashchAsmi meruH shikhariNAmaham.h .. 10\-23.."},
                {sanskrit : "पुरोधसां च मुख्यं मां विद्धि पार्थ बृहस्पतिम् |", engltranslit : "purodhasA.n cha mukhyaM mA.n viddhi pArtha bR^ihaspatim.h ."},
                {sanskrit : "सेनानीनामहं स्कन्दः सरसामस्मि सागरः ||१०- २४||", engltranslit : "senAnInAmahaM skandaH sarasAmasmi sAgaraH .. 10\-24.."},
                {sanskrit : "महर्षीणां भृगुरहं गिरामस्म्येकमक्षरम् |", engltranslit : "maharShINAM bhR^igurahaM girAmasmyekamakSharam.h ."},
                {sanskrit : "यज्ञानां जपयज्ञोऽस्मि स्थावराणां हिमालयः ||१०- २५||", engltranslit : "yaGYAnA.n japayaGYo.asmi sthAvarANA.n himAlayaH .. 10\-25.."},
                {sanskrit : "अश्वत्थः सर्ववृक्षाणां देवर्षीणां च नारदः |", engltranslit : "ashvatthaH sarvavR^ikShANA.n devarShINA.n cha nAradaH ."},
                {sanskrit : "गन्धर्वाणां चित्ररथः सिद्धानां कपिलो मुनिः ||१०- २६||", engltranslit : "gandharvANA.n chitrarathaH siddhAnA.n kapilo muniH .. 10\-26.."},
                {sanskrit : "उच्चैःश्रवसमश्वानां विद्धि माममृतोद्भवम् |", engltranslit : "uchchaiHshravasamashvAnA.n viddhi mAmamR^itodbhavam.h ."},
                {sanskrit : "ऐरावतं गजेन्द्राणां नराणां च नराधिपम् ||१०- २७||", engltranslit : "airAvataM gajendrANA.n narANA.n cha narAdhipam.h .. 10\-27.."},
                {sanskrit : "आयुधानामहं वज्रं धेनूनामस्मि कामधुक् |", engltranslit : "AyudhAnAmahaM vajra.n dhenUnAmasmi kAmadhuk.h ."},
                {sanskrit : "प्रजनश्चास्मि कन्दर्पः सर्पाणामस्मि वासुकिः ||१०- २८||", engltranslit : "prajanashchAsmi kandarpaH sarpANAmasmi vAsukiH .. 10\-28.."},
                {sanskrit : "अनन्तश्चास्मि नागानां वरुणो यादसामहम् |", engltranslit : "anantashchAsmi nAgAnA.n varuNo yAdasAmaham.h ."},
                {sanskrit : "पितॄणामर्यमा चास्मि यमः संयमतामहम् ||१०- २९||", engltranslit : "pitR^INAmaryamA chAsmi yamaH sa.nyamatAmaham.h .. 10\-29.."},
                {sanskrit : "प्रह्लादश्चास्मि दैत्यानां कालः कलयतामहम् |", engltranslit : "prahlAdashchAsmi daityAnA.n kAlaH kalayatAmaham.h ."},
                {sanskrit : "मृगाणां च मृगेन्द्रोऽहं वैनतेयश्च पक्षिणाम् ||१०- ३०||", engltranslit : "mR^igANA.n cha mR^igendro.ahaM vainateyashcha pakShiNAm.h .. 10\-30.."},
                {sanskrit : "पवनः पवतामस्मि रामः शस्त्रभृतामहम् |", engltranslit : "pavanaH pavatAmasmi rAmaH shastrabhR^itAmaham.h ."},
                {sanskrit : "झषाणां मकरश्चास्मि स्रोतसामस्मि जाह्नवी ||१०- ३१||", engltranslit : "jhaShANAM makarashchAsmi srotasAmasmi jAhnavI .. 10\-31.."},
                {sanskrit : "सर्गाणामादिरन्तश्च मध्यं चैवाहमर्जुन |", engltranslit : "sargANAmAdirantashcha madhya.n chaivAhamarjuna ."},
                {sanskrit : "अध्यात्मविद्या विद्यानां वादः प्रवदतामहम् ||१०- ३२||", engltranslit : "adhyAtmavidyA vidyAnA.n vAdaH pravadatAmaham.h .. 10\-32.."},
                {sanskrit : "अक्षराणामकारोऽस्मि द्वन्द्वः सामासिकस्य च |", engltranslit : "akSharANAmakAro.asmi dvandvaH sAmAsikasya cha ."},
                {sanskrit : "अहमेवाक्षयः कालो धाताहं विश्वतोमुखः ||१०- ३३||", engltranslit : "ahamevAkShayaH kAlo dhAtAhaM vishvatomukhaH .. 10\-33.."},
                {sanskrit : "मृत्युः सर्वहरश्चाहमुद्भवश्च भविष्यताम् |", engltranslit : "mR^ityuH sarvaharashchAhamudbhavashcha bhaviShyatAm.h ."},
                {sanskrit : "कीर्तिः श्रीर्वाक्च नारीणां स्मृतिर्मेधा धृतिः क्षमा ||१०- ३४||", engltranslit : "kIrtiH shrIrvAkcha nArINA.n smR^itirmedhA dhR^itiH kShamA .. 10\-34.."},
                {sanskrit : "बृहत्साम तथा साम्नां गायत्री छन्दसामहम् |", engltranslit : "bR^ihatsAma tathA sAmnA.n gAyatrI chhandasAmaham.h ."},
                {sanskrit : "मासानां मार्गशीर्षोऽहमृतूनां कुसुमाकरः ||१०- ३५||", engltranslit : "mAsAnAM mArgashIrSho.ahamR^itUnA.n kusumAkaraH .. 10\-35.."},
                {sanskrit : "द्यूतं छलयतामस्मि तेजस्तेजस्विनामहम् |", engltranslit : "dyUta.n chhalayatAmasmi tejastejasvinAmaham.h ."},
                {sanskrit : "जयोऽस्मि व्यवसायोऽस्मि सत्त्वं सत्त्ववतामहम् ||१०- ३६||", engltranslit : "jayo.asmi vyavasAyo.asmi sattvaM sattvavatAmaham.h .. 10\-36.."},
                {sanskrit : "वृष्णीनां वासुदेवोऽस्मि पाण्डवानां धनञ्जयः |", engltranslit : "vR^iShNInA.n vAsudevo.asmi pANDavAnA.n dhana~njayaH ."},
                {sanskrit : "मुनीनामप्यहं व्यासः कवीनामुशना कविः ||१०- ३७||", engltranslit : "munInAmapyahaM vyAsaH kavInAmushanA kaviH .. 10\-37.."},
                {sanskrit : "दण्डो दमयतामस्मि नीतिरस्मि जिगीषताम् |", engltranslit : "daNDo damayatAmasmi nItirasmi jigIShatAm.h ."},
                {sanskrit : "मौनं चैवास्मि गुह्यानां ज्ञानं ज्ञानवतामहम् ||१०- ३८||", engltranslit : "mauna.n chaivAsmi guhyAnA.n GYAnaM GYAnavatAmaham.h .. 10\-38.."},
                {sanskrit : "यच्चापि सर्वभूतानां बीजं तदहमर्जुन |", engltranslit : "yachchApi sarvabhUtAnAM bIjaM tadahamarjuna ."},
                {sanskrit : "न तदस्ति विना यत्स्यान्मया भूतं चराचरम् ||१०- ३९||", engltranslit : "na tadasti vinA yatsyAnmayA bhUta.n charAcharam.h .. 10\-39.."},
                {sanskrit : "नान्तोऽस्ति मम दिव्यानां विभूतीनां परन्तप |", engltranslit : "nAnto.asti mama divyAnA.n vibhUtInAM parantapa ."},
                {sanskrit : "एष तूद्देशतः प्रोक्तो विभूतेर्विस्तरो मया ||१०- ४०||", engltranslit : "eSha tUddeshataH prokto vibhUtervistaro mayA .. 10\-40.."},
                {sanskrit : "यद्यद्विभूतिमत्सत्त्वं श्रीमदूर्जितमेव वा |", engltranslit : "yadyadvibhUtimatsattvaM shrImadUrjitameva vA ."},
                {sanskrit : "तत्तदेवावगच्छ त्वं मम तेजोंऽशसम्भवम् ||१०- ४१||", engltranslit : "tattadevAvagachchha tvaM mama tejo.n.ashasambhavam.h .. 10\-41.."},
                {sanskrit : "अथवा बहुनैतेन किं ज्ञातेन तवार्जुन |", engltranslit : "athavA bahunaitena kiM GYAtena tavArjuna ."},
                {sanskrit : "विष्टभ्याहमिदं कृत्स्नमेकांशेन स्थितो जगत् ||१०- ४२||", engltranslit : "viShTabhyAhamidaM kR^itsnamekA.nshena sthito jagat.h .. 10\-42.."},
                {sanskrit : "ॐ तत्सदिति श्रीमद्भगवद्गीतासूपनिषत्सु", engltranslit : "AUM tatsaditi shrImadbhagavadgItAsUpaniShatsu"},
                {sanskrit : "ब्रह्मविद्यायां योगशास्त्रे श्रीकृष्णार्जुनसंवादे", engltranslit : "brahmavidyAyA.n yogashAstre shrIkR^iShNArjunasa.nvAde"},
                {sanskrit : "विभूतियोगो नाम दशमोऽध्यायः ||१०||", engltranslit : "vibhUtiyogo nAma dashamo.adhyAyaH .. 10.."},
                {sanskrit : "अथैकादशोऽध्यायः | विश्वरूपदर्शनयोगः", engltranslit : "athaikAdasho.adhyAyaH . vishvarUpadarshanayogaH"},
                {sanskrit : "अर्जुन उवाच |", engltranslit : "arjuna uvAcha ."},
                {sanskrit : "मदनुग्रहाय परमं गुह्यमध्यात्मसंज्ञितम् |", engltranslit : "madanugrahAya paramaM guhyamadhyAtmasa.nGYitam.h ."},
                {sanskrit : "यत्त्वयोक्तं वचस्तेन मोहोऽयं विगतो मम ||११- १||", engltranslit : "yattvayokta.n vachastena moho.ayaM vigato mama .. 11\-1.."},
                {sanskrit : "भवाप्ययौ हि भूतानां श्रुतौ विस्तरशो मया |", engltranslit : "bhavApyayau hi bhUtAnA.n shrutau vistarasho mayA ."},
                {sanskrit : "त्वत्तः कमलपत्राक्ष माहात्म्यमपि चाव्ययम् ||११- २||", engltranslit : "tvattaH kamalapatrAkSha mAhAtmyamapi chAvyayam.h .. 11\-2.."},
                {sanskrit : "एवमेतद्यथात्थ त्वमात्मानं परमेश्वर |", engltranslit : "evametadyathAttha tvamAtmAnaM parameshvara ."},
                {sanskrit : "द्रष्टुमिच्छामि ते रूपमैश्वरं पुरुषोत्तम ||११- ३||", engltranslit : "draShTumichchhAmi te rUpamaishvaraM puruShottama .. 11\-3.."},
                {sanskrit : "मन्यसे यदि तच्छक्यं मया द्रष्टुमिति प्रभो |", engltranslit : "manyase yadi tachchhakyaM mayA draShTumiti prabho ."},
                {sanskrit : "योगेश्वर ततो मे त्वं दर्शयात्मानमव्ययम् ||११- ४||", engltranslit : "yogeshvara tato me tva.n darshayAtmAnamavyayam.h .. 11\-4.."},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "पश्य मे पार्थ रूपाणि शतशोऽथ सहस्रशः |", engltranslit : "pashya me pArtha rUpANi shatasho.atha sahasrashaH ."},
                {sanskrit : "नानाविधानि दिव्यानि नानावर्णाकृतीनि च ||११- ५||", engltranslit : "nAnAvidhAni divyAni nAnAvarNAkR^itIni cha .. 11\-5.."},
                {sanskrit : "पश्यादित्यान्वसून्रुद्रानश्विनौ मरुतस्तथा |", engltranslit : "pashyAdityAnvasUn.hrudrAnashvinau marutastathA ."},
                {sanskrit : "बहून्यदृष्टपूर्वाणि पश्याश्चर्याणि भारत ||११- ६||", engltranslit : "bahUnyadR^iShTapUrvANi pashyAshcharyANi bhArata .. 11\-6.."},
                {sanskrit : "इहैकस्थं जगत्कृत्स्नं पश्याद्य सचराचरम् |", engltranslit : "ihaikastha.n jagatkR^itsnaM pashyAdya sacharAcharam.h ."},
                {sanskrit : "मम देहे गुडाकेश यच्चान्यद् द्रष्टुमिच्छसि ||११- ७||", engltranslit : "mama dehe guDAkesha yachchAnyad.h draShTumichchhasi .. 11\-7.."},
                {sanskrit : "न तु मां शक्यसे द्रष्टुमनेनैव स्वचक्षुषा |", engltranslit : "na tu mA.n shakyase draShTumanenaiva svachakShuShA ."},
                {sanskrit : "दिव्यं ददामि ते चक्षुः पश्य मे योगमैश्वरम् ||११- ८||", engltranslit : "divya.n dadAmi te chakShuH pashya me yogamaishvaram.h .. 11\-8.."},
                {sanskrit : "सञ्जय उवाच |", engltranslit : "sa~njaya uvAcha ."},
                {sanskrit : "एवमुक्त्वा ततो राजन्महायोगेश्वरो हरिः |", engltranslit : "evamuktvA tato rAjanmahAyogeshvaro hariH ."},
                {sanskrit : "दर्शयामास पार्थाय परमं रूपमैश्वरम् ||११- ९||", engltranslit : "darshayAmAsa pArthAya paramaM rUpamaishvaram.h .. 11\-9.."},
                {sanskrit : "अनेकवक्त्रनयनमनेकाद्भुतदर्शनम् |", engltranslit : "anekavaktranayanamanekAd.hbhutadarshanam.h ."},
                {sanskrit : "अनेकदिव्याभरणं दिव्यानेकोद्यतायुधम् ||११- १०||", engltranslit : "anekadivyAbharaNaM divyAnekodyatAyudham.h .. 11\-10.."},
                {sanskrit : "दिव्यमाल्याम्बरधरं दिव्यगन्धानुलेपनम् |", engltranslit : "divyamAlyAmbaradharaM divyagandhAnulepanam.h ."},
                {sanskrit : "सर्वाश्चर्यमयं देवमनन्तं विश्वतोमुखम् ||११- ११||", engltranslit : "sarvAshcharyamayaM devamanantaM vishvatomukham.h .. 11\-11.."},
                {sanskrit : "दिवि सूर्यसहस्रस्य भवेद्युगपदुत्थिता |", engltranslit : "divi sUryasahasrasya bhavedyugapadutthitA ."},
                {sanskrit : "यदि भाः सदृशी सा स्याद्भासस्तस्य महात्मनः ||११- १२||", engltranslit : "yadi bhAH sadR^ishI sA syAdbhAsastasya mahAtmanaH .. 11\-12.."},
                {sanskrit : "तत्रैकस्थं जगत्कृत्स्नं प्रविभक्तमनेकधा |", engltranslit : "tatraikastha.n jagatkR^itsnaM pravibhaktamanekadhA ."},
                {sanskrit : "अपश्यद्देवदेवस्य शरीरे पाण्डवस्तदा ||११- १३||", engltranslit : "apashyaddevadevasya sharIre pANDavastadA .. 11\-13.."},
                {sanskrit : "ततः स विस्मयाविष्टो हृष्टरोमा धनञ्जयः |", engltranslit : "tataH sa vismayAviShTo hR^iShTaromA dhana~njayaH ."},
                {sanskrit : "प्रणम्य शिरसा देवं कृताञ्जलिरभाषत ||११- १४||", engltranslit : "praNamya shirasA devaM kR^itAJNjalirabhAShata .. 11\-14.."},
                {sanskrit : "अर्जुन उवाच |", engltranslit : "arjuna uvAcha ."},
                {sanskrit : "पश्यामि देवांस्तव देव देहे", engltranslit : "pashyAmi devA.nstava deva dehe"},
                {sanskrit : "सर्वांस्तथा भूतविशेषसङ्घान् |", engltranslit : "sarvA.nstathA bhUtavisheShasaN^ghAn.h ."},
                {sanskrit : "ब्रह्माणमीशं कमलासनस्थ- ", engltranslit : "brahmANamIshaM kamalAsanastha\-"},
                {sanskrit : "मृषींश्च सर्वानुरगांश्च दिव्यान् ||११- १५||", engltranslit : "mR^iShI.nshcha sarvAnuragA.nshcha divyAn.h .. 11\-15.."},
                {sanskrit : "अनेकबाहूदरवक्त्रनेत्रं", engltranslit : "anekabAhUdaravaktranetra.n"},
                {sanskrit : "पश्यामि त्वां सर्वतोऽनन्तरूपम् |", engltranslit : "pashyAmi tvA.n sarvato.anantarUpam.h ."},
                {sanskrit : "नान्तं न मध्यं न पुनस्तवादिं", engltranslit : "nAntaM na madhyaM na punastavAdi.n"},
                {sanskrit : "पश्यामि विश्वेश्वरविश्वरूप ||११- १६||", engltranslit : "pashyAmi vishveshvaravishvarUpa .. 11\-16.."},
                {sanskrit : "किरीटिनं गदिनं चक्रिणं च", engltranslit : "kirITinaM gadina.n chakriNa.n cha"},
                {sanskrit : "तेजोराशिं सर्वतो दीप्तिमन्तम् |", engltranslit : "tejorAshi.n sarvato dIptimantam.h ."},
                {sanskrit : "पश्यामि त्वां दुर्निरीक्ष्यं समन्ताद्", engltranslit : "pashyAmi tvA.n durnirIkShya.n samantAd.h"},
                {sanskrit : "दीप्तानलार्कद्युतिमप्रमेयम् ||११- १७||", engltranslit : "dIptAnalArkadyutimaprameyam.h .. 11\-17.."},
                {sanskrit : "त्वमक्षरं परमं वेदितव्यं", engltranslit : "tvamakSharaM paramaM veditavya.n"},
                {sanskrit : "त्वमस्य विश्वस्य परं निधानम् |", engltranslit : "tvamasya vishvasya paraM nidhAnam.h ."},
                {sanskrit : "त्वमव्ययः शाश्वतधर्मगोप्ता", engltranslit : "tvamavyayaH shAshvatadharmagoptA"},
                {sanskrit : "सनातनस्त्वं पुरुषो मतो मे ||११- १८||", engltranslit : "sanAtanastvaM puruSho mato me .. 11\-18.."},
                {sanskrit : "अनादिमध्यान्तमनन्तवीर्य- ", engltranslit : "anAdimadhyAntamanantavIrya\-"},
                {sanskrit : "मनन्तबाहुं शशिसूर्यनेत्रम् |", engltranslit : "manantabAhu.n shashisUryanetram.h ."},
                {sanskrit : "पश्यामि त्वां दीप्तहुताशवक्त्रं", engltranslit : "pashyAmi tvA.n dIptahutAshavaktra.n"},
                {sanskrit : "स्वतेजसा विश्वमिदं तपन्तम् ||११- १९||", engltranslit : "svatejasA vishvamidaM tapantam.h .. 11\-19.."},
                {sanskrit : "द्यावापृथिव्योरिदमन्तरं हि", engltranslit : "dyAvApR^ithivyoridamantara.n hi"},
                {sanskrit : "व्याप्तं त्वयैकेन दिशश्च सर्वाः |", engltranslit : "vyApta.n tvayaikena dishashcha sarvAH ."},
                {sanskrit : "दृष्ट्वाद्भुतं रूपमुग्रं तवेदं", engltranslit : "dR^iShTvAd.hbhutaM rUpamugra.n tavedaM"},
                {sanskrit : "लोकत्रयं प्रव्यथितं महात्मन् ||११- २०||", engltranslit : "lokatrayaM pravyathitaM mahAtman.h .. 11\-20.."},
                {sanskrit : "अमी हि त्वां सुरसङ्घा विशन्ति", engltranslit : "amI hi tvA.n surasaN^ghA vishanti"},
                {sanskrit : "केचिद्भीताः प्राञ्जलयो गृणन्ति |", engltranslit : "kechidbhItAH prAJNjalayo gR^iNanti ."},
                {sanskrit : "स्वस्तीत्युक्त्वा महर्षिसिद्धसङ्घाः", engltranslit : "svastItyuktvA maharShisiddhasaN^ghAH"},
                {sanskrit : "स्तुवन्ति त्वां स्तुतिभिः पुष्कलाभिः ||११- २१||", engltranslit : "stuvanti tvA.n stutibhiH puShkalAbhiH .. 11\-21.."},
                {sanskrit : "रुद्रादित्या वसवो ये च साध्या", engltranslit : "rudrAdityA vasavo ye cha sAdhyA"},
                {sanskrit : "विश्वेऽश्विनौ मरुतश्चोष्मपाश्च |", engltranslit : "vishve.ashvinau marutashchoShmapAshcha ."},
                {sanskrit : "गन्धर्वयक्षासुरसिद्धसङ्घा", engltranslit : "gandharvayakShAsurasiddhasaN^ghA"},
                {sanskrit : "वीक्षन्ते त्वां विस्मिताश्चैव सर्वे ||११- २२||", engltranslit : "vIkShante tvA.n vismitAshchaiva sarve .. 11\-22.."},
                {sanskrit : "रूपं महत्ते बहुवक्त्रनेत्रं", engltranslit : "rUpaM mahatte bahuvaktranetra.n"},
                {sanskrit : "महाबाहो बहुबाहूरुपादम् |", engltranslit : "mahAbAho bahubAhUrupAdam.h ."},
                {sanskrit : "बहूदरं बहुदंष्ट्राकरालं", engltranslit : "bahUdaraM bahuda.nShTrAkarAlaM"},
                {sanskrit : "दृष्ट्वा लोकाः प्रव्यथितास्तथाहम् ||११- २३||", engltranslit : "dR^iShTvA lokAH pravyathitAstathAham.h .. 11\-23.."},
                {sanskrit : "नभःस्पृशं दीप्तमनेकवर्णं", engltranslit : "nabhaHspR^ishaM dIptamanekavarNa.n"},
                {sanskrit : "व्यात्ताननं दीप्तविशालनेत्रम् |", engltranslit : "vyAttAnanaM dIptavishAlanetram.h ."},
                {sanskrit : "दृष्ट्वा हि त्वां प्रव्यथितान्तरात्मा", engltranslit : "dR^iShTvA hi tvAM pravyathitAntarAtmA"},
                {sanskrit : "धृतिं न विन्दामि शमं च विष्णो ||११- २४||", engltranslit : "dhR^iti.n na vindAmi shama.n cha viShNo .. 11\-24.."},
                {sanskrit : "दंष्ट्राकरालानि च ते मुखानि", engltranslit : "da.nShTrAkarAlAni cha te mukhAni"},
                {sanskrit : "दृष्ट्वैव कालानलसन्निभानि |", engltranslit : "dR^iShTvaiva kAlAnalasannibhAni ."},
                {sanskrit : "दिशो न जाने न लभे च शर्म", engltranslit : "disho na jAne na labhe cha sharma"},
                {sanskrit : "प्रसीद देवेश जगन्निवास ||११- २५||", engltranslit : "prasIda devesha jagannivAsa .. 11\-25.."},
                {sanskrit : "अमी च त्वां धृतराष्ट्रस्य पुत्राः", engltranslit : "amI cha tvA.n dhR^itarAShTrasya putrAH"},
                {sanskrit : "सर्वे सहैवावनिपालसङ्घैः |", engltranslit : "sarve sahaivAvanipAlasaN^ghaiH ."},
                {sanskrit : "भीष्मो द्रोणः सूतपुत्रस्तथासौ", engltranslit : "bhIShmo droNaH sUtaputrastathAsau"},
                {sanskrit : "सहास्मदीयैरपि योधमुख्यैः ||११- २६||", engltranslit : "sahAsmadIyairapi yodhamukhyaiH .. 11\-26.."},
                {sanskrit : "वक्त्राणि ते त्वरमाणा विशन्ति", engltranslit : "vaktrANi te tvaramANA vishanti"},
                {sanskrit : "दंष्ट्राकरालानि भयानकानि |", engltranslit : "da.nShTrAkarAlAni bhayAnakAni ."},
                {sanskrit : "केचिद्विलग्ना दशनान्तरेषु", engltranslit : "kechidvilagnA dashanAntareShu"},
                {sanskrit : "सन्दृश्यन्ते चूर्णितैरुत्तमाङ्गैः ||११- २७||", engltranslit : "sandR^ishyante chUrNitairuttamAN^gaiH .. 11\-27.."},
                {sanskrit : "यथा नदीनां बहवोऽम्बुवेगाः", engltranslit : "yathA nadInAM bahavo.ambuvegAH"},
                {sanskrit : "समुद्रमेवाभिमुखा द्रवन्ति |", engltranslit : "samudramevAbhimukhA dravanti ."},
                {sanskrit : "तथा तवामी नरलोकवीरा", engltranslit : "tathA tavAmI naralokavIrA"},
                {sanskrit : "विशन्ति वक्त्राण्यभिविज्वलन्ति ||११- २८||", engltranslit : "vishanti vaktrANyabhivijvalanti .. 11\-28.."},
                {sanskrit : "यथा प्रदीप्तं ज्वलनं पतङ्गा", engltranslit : "yathA pradIpta.n jvalanaM pataN^gA"},
                {sanskrit : "विशन्ति नाशाय समृद्धवेगाः |", engltranslit : "vishanti nAshAya samR^iddhavegAH ."},
                {sanskrit : "तथैव नाशाय विशन्ति लोकास्- ", engltranslit : "tathaiva nAshAya vishanti lokAs\-"},
                {sanskrit : "तवापि वक्त्राणि समृद्धवेगाः ||११- २९||", engltranslit : "tavApi vaktrANi samR^iddhavegAH .. 11\-29.."},
                {sanskrit : "लेलिह्यसे ग्रसमानः समन्ताल्- ", engltranslit : "lelihyase grasamAnaH samantAl.h\-"},
                {sanskrit : "लोकान्समग्रान्वदनैर्ज्वलद्भिः |", engltranslit : "lokAnsamagrAnvadanairjvaladbhiH ."},
                {sanskrit : "तेजोभिरापूर्य जगत्समग्रं", engltranslit : "tejobhirApUrya jagatsamagra.n"},
                {sanskrit : "भासस्तवोग्राः प्रतपन्ति विष्णो ||११- ३०||", engltranslit : "bhAsastavogrAH pratapanti viShNo .. 11\-30.."},
                {sanskrit : "आख्याहि मे को भवानुग्ररूपो", engltranslit : "AkhyAhi me ko bhavAnugrarUpo"},
                {sanskrit : "नमोऽस्तु ते देववर प्रसीद |", engltranslit : "namo.astu te devavara prasIda ."},
                {sanskrit : "विज्ञातुमिच्छामि भवन्तमाद्यं", engltranslit : "viGYAtumichchhAmi bhavantamAdya.n"},
                {sanskrit : "न हि प्रजानामि तव प्रवृत्तिम् ||११- ३१||", engltranslit : "na hi prajAnAmi tava pravR^ittim.h .. 11\-31.."},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "कालोऽस्मि लोकक्षयकृत्प्रवृद्धो", engltranslit : "kAlo.asmi lokakShayakR^itpravR^iddho"},
                {sanskrit : "लोकान्समाहर्तुमिह प्रवृत्तः |", engltranslit : "lokAnsamAhartumiha pravR^ittaH ."},
                {sanskrit : "ऋतेऽपि त्वां न भविष्यन्ति सर्वे", engltranslit : "R^ite.api tvA.n na bhaviShyanti sarve"},
                {sanskrit : "येऽवस्थिताः प्रत्यनीकेषु योधाः ||११- ३२||", engltranslit : "ye.avasthitAH pratyanIkeShu yodhAH .. 11\-32.."},
                {sanskrit : "तस्मात्त्वमुत्तिष्ठ यशो लभस्व", engltranslit : "tasmAttvamuttiShTha yasho labhasva"},
                {sanskrit : "जित्वा शत्रून् भुङ्क्ष्व राज्यं समृद्धम् |", engltranslit : "jitvA shatrUn.h bhuN^.hkShva rAjya.n samR^iddham.h ."},
                {sanskrit : "मयैवैते निहताः पूर्वमेव", engltranslit : "mayaivaite nihatAH pUrvameva"},
                {sanskrit : "निमित्तमात्रं भव सव्यसाचिन् ||११- ३३||", engltranslit : "nimittamAtraM bhava savyasAchin.h .. 11\-33.."},
                {sanskrit : "द्रोणं च भीष्मं च जयद्रथं च", engltranslit : "droNa.n cha bhIShma.n cha jayadratha.n cha"},
                {sanskrit : "कर्णं तथान्यानपि योधवीरान् |", engltranslit : "karNa.n tathAnyAnapi yodhavIrAn.h ."},
                {sanskrit : "मया हतांस्त्वं जहि मा व्यथिष्ठा", engltranslit : "mayA hatA.nstva.n jahi mA vyathiShThA"},
                {sanskrit : "युध्यस्व जेतासि रणे सपत्नान् ||११- ३४||", engltranslit : "yudhyasva jetAsi raNe sapatnAn.h .. 11\-34.."},
                {sanskrit : "सञ्जय उवाच |", engltranslit : "sa~njaya uvAcha ."},
                {sanskrit : "एतच्छ्रुत्वा वचनं केशवस्य", engltranslit : "etachchhrutvA vachanaM keshavasya"},
                {sanskrit : "कृताञ्जलिर्वेपमानः किरीटी |", engltranslit : "kR^itAJNjalirvepamAnaH kirITI ."},
                {sanskrit : "नमस्कृत्वा भूय एवाह कृष्णं", engltranslit : "namaskR^itvA bhUya evAha kR^iShNaM"},
                {sanskrit : "सगद्गदं भीतभीतः प्रणम्य ||११- ३५||", engltranslit : "sagadgadaM bhItabhItaH praNamya .. 11\-35.."},
                {sanskrit : "अर्जुन उवाच |", engltranslit : "arjuna uvAcha ."},
                {sanskrit : "स्थाने हृषीकेश तव प्रकीर्त्या", engltranslit : "sthAne hR^iShIkesha tava prakIrtyA"},
                {sanskrit : "जगत्प्रहृष्यत्यनुरज्यते च |", engltranslit : "jagatprahR^iShyatyanurajyate cha ."},
                {sanskrit : "रक्षांसि भीतानि दिशो द्रवन्ति", engltranslit : "rakShA.nsi bhItAni disho dravanti"},
                {sanskrit : "सर्वे नमस्यन्ति च सिद्धसङ्घाः ||११- ३६||", engltranslit : "sarve namasyanti cha siddhasaN^ghAH .. 11\-36.."},
                {sanskrit : "कस्माच्च ते न नमेरन्महात्मन्", engltranslit : "kasmAchcha te na nameranmahAtman.h"},
                {sanskrit : "गरीयसे ब्रह्मणोऽप्यादिकर्त्रे |", engltranslit : "garIyase brahmaNo.apyAdikartre ."},
                {sanskrit : "अनन्त देवेश जगन्निवास", engltranslit : "ananta devesha jagannivAsa"},
                {sanskrit : "त्वमक्षरं सदसत्तत्परं यत् ||११- ३७||", engltranslit : "tvamakSharaM sadasattatparaM yat.h .. 11\-37.."},
                {sanskrit : "त्वमादिदेवः पुरुषः पुराणस्- ", engltranslit : "tvamAdidevaH puruShaH purANas\-"},
                {sanskrit : "त्वमस्य विश्वस्य परं निधानम् |", engltranslit : "tvamasya vishvasya paraM nidhAnam.h ."},
                {sanskrit : "वेत्तासि वेद्यं च परं च धाम", engltranslit : "vettAsi vedya.n cha para.n cha dhAma"},
                {sanskrit : "त्वया ततं विश्वमनन्तरूप ||११- ३८||", engltranslit : "tvayA tataM vishvamanantarUpa .. 11\-38.."},
                {sanskrit : "वायुर्यमोऽग्निर्वरुणः शशाङ्कः", engltranslit : "vAyuryamo.agnirvaruNaH shashAN^kaH"},
                {sanskrit : "प्रजापतिस्त्वं प्रपितामहश्च |", engltranslit : "prajApatistvaM prapitAmahashcha ."},
                {sanskrit : "नमो नमस्तेऽस्तु सहस्रकृत्वः", engltranslit : "namo namaste.astu sahasrakR^itvaH"},
                {sanskrit : "पुनश्च भूयोऽपि नमो नमस्ते ||११- ३९||", engltranslit : "punashcha bhUyo.api namo namaste .. 11\-39.."},
                {sanskrit : "नमः पुरस्तादथ पृष्ठतस्ते", engltranslit : "namaH purastAdatha pR^iShThataste"},
                {sanskrit : "नमोऽस्तु ते सर्वत एव सर्व |", engltranslit : "namo.astu te sarvata eva sarva ."},
                {sanskrit : "अनन्तवीर्यामितविक्रमस्त्वं", engltranslit : "anantavIryAmitavikramastva.n"},
                {sanskrit : "सर्वं समाप्नोषि ततोऽसि सर्वः ||११- ४०||", engltranslit : "sarva.n samApnoShi tato.asi sarvaH .. 11\-40.."},
                {sanskrit : "सखेति मत्वा प्रसभं यदुक्तं", engltranslit : "sakheti matvA prasabhaM yaduktaM"},
                {sanskrit : "हे कृष्ण हे यादव हे सखेति |", engltranslit : "he kR^iShNa he yAdava he sakheti ."},
                {sanskrit : "अजानता महिमानं तवेदं", engltranslit : "ajAnatA mahimAnaM tavedaM"},
                {sanskrit : "मया प्रमादात्प्रणयेन वापि ||११- ४१||", engltranslit : "mayA pramAdAtpraNayena vApi .. 11\-41.."},
                {sanskrit : "यच्चावहासार्थमसत्कृतोऽसि", engltranslit : "yachchAvahAsArthamasatkR^ito.asi"},
                {sanskrit : "विहारशय्यासनभोजनेषु |", engltranslit : "vihArashayyAsanabhojaneShu ."},
                {sanskrit : "एकोऽथवाप्यच्युत तत्समक्षं", engltranslit : "eko.athavApyachyuta tatsamakSha.n"},
                {sanskrit : "तत्क्षामये त्वामहमप्रमेयम् ||११- ४२||", engltranslit : "tatkShAmaye tvAmahamaprameyam.h .. 11\-42.."},
                {sanskrit : "पितासि लोकस्य चराचरस्य", engltranslit : "pitAsi lokasya charAcharasya"},
                {sanskrit : "त्वमस्य पूज्यश्च गुरुर्गरीयान् |", engltranslit : "tvamasya pUjyashcha gururgarIyAn.h ."},
                {sanskrit : "न त्वत्समोऽस्त्यभ्यधिकः कुतोऽन्यो", engltranslit : "na tvatsamo.astyabhyadhikaH kuto.anyo"},
                {sanskrit : "लोकत्रयेऽप्यप्रतिमप्रभाव ||११- ४३||", engltranslit : "lokatraye.apyapratimaprabhAva .. 11\-43.."},
                {sanskrit : "तस्मात्प्रणम्य प्रणिधाय कायं", engltranslit : "tasmAtpraNamya praNidhAya kAyaM"},
                {sanskrit : "प्रसादये त्वामहमीशमीड्यम् |", engltranslit : "prasAdaye tvAmahamIshamIDyam.h ."},
                {sanskrit : "पितेव पुत्रस्य सखेव सख्युः", engltranslit : "piteva putrasya sakheva sakhyuH"},
                {sanskrit : "प्रियः प्रियायार्हसि देव सोढुम् ||११- ४४||", engltranslit : "priyaH priyAyArhasi deva soDhum.h .. 11\-44.."},
                {sanskrit : "अदृष्टपूर्वं हृषितोऽस्मि दृष्ट्वा", engltranslit : "adR^iShTapUrva.n hR^iShito.asmi dR^iShTvA"},
                {sanskrit : "भयेन च प्रव्यथितं मनो मे |", engltranslit : "bhayena cha pravyathitaM mano me ."},
                {sanskrit : "तदेव मे दर्शय देव रूपं", engltranslit : "tadeva me darshaya deva rUpaM"},
                {sanskrit : "प्रसीद देवेश जगन्निवास ||११- ४५||", engltranslit : "prasIda devesha jagannivAsa .. 11\-45.."},
                {sanskrit : "किरीटिनं गदिनं चक्रहस्तं", engltranslit : "kirITinaM gadina.n chakrahastaM"},
                {sanskrit : "इच्छामि त्वां द्रष्टुमहं तथैव |", engltranslit : "ichchhAmi tvA.n draShTumahaM tathaiva ."},
                {sanskrit : "तेनैव रूपेण चतुर्भुजेन", engltranslit : "tenaiva rUpeNa chaturbhujena"},
                {sanskrit : "सहस्रबाहो भव विश्वमूर्ते ||११- ४६||", engltranslit : "sahasrabAho bhava vishvamUrte .. 11\-46.."},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "मया प्रसन्नेन तवार्जुनेदं", engltranslit : "mayA prasannena tavArjunedaM"},
                {sanskrit : "रूपं परं दर्शितमात्मयोगात् |", engltranslit : "rUpaM paraM darshitamAtmayogAt.h ."},
                {sanskrit : "तेजोमयं विश्वमनन्तमाद्यं", engltranslit : "tejomayaM vishvamanantamAdya.n"},
                {sanskrit : "यन्मे त्वदन्येन न दृष्टपूर्वम् ||११- ४७||", engltranslit : "yanme tvadanyena na dR^iShTapUrvam.h .. 11\-47.."},
                {sanskrit : "न वेदयज्ञाध्ययनैर्न दानैर्- ", engltranslit : "na vedayaGYAdhyayanairna dAnair\-"},
                {sanskrit : "न च क्रियाभिर्न तपोभिरुग्रैः |", engltranslit : "na cha kriyAbhirna tapobhirugraiH ."},
                {sanskrit : "एवंरूपः शक्य अहं नृलोके", engltranslit : "eva.nrUpaH shakya ahaM nR^iloke"},
                {sanskrit : "द्रष्टुं त्वदन्येन कुरुप्रवीर ||११- ४८||", engltranslit : "draShTuM tvadanyena kurupravIra .. 11\-48.."},
                {sanskrit : "मा ते व्यथा मा च विमूढभावो", engltranslit : "mA te vyathA mA cha vimUDhabhAvo"},
                {sanskrit : "दृष्ट्वा रूपं घोरमीदृङ्ममेदम् |", engltranslit : "dR^iShTvA rUpaM ghoramIdR^iN^.hmamedam.h ."},
                {sanskrit : "व्यपेतभीः प्रीतमनाः पुनस्त्वं", engltranslit : "vyapetabhIH prItamanAH punastva.n"},
                {sanskrit : "तदेव मे रूपमिदं प्रपश्य ||११- ४९||", engltranslit : "tadeva me rUpamidaM prapashya .. 11\-49.."},
                {sanskrit : "सञ्जय उवाच |", engltranslit : "sa~njaya uvAcha ."},
                {sanskrit : "इत्यर्जुनं वासुदेवस्तथोक्त्वा", engltranslit : "ityarjunaM vAsudevastathoktvA"},
                {sanskrit : "स्वकं रूपं दर्शयामास भूयः |", engltranslit : "svakaM rUpaM darshayAmAsa bhUyaH ."},
                {sanskrit : "आश्वासयामास च भीतमेनं", engltranslit : "AshvAsayAmAsa cha bhItamenaM"},
                {sanskrit : "भूत्वा पुनः सौम्यवपुर्महात्मा ||११- ५०||", engltranslit : "bhUtvA punaH saumyavapurmahAtmA .. 11\-50.."},
                {sanskrit : "अर्जुन उवाच |", engltranslit : "arjuna uvAcha ."},
                {sanskrit : "दृष्ट्वेदं मानुषं रूपं तव सौम्यं जनार्दन |", engltranslit : "dR^iShTvedaM mAnuShaM rUpaM tava saumya.n janArdana ."},
                {sanskrit : "इदानीमस्मि संवृत्तः सचेताः प्रकृतिं गतः ||११- ५१||", engltranslit : "idAnImasmi sa.nvR^ittaH sachetAH prakR^iti.n gataH .. 11\-51.."},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "सुदुर्दर्शमिदं रूपं दृष्टवानसि यन्मम |", engltranslit : "sudurdarshamidaM rUpaM dR^iShTavAnasi yanmama ."},
                {sanskrit : "देवा अप्यस्य रूपस्य नित्यं दर्शनकाङ्क्षिणः ||११- ५२||", engltranslit : "devA apyasya rUpasya nitya.n darshanakAN^kShiNaH .. 11\-52.."},
                {sanskrit : "नाहं वेदैर्न तपसा न दानेन न चेज्यया |", engltranslit : "nAhaM vedairna tapasA na dAnena na chejyayA ."},
                {sanskrit : "शक्य एवंविधो द्रष्टुं दृष्टवानसि मां यथा ||११- ५३||", engltranslit : "shakya eva.nvidho draShTuM dR^iShTavAnasi mA.n yathA .. 11\-53.."},
                {sanskrit : "भक्त्या त्वनन्यया शक्य अहमेवंविधोऽर्जुन |", engltranslit : "bhaktyA tvananyayA shakya ahameva.nvidho.arjuna ."},
                {sanskrit : "ज्ञातुं द्रष्टुं च तत्त्वेन प्रवेष्टुं च परन्तप ||११- ५४||", engltranslit : "GYAtuM draShTu.n cha tattvena praveShTu.n cha parantapa .. 11\-54.."},
                {sanskrit : "मत्कर्मकृन्मत्परमो मद्भक्तः सङ्गवर्जितः |", engltranslit : "matkarmakR^inmatparamo madbhaktaH saN^gavarjitaH ."},
                {sanskrit : "निर्वैरः सर्वभूतेषु यः स मामेति पाण्डव ||११- ५५||", engltranslit : "nirvairaH sarvabhUteShu yaH sa mAmeti pANDava .. 11\-55.."},
                {sanskrit : "ॐ तत्सदिति श्रीमद्भगवद्गीतासूपनिषत्सु", engltranslit : "AUM tatsaditi shrImadbhagavadgItAsUpaniShatsu"},
                {sanskrit : "ब्रह्मविद्यायां योगशास्त्रे श्रीकृष्णार्जुनसंवादे", engltranslit : "brahmavidyAyA.n yogashAstre shrIkR^iShNArjunasa.nvAde"},
                {sanskrit : "विश्वरूपदर्शनयोगो नामैकादशोऽध्यायः ||११||", engltranslit : "vishvarUpadarshanayogo nAmaikAdasho.adhyAyaH .. 11.."},
                {sanskrit : "अथ द्वादशोऽध्यायः | भक्तियोगः", engltranslit : "atha dvAdasho.adhyAyaH . bhaktiyogaH"},
                {sanskrit : "अर्जुन उवाच |", engltranslit : "arjuna uvAcha ."},
                {sanskrit : "एवं सततयुक्ता ये भक्तास्त्वां पर्युपासते |", engltranslit : "evaM satatayuktA ye bhaktAstvAM paryupAsate ."},
                {sanskrit : "ये चाप्यक्षरमव्यक्तं तेषां के योगवित्तमाः ||१२- १||", engltranslit : "ye chApyakSharamavyaktaM teShA.n ke yogavittamAH .. 12\-1.."},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "मय्यावेश्य मनो ये मां नित्ययुक्ता उपासते |", engltranslit : "mayyAveshya mano ye mAM nityayuktA upAsate ."},
                {sanskrit : "श्रद्धया परयोपेताः ते मे युक्ततमा मताः ||१२- २||", engltranslit : "shraddhayA parayopetAH te me yuktatamA matAH .. 12\-2.."},
                {sanskrit : "ये त्वक्षरमनिर्देश्यमव्यक्तं पर्युपासते |", engltranslit : "ye tvakSharamanirdeshyamavyaktaM paryupAsate ."},
                {sanskrit : "सर्वत्रगमचिन्त्यञ्च कूटस्थमचलन्ध्रुवम् ||१२- ३||", engltranslit : "sarvatragamachintya~ncha kUTasthamachalandhruvam.h .. 12\-3.."},
                {sanskrit : "सन्नियम्येन्द्रियग्रामं सर्वत्र समबुद्धयः |", engltranslit : "sanniyamyendriyagrAmaM sarvatra samabuddhayaH ."},
                {sanskrit : "ते प्राप्नुवन्ति मामेव सर्वभूतहिते रताः ||१२- ४||", engltranslit : "te prApnuvanti mAmeva sarvabhUtahite ratAH .. 12\-4.."},
                {sanskrit : "क्लेशोऽधिकतरस्तेषामव्यक्तासक्तचेतसाम् ||", engltranslit : "klesho.adhikatarasteShAmavyaktAsaktachetasAm.h .."},
                {sanskrit : "अव्यक्ता हि गतिर्दुःखं देहवद्भिरवाप्यते ||१२- ५||", engltranslit : "avyaktA hi gatirduHkha.n dehavadbhiravApyate .. 12\-5.."},
                {sanskrit : "ये तु सर्वाणि कर्माणि मयि संन्यस्य मत्परः |", engltranslit : "ye tu sarvANi karmANi mayi sa.nnyasya matparaH ."},
                {sanskrit : "अनन्येनैव योगेन मां ध्यायन्त उपासते ||१२- ६||", engltranslit : "ananyenaiva yogena mA.n dhyAyanta upAsate .. 12\-6.."},
                {sanskrit : "तेषामहं समुद्धर्ता मृत्युसंसारसागरात् |", engltranslit : "teShAmahaM samuddhartA mR^ityusa.nsArasAgarAt.h ."},
                {sanskrit : "भवामि नचिरात्पार्थ मय्यावेशितचेतसाम् ||१२- ७||", engltranslit : "bhavAmi nachirAtpArtha mayyAveshitachetasAm.h .. 12\-7.."},
                {sanskrit : "मय्येव मन आधत्स्व मयि बुद्धिं निवेशय |", engltranslit : "mayyeva mana Adhatsva mayi buddhi.n niveshaya ."},
                {sanskrit : "निवसिष्यसि मय्येव अत ऊर्ध्वं न संशयः ||१२- ८||", engltranslit : "nivasiShyasi mayyeva ata Urdhva.n na sa.nshayaH .. 12\-8.."},
                {sanskrit : "अथ चित्तं समाधातुं न शक्नोषि मयि स्थिरम् |", engltranslit : "atha chittaM samAdhAtuM na shaknoShi mayi sthiram.h ."},
                {sanskrit : "अभ्यासयोगेन ततो मामिच्छाप्तुं धनञ्जय ||१२- ९||", engltranslit : "abhyAsayogena tato mAmichchhAptuM dhana~njaya .. 12\-9.."},
                {sanskrit : "अभ्यासेऽप्यसमर्थोऽसि मत्कर्मपरमो भव |", engltranslit : "abhyAse.apyasamartho.asi matkarmaparamo bhava ."},
                {sanskrit : "मदर्थमपि कर्माणि कुर्वन्सिद्धिमवाप्स्यसि ||१२- १०||", engltranslit : "madarthamapi karmANi kurvansiddhimavApsyasi .. 12\-10.."},
                {sanskrit : "अथैतदप्यशक्तोऽसि कर्तुं मद्योगमाश्रितः |", engltranslit : "athaitadapyashakto.asi kartuM madyogamAshritaH ."},
                {sanskrit : "सर्वकर्मफलत्यागं ततः कुरु यतात्मवान् ||१२- ११||", engltranslit : "sarvakarmaphalatyAgaM tataH kuru yatAtmavAn.h .. 12\-11.."},
                {sanskrit : "श्रेयो हि ज्ञानमभ्यासाज्ज्ञानाद्ध्यानं विशिष्यते |", engltranslit : "shreyo hi GYAnamabhyAsAjGYAnAd.hdhyAnaM vishiShyate ."},
                {sanskrit : "ध्यानात्कर्मफलत्यागस्त्यागाच्छान्तिरनन्तरम् ||१२- १२||", engltranslit : "dhyAnAtkarmaphalatyAgastyAgAchchhAntiranantaram.h .. 12\-12.."},
                {sanskrit : "अद्वेष्टा सर्वभूतानां मैत्रः करुण एव च |", engltranslit : "adveShTA sarvabhUtAnAM maitraH karuNa eva cha ."},
                {sanskrit : "निर्ममो निरहङ्कारः समदुःखसुखः क्षमी ||१२- १३||", engltranslit : "nirmamo nirahaN^kAraH samaduHkhasukhaH kShamI .. 12\-13.."},
                {sanskrit : "सन्तुष्टः सततं योगी यतात्मा दृढनिश्चयः |", engltranslit : "santuShTaH satataM yogI yatAtmA dR^iDhanishchayaH ."},
                {sanskrit : "मय्यर्पितमनोबुद्धिर्यो मद्भक्तः स मे प्रियः ||१२- १४||", engltranslit : "mayyarpitamanobuddhiryo madbhaktaH sa me priyaH .. 12\-14.."},
                {sanskrit : "यस्मान्नोद्विजते लोको लोकान्नोद्विजते च यः |", engltranslit : "yasmAnnodvijate loko lokAnnodvijate cha yaH ."},
                {sanskrit : "हर्षामर्षभयोद्वेगैर्मुक्तो यः स च मे प्रियः ||१२- १५||", engltranslit : "harShAmarShabhayodvegairmukto yaH sa cha me priyaH .. 12\-15.."},
                {sanskrit : "अनपेक्षः शुचिर्दक्ष उदासीनो गतव्यथः |", engltranslit : "anapekShaH shuchirdakSha udAsIno gatavyathaH ."},
                {sanskrit : "सर्वारम्भपरित्यागी यो मद्भक्तः स मे प्रियः ||१२- १६||", engltranslit : "sarvArambhaparityAgI yo madbhaktaH sa me priyaH .. 12\-16.."},
                {sanskrit : "यो न हृष्यति न द्वेष्टि न शोचति न काङ्क्षति |", engltranslit : "yo na hR^iShyati na dveShTi na shochati na kAN^kShati ."},
                {sanskrit : "शुभाशुभपरित्यागी भक्तिमान्यः स मे प्रियः ||१२- १७||", engltranslit : "shubhAshubhaparityAgI bhaktimAnyaH sa me priyaH .. 12\-17.."},
                {sanskrit : "समः शत्रौ च मित्रे च तथा मानापमानयोः |", engltranslit : "samaH shatrau cha mitre cha tathA mAnApamAnayoH ."},
                {sanskrit : "शीतोष्णसुखदुःखेषु समः सङ्गविवर्जितः ||१२- १८||", engltranslit : "shItoShNasukhaduHkheShu samaH saN^gavivarjitaH .. 12\-18.."},
                {sanskrit : "तुल्यनिन्दास्तुतिर्मौनी सन्तुष्टो येन केनचित् |", engltranslit : "tulyanindAstutirmaunI santuShTo yena kenachit.h ."},
                {sanskrit : "अनिकेतः स्थिरमतिर्भक्तिमान्मे प्रियो नरः ||१२- १९||", engltranslit : "aniketaH sthiramatirbhaktimAnme priyo naraH .. 12\-19.."},
                {sanskrit : "ये तु धर्म्यामृतमिदं यथोक्तं पर्युपासते |", engltranslit : "ye tu dharmyAmR^itamidaM yathoktaM paryupAsate ."},
                {sanskrit : "श्रद्दधाना मत्परमा भक्तास्तेऽतीव मे प्रियाः ||१२- २०||", engltranslit : "shraddadhAnA matparamA bhaktAste.atIva me priyAH .. 12\-20.."},
                {sanskrit : "ॐ तत्सदिति श्रीमद्भगवद्गीतासूपनिषत्सु", engltranslit : "AUM tatsaditi shrImadbhagavadgItAsUpaniShatsu"},
                {sanskrit : "ब्रह्मविद्यायां योगशास्त्रे श्रीकृष्णार्जुनसंवादे", engltranslit : "brahmavidyAyA.n yogashAstre shrIkR^iShNArjunasa.nvAde"},
                {sanskrit : "भक्तियोगो नाम द्वादशोऽध्यायः ||१२||", engltranslit : "bhaktiyogo nAma dvAdasho.adhyAyaH .. 12.."},
                {sanskrit : "अथ त्रयोदशोऽध्यायः | क्षेत्रक्षेत्रज्ञविभागयोगः", engltranslit : "atha trayodasho.adhyAyaH . kShetrakShetraGYavibhAgayogaH"},
                {sanskrit : "अर्जुन उवाच |", engltranslit : "arjuna uvAcha ."},
                {sanskrit : "प्रकृतिं पुरुषं चैव क्षेत्रं क्षेत्रज्ञमेव च |", engltranslit : "prakR^itiM puruSha.n chaiva kShetra.n kShetraGYameva cha ."},
                {sanskrit : "एतद्वेदितुमिच्छामि ज्ञानं ज्ञेयं च केशव ||१३- १||", engltranslit : "etadveditumichchhAmi GYAnaM GYeya.n cha keshava .. 13\-1.."},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "इदं शरीरं कौन्तेय क्षेत्रमित्यभिधीयते |", engltranslit : "idaM sharIraM kaunteya kShetramityabhidhIyate ."},
                {sanskrit : "एतद्यो वेत्ति तं प्राहुः क्षेत्रज्ञ इति तद्विदः ||१३- २||", engltranslit : "etadyo vetti taM prAhuH kShetraGYa iti tadvidaH .. 13\-2.."},
                {sanskrit : "क्षेत्रज्ञं चापि मां विद्धि सर्वक्षेत्रेषु भारत |", engltranslit : "kShetraGYa.n chApi mAM viddhi sarvakShetreShu bhArata ."},
                {sanskrit : "क्षेत्रक्षेत्रज्ञयोर्ज्ञानं यत्तज्ज्ञानं मतं मम ||१३- ३||", engltranslit : "kShetrakShetraGYayorGYAnaM yattajGYAnaM mataM mama .. 13\-3.."},
                {sanskrit : "तत्क्षेत्रं यच्च यादृक्च यद्विकारि यतश्च यत् |", engltranslit : "tatkShetra.n yachcha yAdR^ikcha yadvikAri yatashcha yat.h ."},
                {sanskrit : "स च यो यत्प्रभावश्च तत्समासेन मे शृणु ||१३- ४||", engltranslit : "sa cha yo yatprabhAvashcha tatsamAsena me shR^iNu .. 13\-4.."},
                {sanskrit : "ऋषिभिर्बहुधा गीतं छन्दोभिर्विविधैः पृथक् |", engltranslit : "R^iShibhirbahudhA gIta.n chhandobhirvividhaiH pR^ithak.h ."},
                {sanskrit : "ब्रह्मसूत्रपदैश्चैव हेतुमद्भिर्विनिश्चितैः ||१३- ५||", engltranslit : "brahmasUtrapadaishchaiva hetumadbhirvinishchitaiH .. 13\-5.."},
                {sanskrit : "महाभूतान्यहंकारो बुद्धिरव्यक्तमेव च |", engltranslit : "mahAbhUtAnyaha.nkAro buddhiravyaktameva cha ."},
                {sanskrit : "इन्द्रियाणि दशैकं च पञ्च चेन्द्रियगोचराः ||१३- ६||", engltranslit : "indriyANi dashaika.n cha pa~ncha chendriyagocharAH .. 13\-6.."},
                {sanskrit : "इच्छा द्वेषः सुखं दुःखं संघातश्चेतना धृतिः |", engltranslit : "ichchhA dveShaH sukhaM duHkhaM sa.nghAtashchetanA dhR^itiH ."},
                {sanskrit : "एतत्क्षेत्रं समासेन सविकारमुदाहृतम् ||१३- ७||", engltranslit : "etatkShetra.n samAsena savikAramudAhR^itam.h .. 13\-7.."},
                {sanskrit : "अमानित्वमदम्भित्वमहिंसा क्षान्तिरार्जवम् |", engltranslit : "amAnitvamadambhitvamahi.nsA kShAntirArjavam.h ."},
                {sanskrit : "आचार्योपासनं शौचं स्थैर्यमात्मविनिग्रहः ||१३- ८||", engltranslit : "AchAryopAsanaM shauchaM sthairyamAtmavinigrahaH .. 13\-8.."},
                {sanskrit : "इन्द्रियार्थेषु वैराग्यमनहंकार एव च |", engltranslit : "indriyArtheShu vairAgyamanaha.nkAra eva cha ."},
                {sanskrit : "जन्ममृत्युजराव्याधिदुःखदोषानुदर्शनम् ||१३- ९||", engltranslit : "janmamR^ityujarAvyAdhiduHkhadoShAnudarshanam.h .. 13\-9.."},
                {sanskrit : "असक्तिरनभिष्वङ्गः पुत्रदारगृहादिषु |", engltranslit : "asaktiranabhiShvaN^gaH putradAragR^ihAdiShu ."},
                {sanskrit : "नित्यं च समचित्तत्वमिष्टानिष्टोपपत्तिषु ||१३- १०||", engltranslit : "nitya.n cha samachittatvamiShTAniShTopapattiShu .. 13\-10.."},
                {sanskrit : "मयि चानन्ययोगेन भक्तिरव्यभिचारिणी |", engltranslit : "mayi chAnanyayogena bhaktiravyabhichAriNI ."},
                {sanskrit : "विविक्तदेशसेवित्वमरतिर्जनसंसदि ||१३- ११||", engltranslit : "viviktadeshasevitvamaratirjanasa.nsadi .. 13\-11.."},
                {sanskrit : "अध्यात्मज्ञाननित्यत्वं तत्त्वज्ञानार्थदर्शनम् |", engltranslit : "adhyAtmaGYAnanityatva.n tattvaGYAnArthadarshanam.h ."},
                {sanskrit : "एतज्ज्ञानमिति प्रोक्तमज्ञानं यदतोऽन्यथा ||१३- १२||", engltranslit : "etajGYAnamiti proktamaGYAnaM yadato.anyathA .. 13\-12.."},
                {sanskrit : "ज्ञेयं यत्तत्प्रवक्ष्यामि यज्ज्ञात्वामृतमश्नुते |", engltranslit : "GYeyaM yattatpravakShyAmi yajGYAtvAmR^itamashnute ."},
                {sanskrit : "अनादिमत्परं ब्रह्म न सत्तन्नासदुच्यते ||१३- १३||", engltranslit : "anAdimatparaM brahma na sattannAsaduchyate .. 13\-13.."},
                {sanskrit : "सर्वतः पाणिपादं तत्सर्वतोऽक्षिशिरोमुखम् |", engltranslit : "sarvataH pANipAdaM tatsarvato.akShishiromukham.h ."},
                {sanskrit : "सर्वतः श्रुतिमल्लोके सर्वमावृत्य तिष्ठति ||१३- १४||", engltranslit : "sarvataH shrutimalloke sarvamAvR^itya tiShThati .. 13\-14.."},
                {sanskrit : "सर्वेन्द्रियगुणाभासं सर्वेन्द्रियविवर्जितम् |", engltranslit : "sarvendriyaguNAbhAsaM sarvendriyavivarjitam.h ."},
                {sanskrit : "असक्तं सर्वभृच्चैव निर्गुणं गुणभोक्तृ च ||१३- १५||", engltranslit : "asakta.n sarvabhR^ichchaiva nirguNaM guNabhoktR^i cha .. 13\-15.."},
                {sanskrit : "बहिरन्तश्च भूतानामचरं चरमेव च |", engltranslit : "bahirantashcha bhUtAnAmachara.n charameva cha ."},
                {sanskrit : "सूक्ष्मत्वात्तदविज्ञेयं दूरस्थं चान्तिके च तत् ||१३- १६||", engltranslit : "sUkShmatvAttadaviGYeyaM dUrastha.n chAntike cha tat.h .. 13\-16.."},
                {sanskrit : "अविभक्तं च भूतेषु विभक्तमिव च स्थितम् |", engltranslit : "avibhakta.n cha bhUteShu vibhaktamiva cha sthitam.h ."},
                {sanskrit : "भूतभर्तृ च तज्ज्ञेयं ग्रसिष्णु प्रभविष्णु च ||१३- १७||", engltranslit : "bhUtabhartR^i cha tajGYeyaM grasiShNu prabhaviShNu cha .. 13\-17.."},
                {sanskrit : "ज्योतिषामपि तज्ज्योतिस्तमसः परमुच्यते |", engltranslit : "jyotiShAmapi tajjyotistamasaH paramuchyate ."},
                {sanskrit : "ज्ञानं ज्ञेयं ज्ञानगम्यं हृदि सर्वस्य विष्ठितम् ||१३- १८||", engltranslit : "GYAnaM GYeyaM GYAnagamya.n hR^idi sarvasya viShThitam.h .. 13\-18.."},
                {sanskrit : "इति क्षेत्रं तथा ज्ञानं ज्ञेयं चोक्तं समासतः |", engltranslit : "iti kShetra.n tathA GYAnaM GYeya.n chokta.n samAsataH ."},
                {sanskrit : "मद्भक्त एतद्विज्ञाय मद्भावायोपपद्यते ||१३- १९||", engltranslit : "madbhakta etadviGYAya madbhAvAyopapadyate .. 13\-19.."},
                {sanskrit : "प्रकृतिं पुरुषं चैव विद्ध्यनादी उभावपि |", engltranslit : "prakR^itiM puruSha.n chaiva vid.hdhyanAdI ubhAvapi ."},
                {sanskrit : "विकारांश्च गुणांश्चैव विद्धि प्रकृतिसम्भवान् ||१३- २०||", engltranslit : "vikArA.nshcha guNA.nshchaiva viddhi prakR^itisambhavAn.h .. 13\-20.."},
                {sanskrit : "कार्यकारणकर्तृत्वे हेतुः प्रकृतिरुच्यते |", engltranslit : "kAryakAraNakartR^itve hetuH prakR^itiruchyate ."},
                {sanskrit : "पुरुषः सुखदुःखानां भोक्तृत्वे हेतुरुच्यते ||१३- २१||", engltranslit : "puruShaH sukhaduHkhAnAM bhoktR^itve heturuchyate .. 13\-21.."},
                {sanskrit : "पुरुषः प्रकृतिस्थो हि भुङ्क्ते प्रकृतिजान्गुणान् |", engltranslit : "puruShaH prakR^itistho hi bhuN^.hkte prakR^itijAnguNAn.h ."},
                {sanskrit : "कारणं गुणसङ्गोऽस्य सदसद्योनिजन्मसु ||१३- २२||", engltranslit : "kAraNaM guNasaN^go.asya sadasadyonijanmasu .. 13\-22.."},
                {sanskrit : "उपद्रष्टानुमन्ता च भर्ता भोक्ता महेश्वरः |", engltranslit : "upadraShTAnumantA cha bhartA bhoktA maheshvaraH ."},
                {sanskrit : "परमात्मेति चाप्युक्तो देहेऽस्मिन्पुरुषः परः ||१३- २३||", engltranslit : "paramAtmeti chApyukto dehe.asminpuruShaH paraH .. 13\-23.."},
                {sanskrit : "य एवं वेत्ति पुरुषं प्रकृतिं च गुणैः सह |", engltranslit : "ya evaM vetti puruShaM prakR^iti.n cha guNaiH saha ."},
                {sanskrit : "सर्वथा वर्तमानोऽपि न स भूयोऽभिजायते ||१३- २४||", engltranslit : "sarvathA vartamAno.api na sa bhUyo.abhijAyate .. 13\-24.."},
                {sanskrit : "ध्यानेनात्मनि पश्यन्ति केचिदात्मानमात्मना |", engltranslit : "dhyAnenAtmani pashyanti kechidAtmAnamAtmanA ."},
                {sanskrit : "अन्ये साङ्ख्येन योगेन कर्मयोगेन चापरे ||१३- २५||", engltranslit : "anye sAN^khyena yogena karmayogena chApare .. 13\-25.."},
                {sanskrit : "अन्ये त्वेवमजानन्तः श्रुत्वान्येभ्य उपासते |", engltranslit : "anye tvevamajAnantaH shrutvAnyebhya upAsate ."},
                {sanskrit : "तेऽपि चातितरन्त्येव मृत्युं श्रुतिपरायणाः ||१३- २६||", engltranslit : "te.api chAtitarantyeva mR^ityu.n shrutiparAyaNAH .. 13\-26.."},
                {sanskrit : "यावत्सञ्जायते किञ्चित्सत्त्वं स्थावरजङ्गमम् |", engltranslit : "yAvatsa~njAyate ki~nchitsattvaM sthAvarajaN^gamam.h ."},
                {sanskrit : "क्षेत्रक्षेत्रज्ञसंयोगात्तद्विद्धि भरतर्षभ ||१३- २७||", engltranslit : "kShetrakShetraGYasa.nyogAttadviddhi bharatarShabha .. 13\-27.."},
                {sanskrit : "समं सर्वेषु भूतेषु तिष्ठन्तं परमेश्वरम् |", engltranslit : "samaM sarveShu bhUteShu tiShThantaM parameshvaram.h ."},
                {sanskrit : "विनश्यत्स्वविनश्यन्तं यः पश्यति स पश्यति ||१३- २८||", engltranslit : "vinashyatsvavinashyanta.n yaH pashyati sa pashyati .. 13\-28.."},
                {sanskrit : "समं पश्यन्हि सर्वत्र समवस्थितमीश्वरम् |", engltranslit : "samaM pashyanhi sarvatra samavasthitamIshvaram.h ."},
                {sanskrit : "न हिनस्त्यात्मनात्मानं ततो याति परां गतिम् ||१३- २९||", engltranslit : "na hinastyAtmanAtmAnaM tato yAti parAM gatim.h .. 13\-29.."},
                {sanskrit : "प्रकृत्यैव च कर्माणि क्रियमाणानि सर्वशः |", engltranslit : "prakR^ityaiva cha karmANi kriyamANAni sarvashaH ."},
                {sanskrit : "यः पश्यति तथात्मानमकर्तारं स पश्यति ||१३- ३०||", engltranslit : "yaH pashyati tathAtmAnamakartAraM sa pashyati .. 13\-30.."},
                {sanskrit : "यदा भूतपृथग्भावमेकस्थमनुपश्यति |", engltranslit : "yadA bhUtapR^ithagbhAvamekasthamanupashyati ."},
                {sanskrit : "तत एव च विस्तारं ब्रह्म सम्पद्यते तदा ||१३- ३१||", engltranslit : "tata eva cha vistAraM brahma sampadyate tadA .. 13\-31.."},
                {sanskrit : "अनादित्वान्निर्गुणत्वात्परमात्मायमव्ययः |", engltranslit : "anAditvAnnirguNatvAtparamAtmAyamavyayaH ."},
                {sanskrit : "शरीरस्थोऽपि कौन्तेय न करोति न लिप्यते ||१३- ३२||", engltranslit : "sharIrastho.api kaunteya na karoti na lipyate .. 13\-32.."},
                {sanskrit : "यथा सर्वगतं सौक्ष्म्यादाकाशं नोपलिप्यते |", engltranslit : "yathA sarvagataM saukShmyAdAkAshaM nopalipyate ."},
                {sanskrit : "सर्वत्रावस्थितो देहे तथात्मा नोपलिप्यते ||१३- ३३||", engltranslit : "sarvatrAvasthito dehe tathAtmA nopalipyate .. 13\-33.."},
                {sanskrit : "यथा प्रकाशयत्येकः कृत्स्नं लोकमिमं रविः |", engltranslit : "yathA prakAshayatyekaH kR^itsna.n lokamimaM raviH ."},
                {sanskrit : "क्षेत्रं क्षेत्री तथा कृत्स्नं प्रकाशयति भारत ||१३- ३४||", engltranslit : "kShetra.n kShetrI tathA kR^itsnaM prakAshayati bhArata .. 13\-34.."},
                {sanskrit : "क्षेत्रक्षेत्रज्ञयोरेवमन्तरं ज्ञानचक्षुषा |", engltranslit : "kShetrakShetraGYayorevamantaraM GYAnachakShuShA ."},
                {sanskrit : "भूतप्रकृतिमोक्षं च ये विदुर्यान्ति ते परम् ||१३- ३५||", engltranslit : "bhUtaprakR^itimokSha.n cha ye viduryAnti te param.h .. 13\-35.."},
                {sanskrit : "ॐ तत्सदिति श्रीमद्भगवद्गीतासूपनिषत्सु", engltranslit : "AUM tatsaditi shrImadbhagavadgItAsUpaniShatsu"},
                {sanskrit : "ब्रह्मविद्यायां योगशास्त्रे श्रीकृष्णार्जुनसंवादे", engltranslit : "brahmavidyAyA.n yogashAstre shrIkR^iShNArjunasa.nvAde"},
                {sanskrit : "क्षेत्रक्षेत्रज्ञविभागयोगो नाम त्रयोदशोऽध्यायः ||१३||", engltranslit : "kShetrakShetraGYavibhAgayogo nAma trayodasho.adhyAyaH .. 13.."},
                {sanskrit : "अथ चतुर्दशोऽध्यायः | गुणत्रयविभागयोगः", engltranslit : "atha chaturdasho.adhyAyaH . guNatrayavibhAgayogaH"},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "परं भूयः प्रवक्ष्यामि ज्ञानानां ज्ञानमुत्तमम् |", engltranslit : "paraM bhUyaH pravakShyAmi GYAnAnA.n GYAnamuttamam.h ."},
                {sanskrit : "यज्ज्ञात्वा मुनयः सर्वे परां सिद्धिमितो गताः ||१४- १||", engltranslit : "yajGYAtvA munayaH sarve parAM siddhimito gatAH .. 14\-1.."},
                {sanskrit : "इदं ज्ञानमुपाश्रित्य मम साधर्म्यमागताः |", engltranslit : "idaM GYAnamupAshritya mama sAdharmyamAgatAH ."},
                {sanskrit : "सर्गेऽपि नोपजायन्ते प्रलये न व्यथन्ति च ||१४- २||", engltranslit : "sarge.api nopajAyante pralaye na vyathanti cha .. 14\-2.."},
                {sanskrit : "मम योनिर्महद् ब्रह्म तस्मिन्गर्भं दधाम्यहम् |", engltranslit : "mama yonirmahad.h brahma tasmingarbha.n dadhAmyaham.h ."},
                {sanskrit : "सम्भवः सर्वभूतानां ततो भवति भारत ||१४- ३||", engltranslit : "sambhavaH sarvabhUtAnA.n tato bhavati bhArata .. 14\-3.."},
                {sanskrit : "सर्वयोनिषु कौन्तेय मूर्तयः सम्भवन्ति याः |", engltranslit : "sarvayoniShu kaunteya mUrtayaH sambhavanti yAH ."},
                {sanskrit : "तासां ब्रह्म महद्योनिरहं बीजप्रदः पिता ||१४- ४||", engltranslit : "tAsAM brahma mahadyonirahaM bIjapradaH pitA .. 14\-4.."},
                {sanskrit : "सत्त्वं रजस्तम इति गुणाः प्रकृतिसम्भवाः |", engltranslit : "sattvaM rajastama iti guNAH prakR^itisambhavAH ."},
                {sanskrit : "निबध्नन्ति महाबाहो देहे देहिनमव्ययम् ||१४- ५||", engltranslit : "nibadhnanti mahAbAho dehe dehinamavyayam.h .. 14\-5.."},
                {sanskrit : "तत्र सत्त्वं निर्मलत्वात्प्रकाशकमनामयम् |", engltranslit : "tatra sattvaM nirmalatvAtprakAshakamanAmayam.h ."},
                {sanskrit : "सुखसङ्गेन बध्नाति ज्ञानसङ्गेन चानघ ||१४- ६||", engltranslit : "sukhasaN^gena badhnAti GYAnasaN^gena chAnagha .. 14\-6.."},
                {sanskrit : "रजो रागात्मकं विद्धि तृष्णासङ्गसमुद्भवम् |", engltranslit : "rajo rAgAtmakaM viddhi tR^iShNAsaN^gasamudbhavam.h ."},
                {sanskrit : "तन्निबध्नाति कौन्तेय कर्मसङ्गेन देहिनम् ||१४- ७||", engltranslit : "tannibadhnAti kaunteya karmasaN^gena dehinam.h .. 14\-7.."},
                {sanskrit : "तमस्त्वज्ञानजं विद्धि मोहनं सर्वदेहिनाम् |", engltranslit : "tamastvaGYAnajaM viddhi mohanaM sarvadehinAm.h ."},
                {sanskrit : "प्रमादालस्यनिद्राभिस्तन्निबध्नाति भारत ||१४- ८||", engltranslit : "pramAdAlasyanidrAbhistannibadhnAti bhArata .. 14\-8.."},
                {sanskrit : "सत्त्वं सुखे सञ्जयति रजः कर्मणि भारत |", engltranslit : "sattvaM sukhe sa~njayati rajaH karmaNi bhArata ."},
                {sanskrit : "ज्ञानमावृत्य तु तमः प्रमादे सञ्जयत्युत ||१४- ९||", engltranslit : "GYAnamAvR^itya tu tamaH pramAde sa~njayatyuta .. 14\-9.."},
                {sanskrit : "रजस्तमश्चाभिभूय सत्त्वं भवति भारत |", engltranslit : "rajastamashchAbhibhUya sattvaM bhavati bhArata ."},
                {sanskrit : "रजः सत्त्वं तमश्चैव तमः सत्त्वं रजस्तथा ||१४- १०||", engltranslit : "rajaH sattvaM tamashchaiva tamaH sattvaM rajastathA .. 14\-10.."},
                {sanskrit : "सर्वद्वारेषु देहेऽस्मिन्प्रकाश उपजायते |", engltranslit : "sarvadvAreShu dehe.asminprakAsha upajAyate ."},
                {sanskrit : "ज्ञानं यदा तदा विद्याद्विवृद्धं सत्त्वमित्युत ||१४- ११||", engltranslit : "GYAnaM yadA tadA vidyAdvivR^iddha.n sattvamityuta .. 14\-11.."},
                {sanskrit : "लोभः प्रवृत्तिरारम्भः कर्मणामशमः स्पृहा |", engltranslit : "lobhaH pravR^ittirArambhaH karmaNAmashamaH spR^ihA ."},
                {sanskrit : "रजस्येतानि जायन्ते विवृद्धे भरतर्षभ ||१४- १२||", engltranslit : "rajasyetAni jAyante vivR^iddhe bharatarShabha .. 14\-12.."},
                {sanskrit : "अप्रकाशोऽप्रवृत्तिश्च प्रमादो मोह एव च |", engltranslit : "aprakAsho.apravR^ittishcha pramAdo moha eva cha ."},
                {sanskrit : "तमस्येतानि जायन्ते विवृद्धे कुरुनन्दन ||१४- १३||", engltranslit : "tamasyetAni jAyante vivR^iddhe kurunandana .. 14\-13.."},
                {sanskrit : "यदा सत्त्वे प्रवृद्धे तु प्रलयं याति देहभृत् |", engltranslit : "yadA sattve pravR^iddhe tu pralayaM yAti dehabhR^it.h ."},
                {sanskrit : "तदोत्तमविदां लोकानमलान्प्रतिपद्यते ||१४- १४||", engltranslit : "tadottamavidA.n lokAnamalAnpratipadyate .. 14\-14.."},
                {sanskrit : "रजसि प्रलयं गत्वा कर्मसङ्गिषु जायते |", engltranslit : "rajasi pralayaM gatvA karmasaN^giShu jAyate ."},
                {sanskrit : "तथा प्रलीनस्तमसि मूढयोनिषु जायते ||१४- १५||", engltranslit : "tathA pralInastamasi mUDhayoniShu jAyate .. 14\-15.."},
                {sanskrit : "कर्मणः सुकृतस्याहुः सात्त्विकं निर्मलं फलम् |", engltranslit : "karmaNaH sukR^itasyAhuH sAttvikaM nirmalaM phalam.h ."},
                {sanskrit : "रजसस्तु फलं दुःखमज्ञानं तमसः फलम् ||१४- १६||", engltranslit : "rajasastu phalaM duHkhamaGYAnaM tamasaH phalam.h .. 14\-16.."},
                {sanskrit : "सत्त्वात्सञ्जायते ज्ञानं रजसो लोभ एव च |", engltranslit : "sattvAtsa~njAyate GYAnaM rajaso lobha eva cha ."},
                {sanskrit : "प्रमादमोहौ तमसो भवतोऽज्ञानमेव च ||१४- १७||", engltranslit : "pramAdamohau tamaso bhavato.aGYAnameva cha .. 14\-17.."},
                {sanskrit : "ऊर्ध्वं गच्छन्ति सत्त्वस्था मध्ये तिष्ठन्ति राजसाः |", engltranslit : "Urdhva.n gachchhanti sattvasthA madhye tiShThanti rAjasAH ."},
                {sanskrit : "जघन्यगुणवृत्तिस्था अधो गच्छन्ति तामसाः ||१४- १८||", engltranslit : "jaghanyaguNavR^ittisthA adho gachchhanti tAmasAH .. 14\-18.."},
                {sanskrit : "नान्यं गुणेभ्यः कर्तारं यदा द्रष्टानुपश्यति |", engltranslit : "nAnya.n guNebhyaH kartAraM yadA draShTAnupashyati ."},
                {sanskrit : "गुणेभ्यश्च परं वेत्ति मद्भावं सोऽधिगच्छति ||१४- १९||", engltranslit : "guNebhyashcha paraM vetti madbhAvaM so.adhigachchhati .. 14\-19.."},
                {sanskrit : "गुणानेतानतीत्य त्रीन्देही देहसमुद्भवान् |", engltranslit : "guNAnetAnatItya trIndehI dehasamudbhavAn.h ."},
                {sanskrit : "जन्ममृत्युजरादुःखैर्विमुक्तोऽमृतमश्नुते ||१४- २०||", engltranslit : "janmamR^ityujarAduHkhairvimukto.amR^itamashnute .. 14\-20.."},
                {sanskrit : "अर्जुन उवाच |", engltranslit : "arjuna uvAcha ."},
                {sanskrit : "कैर्लिङ्गैस्त्रीन्गुणानेतानतीतो भवति प्रभो |", engltranslit : "kairliN^gaistrInguNAnetAnatIto bhavati prabho ."},
                {sanskrit : "किमाचारः कथं चैतांस्त्रीन्गुणानतिवर्तते ||१४- २१||", engltranslit : "kimAchAraH katha.n chaitA.nstrInguNAnativartate .. 14\-21.."},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "प्रकाशं च प्रवृत्तिं च मोहमेव च पाण्डव |", engltranslit : "prakAsha.n cha pravR^itti.n cha mohameva cha pANDava ."},
                {sanskrit : "न द्वेष्टि सम्प्रवृत्तानि न निवृत्तानि काङ्क्षति ||१४- २२||", engltranslit : "na dveShTi sampravR^ittAni na nivR^ittAni kAN^kShati .. 14\-22.."},
                {sanskrit : "उदासीनवदासीनो गुणैर्यो न विचाल्यते |", engltranslit : "udAsInavadAsIno guNairyo na vichAlyate ."},
                {sanskrit : "गुणा वर्तन्त इत्येवं योऽवतिष्ठति नेङ्गते ||१४- २३||", engltranslit : "guNA vartanta ityevaM yo.avatiShThati neN^gate .. 14\-23.."},
                {sanskrit : "समदुःखसुखः स्वस्थः समलोष्टाश्मकाञ्चनः |", engltranslit : "samaduHkhasukhaH svasthaH samaloShTAshmakA~nchanaH ."},
                {sanskrit : "तुल्यप्रियाप्रियो धीरस्तुल्यनिन्दात्मसंस्तुतिः ||१४- २४||", engltranslit : "tulyapriyApriyo dhIrastulyanindAtmasa.nstutiH .. 14\-24.."},
                {sanskrit : "मानापमानयोस्तुल्यस्तुल्यो मित्रारिपक्षयोः |", engltranslit : "mAnApamAnayostulyastulyo mitrAripakShayoH ."},
                {sanskrit : "सर्वारम्भपरित्यागी गुणातीतः स उच्यते ||१४- २५||", engltranslit : "sarvArambhaparityAgI guNAtItaH sa uchyate .. 14\-25.."},
                {sanskrit : "मां च योऽव्यभिचारेण भक्तियोगेन सेवते |", engltranslit : "mA.n cha yo.avyabhichAreNa bhaktiyogena sevate ."},
                {sanskrit : "स गुणान्समतीत्यैतान्ब्रह्मभूयाय कल्पते ||१४- २६||", engltranslit : "sa guNAnsamatItyaitAnbrahmabhUyAya kalpate .. 14\-26.."},
                {sanskrit : "ब्रह्मणो हि प्रतिष्ठाहममृतस्याव्ययस्य च |", engltranslit : "brahmaNo hi pratiShThAhamamR^itasyAvyayasya cha ."},
                {sanskrit : "शाश्वतस्य च धर्मस्य सुखस्यैकान्तिकस्य च ||१४- २७||", engltranslit : "shAshvatasya cha dharmasya sukhasyaikAntikasya cha .. 14\-27.."},
                {sanskrit : "ॐ तत्सदिति श्रीमद्भगवद्गीतासूपनिषत्सु", engltranslit : "AUM tatsaditi shrImadbhagavadgItAsUpaniShatsu"},
                {sanskrit : "ब्रह्मविद्यायां योगशास्त्रे श्रीकृष्णार्जुनसंवादे", engltranslit : "brahmavidyAyA.n yogashAstre shrIkR^iShNArjunasa.nvAde"},
                {sanskrit : "गुणत्रयविभागयोगो नाम चतुर्दशोऽध्यायः ||१४||", engltranslit : "guNatrayavibhAgayogo nAma chaturdasho.adhyAyaH .. 14.."},
                {sanskrit : "अथ पञ्चदशोऽध्यायः | पुरुषोत्तमयोगः", engltranslit : "atha pa~nchadasho.adhyAyaH . puruShottamayogaH"},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "ऊर्ध्वमूलमधःशाखमश्वत्थं प्राहुरव्ययम् |", engltranslit : "UrdhvamUlamadhaHshAkhamashvatthaM prAhuravyayam.h ."},
                {sanskrit : "छन्दांसि यस्य पर्णानि यस्तं वेद स वेदवित् ||१५- १||", engltranslit : "chhandA.nsi yasya parNAni yasta.n veda sa vedavit.h .. 15\-1.."},
                {sanskrit : "अधश्चोर्ध्वं प्रसृतास्तस्य शाखा", engltranslit : "adhashchordhvaM prasR^itAstasya shAkhA"},
                {sanskrit : "गुणप्रवृद्धा विषयप्रवालाः |", engltranslit : "guNapravR^iddhA viShayapravAlAH ."},
                {sanskrit : "अधश्च मूलान्यनुसन्ततानि", engltranslit : "adhashcha mUlAnyanusantatAni"},
                {sanskrit : "कर्मानुबन्धीनि मनुष्यलोके ||१५- २||", engltranslit : "karmAnubandhIni manuShyaloke .. 15\-2.."},
                {sanskrit : "न रूपमस्येह तथोपलभ्यते", engltranslit : "na rUpamasyeha tathopalabhyate"},
                {sanskrit : "नान्तो न चादिर्न च सम्प्रतिष्ठा |", engltranslit : "nAnto na chAdirna cha sampratiShThA ."},
                {sanskrit : "अश्वत्थमेनं सुविरूढमूलं", engltranslit : "ashvatthamenaM suvirUDhamUlaM"},
                {sanskrit : "असङ्गशस्त्रेण दृढेन छित्त्वा ||१५- ३||", engltranslit : "asaN^gashastreNa dR^iDhena chhittvA .. 15\-3.."},
                {sanskrit : "ततः पदं तत्परिमार्गितव्यं", engltranslit : "tataH padaM tatparimArgitavyaM"},
                {sanskrit : "यस्मिन्गता न निवर्तन्ति भूयः |", engltranslit : "yasmingatA na nivartanti bhUyaH ."},
                {sanskrit : "तमेव चाद्यं पुरुषं प्रपद्ये |", engltranslit : "tameva chAdyaM puruShaM prapadye ."},
                {sanskrit : "यतः प्रवृत्तिः प्रसृता पुराणी ||१५- ४||", engltranslit : "yataH pravR^ittiH prasR^itA purANI .. 15\-4.."},
                {sanskrit : "निर्मानमोहा जितसङ्गदोषा", engltranslit : "nirmAnamohA jitasaN^gadoShA"},
                {sanskrit : "अध्यात्मनित्या विनिवृत्तकामाः |", engltranslit : "adhyAtmanityA vinivR^ittakAmAH ."},
                {sanskrit : "द्वन्द्वैर्विमुक्ताः सुखदुःखसंज्ञैर्- ", engltranslit : "dvandvairvimuktAH sukhaduHkhasa.nGYair\-"},
                {sanskrit : "गच्छन्त्यमूढाः पदमव्ययं तत् ||१५- ५||", engltranslit : "gachchhantyamUDhAH padamavyayaM tat.h .. 15\-5.."},
                {sanskrit : "न तद्भासयते सूर्यो न शशाङ्को न पावकः |", engltranslit : "na tadbhAsayate sUryo na shashAN^ko na pAvakaH ."},
                {sanskrit : "यद्गत्वा न निवर्तन्ते तद्धाम परमं मम ||१५- ६||", engltranslit : "yadgatvA na nivartante taddhAma paramaM mama .. 15\-6.."},
                {sanskrit : "ममैवांशो जीवलोके जीवभूतः सनातनः |", engltranslit : "mamaivA.nsho jIvaloke jIvabhUtaH sanAtanaH ."},
                {sanskrit : "मनःषष्ठानीन्द्रियाणि प्रकृतिस्थानि कर्षति ||१५- ७||", engltranslit : "manaHShaShThAnIndriyANi prakR^itisthAni karShati .. 15\-7.."},
                {sanskrit : "शरीरं यदवाप्नोति यच्चाप्युत्क्रामतीश्वरः |", engltranslit : "sharIra.n yadavApnoti yachchApyutkrAmatIshvaraH ."},
                {sanskrit : "गृहीत्वैतानि संयाति वायुर्गन्धानिवाशयात् ||१५- ८||", engltranslit : "gR^ihItvaitAni sa.nyAti vAyurgandhAnivAshayAt.h .. 15\-8.."},
                {sanskrit : "श्रोत्रं चक्षुः स्पर्शनं च रसनं घ्राणमेव च |", engltranslit : "shrotra.n chakShuH sparshana.n cha rasanaM ghrANameva cha ."},
                {sanskrit : "अधिष्ठाय मनश्चायं विषयानुपसेवते ||१५- ९||", engltranslit : "adhiShThAya manashchAyaM viShayAnupasevate .. 15\-9.."},
                {sanskrit : "उत्क्रामन्तं स्थितं वापि भुञ्जानं वा गुणान्वितम् |", engltranslit : "utkrAmantaM sthitaM vApi bhu~njAnaM vA guNAnvitam.h ."},
                {sanskrit : "विमूढा नानुपश्यन्ति पश्यन्ति ज्ञानचक्षुषः ||१५- १०||", engltranslit : "vimUDhA nAnupashyanti pashyanti GYAnachakShuShaH .. 15\-10.."},
                {sanskrit : "यतन्तो योगिनश्चैनं पश्यन्त्यात्मन्यवस्थितम् |", engltranslit : "yatanto yoginashchainaM pashyantyAtmanyavasthitam.h ."},
                {sanskrit : "यतन्तोऽप्यकृतात्मानो नैनं पश्यन्त्यचेतसः ||१५- ११||", engltranslit : "yatanto.apyakR^itAtmAno nainaM pashyantyachetasaH .. 15\-11.."},
                {sanskrit : "यदादित्यगतं तेजो जगद्भासयतेऽखिलम् |", engltranslit : "yadAdityagataM tejo jagadbhAsayate.akhilam.h ."},
                {sanskrit : "यच्चन्द्रमसि यच्चाग्नौ तत्तेजो विद्धि मामकम् ||१५- १२||", engltranslit : "yachchandramasi yachchAgnau tattejo viddhi mAmakam.h .. 15\-12.."},
                {sanskrit : "गामाविश्य च भूतानि धारयाम्यहमोजसा |", engltranslit : "gAmAvishya cha bhUtAni dhArayAmyahamojasA ."},
                {sanskrit : "पुष्णामि चौषधीः सर्वाः सोमो भूत्वा रसात्मकः ||१५- १३||", engltranslit : "puShNAmi chauShadhIH sarvAH somo bhUtvA rasAtmakaH .. 15\-13.."},
                {sanskrit : "अहं वैश्वानरो भूत्वा प्राणिनां देहमाश्रितः |", engltranslit : "aha.n vaishvAnaro bhUtvA prANinA.n dehamAshritaH ."},
                {sanskrit : "प्राणापानसमायुक्तः पचाम्यन्नं चतुर्विधम् ||१५- १४||", engltranslit : "prANApAnasamAyuktaH pachAmyanna.n chaturvidham.h .. 15\-14.."},
                {sanskrit : "सर्वस्य चाहं हृदि सन्निविष्टो", engltranslit : "sarvasya chAhaM hR^idi sanniviShTo"},
                {sanskrit : "मत्तः स्मृतिर्ज्ञानमपोहनञ्च |", engltranslit : "mattaH smR^itirGYAnamapohana~ncha ."},
                {sanskrit : "वेदैश्च सर्वैरहमेव वेद्यो", engltranslit : "vedaishcha sarvairahameva vedyo"},
                {sanskrit : "वेदान्तकृद्वेदविदेव चाहम् ||१५- १५||", engltranslit : "vedAntakR^idvedavideva chAham.h .. 15\-15.."},
                {sanskrit : "द्वाविमौ पुरुषौ लोके क्षरश्चाक्षर एव च |", engltranslit : "dvAvimau puruShau loke kSharashchAkShara eva cha ."},
                {sanskrit : "क्षरः सर्वाणि भूतानि कूटस्थोऽक्षर उच्यते ||१५- १६||", engltranslit : "kSharaH sarvANi bhUtAni kUTastho.akShara uchyate .. 15\-16.."},
                {sanskrit : "उत्तमः पुरुषस्त्वन्यः परमात्मेत्युधाहृतः |", engltranslit : "uttamaH puruShastvanyaH paramAtmetyudhAhR^itaH ."},
                {sanskrit : "यो लोकत्रयमाविश्य बिभर्त्यव्यय ईश्वरः ||१५- १७||", engltranslit : "yo lokatrayamAvishya bibhartyavyaya IshvaraH .. 15\-17.."},
                {sanskrit : "यस्मात्क्षरमतीतोऽहमक्षरादपि चोत्तमः |", engltranslit : "yasmAtkSharamatIto.ahamakSharAdapi chottamaH ."},
                {sanskrit : "अतोऽस्मि लोके वेदे च प्रथितः पुरुषोत्तमः ||१५- १८||", engltranslit : "ato.asmi loke vede cha prathitaH puruShottamaH .. 15\-18.."},
                {sanskrit : "यो मामेवमसम्मूढो जानाति पुरुषोत्तमम् |", engltranslit : "yo mAmevamasammUDho jAnAti puruShottamam.h ."},
                {sanskrit : "स सर्वविद्भजति मां सर्वभावेन भारत ||१५- १९||", engltranslit : "sa sarvavidbhajati mAM sarvabhAvena bhArata .. 15\-19.."},
                {sanskrit : "इति गुह्यतमं शास्त्रमिदमुक्तं मयानघ |", engltranslit : "iti guhyatamaM shAstramidamuktaM mayAnagha ."},
                {sanskrit : "एतद्बुद्ध्वा बुद्धिमान्स्यात्कृतकृत्यश्च भारत ||१५- २०||", engltranslit : "etadbud.hdhvA buddhimAnsyAtkR^itakR^ityashcha bhArata .. 15\-20.."},
                {sanskrit : "ॐ तत्सदिति श्रीमद्भगवद्गीतासूपनिषत्सु", engltranslit : "AUM tatsaditi shrImadbhagavadgItAsUpaniShatsu"},
                {sanskrit : "ब्रह्मविद्यायां योगशास्त्रे श्रीकृष्णार्जुन संवादे", engltranslit : "brahmavidyAyA.n yogashAstre shrIkR^iShNArjuna sa.nvAde"},
                {sanskrit : "पुरुषोत्तमयोगो नाम पञ्चदशोऽध्यायः ||१५||", engltranslit : "puruShottamayogo nAma pa~nchadasho.adhyAyaH .. 15.."},
                {sanskrit : "अथ षोडशोऽध्यायः | दैवासुरसम्पद्विभागयोगः", engltranslit : "atha ShoDasho.adhyAyaH . daivAsurasampadvibhAgayogaH"},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "अभयं सत्त्वसंशुद्धिर्ज्ञानयोगव्यवस्थितिः |", engltranslit : "abhayaM sattvasa.nshuddhirGYAnayogavyavasthitiH ."},
                {sanskrit : "दानं दमश्च यज्ञश्च स्वाध्यायस्तप आर्जवम् ||१६- १||", engltranslit : "dAnaM damashcha yaGYashcha svAdhyAyastapa Arjavam.h .. 16\-1.."},
                {sanskrit : "अहिंसा सत्यमक्रोधस्त्यागः शान्तिरपैशुनम् |", engltranslit : "ahi.nsA satyamakrodhastyAgaH shAntirapaishunam.h ."},
                {sanskrit : "दया भूतेष्वलोलुप्त्वं मार्दवं ह्रीरचापलम् ||१६- २||", engltranslit : "dayA bhUteShvaloluptvaM mArdavaM hrIrachApalam.h .. 16\-2.."},
                {sanskrit : "तेजः क्षमा धृतिः शौचमद्रोहो नातिमानिता |", engltranslit : "tejaH kShamA dhR^itiH shauchamadroho nAtimAnitA ."},
                {sanskrit : "भवन्ति सम्पदं दैवीमभिजातस्य भारत ||१६- ३||", engltranslit : "bhavanti sampadaM daivImabhijAtasya bhArata .. 16\-3.."},
                {sanskrit : "दम्भो दर्पोऽभिमानश्च क्रोधः पारुष्यमेव च |", engltranslit : "dambho darpo.abhimAnashcha krodhaH pAruShyameva cha ."},
                {sanskrit : "अज्ञानं चाभिजातस्य पार्थ सम्पदमासुरीम् ||१६- ४||", engltranslit : "aGYAna.n chAbhijAtasya pArtha sampadamAsurIm.h .. 16\-4.."},
                {sanskrit : "दैवी सम्पद्विमोक्षाय निबन्धायासुरी मता |", engltranslit : "daivI sampadvimokShAya nibandhAyAsurI matA ."},
                {sanskrit : "मा शुचः सम्पदं दैवीमभिजातोऽसि पाण्डव ||१६- ५||", engltranslit : "mA shuchaH sampadaM daivImabhijAto.asi pANDava .. 16\-5.."},
                {sanskrit : "द्वौ भूतसर्गौ लोकेऽस्मिन्दैव आसुर एव च |", engltranslit : "dvau bhUtasargau loke.asmindaiva Asura eva cha ."},
                {sanskrit : "दैवो विस्तरशः प्रोक्त आसुरं पार्थ मे शृणु ||१६- ६||", engltranslit : "daivo vistarashaH prokta AsuraM pArtha me shR^iNu .. 16\-6.."},
                {sanskrit : "प्रवृत्तिं च निवृत्तिं च जना न विदुरासुराः |", engltranslit : "pravR^itti.n cha nivR^itti.n cha janA na vidurAsurAH ."},
                {sanskrit : "न शौचं नापि चाचारो न सत्यं तेषु विद्यते ||१६- ७||", engltranslit : "na shauchaM nApi chAchAro na satya.n teShu vidyate .. 16\-7.."},
                {sanskrit : "असत्यमप्रतिष्ठं ते जगदाहुरनीश्वरम् |", engltranslit : "asatyamapratiShTha.n te jagadAhuranIshvaram.h ."},
                {sanskrit : "अपरस्परसम्भूतं किमन्यत्कामहैतुकम् ||१६- ८||", engltranslit : "aparasparasambhUtaM kimanyatkAmahaitukam.h .. 16\-8.."},
                {sanskrit : "एतां दृष्टिमवष्टभ्य नष्टात्मानोऽल्पबुद्धयः |", engltranslit : "etA.n dR^iShTimavaShTabhya naShTAtmAno.alpabuddhayaH ."},
                {sanskrit : "प्रभवन्त्युग्रकर्माणः क्षयाय जगतोऽहिताः ||१६- ९||", engltranslit : "prabhavantyugrakarmANaH kShayAya jagato.ahitAH .. 16\-9.."},
                {sanskrit : "काममाश्रित्य दुष्पूरं दम्भमानमदान्विताः |", engltranslit : "kAmamAshritya duShpUraM dambhamAnamadAnvitAH ."},
                {sanskrit : "मोहाद्गृहीत्वासद्ग्राहान्प्रवर्तन्तेऽशुचिव्रताः ||१६- १०||", engltranslit : "mohAd.hgR^ihItvAsad.hgrAhAnpravartante.ashuchivratAH .. 16\-10.."},
                {sanskrit : "चिन्तामपरिमेयां च प्रलयान्तामुपाश्रिताः |", engltranslit : "chintAmaparimeyA.n cha pralayAntAmupAshritAH ."},
                {sanskrit : "कामोपभोगपरमा एतावदिति निश्चिताः ||१६- ११||", engltranslit : "kAmopabhogaparamA etAvaditi nishchitAH .. 16\-11.."},
                {sanskrit : "आशापाशशतैर्बद्धाः कामक्रोधपरायणाः |", engltranslit : "AshApAshashatairbaddhAH kAmakrodhaparAyaNAH ."},
                {sanskrit : "ईहन्ते कामभोगार्थमन्यायेनार्थसञ्चयान् ||१६- १२||", engltranslit : "Ihante kAmabhogArthamanyAyenArthasa~nchayAn.h .. 16\-12.."},
                {sanskrit : "इदमद्य मया लब्धमिमं प्राप्स्ये मनोरथम् |", engltranslit : "idamadya mayA labdhamimaM prApsye manoratham.h ."},
                {sanskrit : "इदमस्तीदमपि मे भविष्यति पुनर्धनम् ||१६- १३||", engltranslit : "idamastIdamapi me bhaviShyati punardhanam.h .. 16\-13.."},
                {sanskrit : "असौ मया हतः शत्रुर्हनिष्ये चापरानपि |", engltranslit : "asau mayA hataH shatrurhaniShye chAparAnapi ."},
                {sanskrit : "ईश्वरोऽहमहं भोगी सिद्धोऽहं बलवान्सुखी ||१६- १४||", engltranslit : "Ishvaro.ahamahaM bhogI siddho.ahaM balavAnsukhI .. 16\-14.."},
                {sanskrit : "आढ्योऽभिजनवानस्मि कोऽन्योऽस्ति सदृशो मया |", engltranslit : "ADhyo.abhijanavAnasmi ko.anyo.asti sadR^isho mayA ."},
                {sanskrit : "यक्ष्ये दास्यामि मोदिष्य इत्यज्ञानविमोहिताः ||१६- १५||", engltranslit : "yakShye dAsyAmi modiShya ityaGYAnavimohitAH .. 16\-15.."},
                {sanskrit : "अनेकचित्तविभ्रान्ता मोहजालसमावृताः |", engltranslit : "anekachittavibhrAntA mohajAlasamAvR^itAH ."},
                {sanskrit : "प्रसक्ताः कामभोगेषु पतन्ति नरकेऽशुचौ ||१६- १६||", engltranslit : "prasaktAH kAmabhogeShu patanti narake.ashuchau .. 16\-16.."},
                {sanskrit : "आत्मसम्भाविताः स्तब्धा धनमानमदान्विताः |", engltranslit : "AtmasambhAvitAH stabdhA dhanamAnamadAnvitAH ."},
                {sanskrit : "यजन्ते नामयज्ञैस्ते दम्भेनाविधिपूर्वकम् ||१६- १७||", engltranslit : "yajante nAmayaGYaiste dambhenAvidhipUrvakam.h .. 16\-17.."},
                {sanskrit : "अहंकारं बलं दर्पं कामं क्रोधं च संश्रिताः |", engltranslit : "aha.nkAraM balaM darpa.n kAmaM krodha.n cha sa.nshritAH ."},
                {sanskrit : "मामात्मपरदेहेषु प्रद्विषन्तोऽभ्यसूयकाः ||१६- १८||", engltranslit : "mAmAtmaparadeheShu pradviShanto.abhyasUyakAH .. 16\-18.."},
                {sanskrit : "तानहं द्विषतः क्रुरान्संसारेषु नराधमान् |", engltranslit : "tAnahaM dviShataH krurAnsa.nsAreShu narAdhamAn.h ."},
                {sanskrit : "क्षिपाम्यजस्रमशुभानासुरीष्वेव योनिषु ||१६- १९||", engltranslit : "kShipAmyajasramashubhAnAsurIShveva yoniShu .. 16\-19.."},
                {sanskrit : "आसुरीं योनिमापन्ना मूढा जन्मनि जन्मनि |", engltranslit : "AsurI.n yonimApannA mUDhA janmani janmani ."},
                {sanskrit : "मामप्राप्यैव कौन्तेय ततो यान्त्यधमां गतिम् ||१६- २०||", engltranslit : "mAmaprApyaiva kaunteya tato yAntyadhamA.n gatim.h .. 16\-20.."},
                {sanskrit : "त्रिविधं नरकस्येदं द्वारं नाशनमात्मनः |", engltranslit : "trividhaM narakasyedaM dvAraM nAshanamAtmanaH ."},
                {sanskrit : "कामः क्रोधस्तथा लोभस्तस्मादेतत्त्रयं त्यजेत् ||१६- २१||", engltranslit : "kAmaH krodhastathA lobhastasmAdetattrayaM tyajet.h .. 16\-21.."},
                {sanskrit : "एतैर्विमुक्तः कौन्तेय तमोद्वारैस्त्रिभिर्नरः |", engltranslit : "etairvimuktaH kaunteya tamodvAraistribhirnaraH ."},
                {sanskrit : "आचरत्यात्मनः श्रेयस्ततो याति परां गतिम् ||१६- २२||", engltranslit : "AcharatyAtmanaH shreyastato yAti parAM gatim.h .. 16\-22.."},
                {sanskrit : "यः शास्त्रविधिमुत्सृज्य वर्तते कामकारतः |", engltranslit : "yaH shAstravidhimutsR^ijya vartate kAmakArataH ."},
                {sanskrit : "न स सिद्धिमवाप्नोति न सुखं न परां गतिम् ||१६- २३||", engltranslit : "na sa siddhimavApnoti na sukhaM na parAM gatim.h .. 16\-23.."},
                {sanskrit : "तस्माच्छास्त्रं प्रमाणं ते कार्याकार्यव्यवस्थितौ |", engltranslit : "tasmAchchhAstraM pramANaM te kAryAkAryavyavasthitau ."},
                {sanskrit : "ज्ञात्वा शास्त्रविधानोक्तं कर्म कर्तुमिहार्हसि ||१६- २४||", engltranslit : "GYAtvA shAstravidhAnokta.n karma kartumihArhasi .. 16\-24.."},
                {sanskrit : "ॐ तत्सदिति श्रीमद्भगवद्गीतासूपनिषत्सु", engltranslit : "AUM tatsaditi shrImadbhagavadgItAsUpaniShatsu"},
                {sanskrit : "ब्रह्मविद्यायां योगशास्त्रे श्रीकृष्णार्जुनसंवादे", engltranslit : "brahmavidyAyA.n yogashAstre shrIkR^iShNArjunasa.nvAde"},
                {sanskrit : "दैवासुरसम्पद्विभागयोगो नाम षोडशोऽध्यायः ||१६||", engltranslit : "daivAsurasampadvibhAgayogo nAma ShoDasho.adhyAyaH .. 16.."},
                {sanskrit : "अथ सप्तदशोऽध्यायः | श्रद्धात्रयविभागयोगः", engltranslit : "atha saptadasho.adhyAyaH . shraddhAtrayavibhAgayogaH"},
                {sanskrit : "अर्जुन उवाच |", engltranslit : "arjuna uvAcha ."},
                {sanskrit : "ये शास्त्रविधिमुत्सृज्य यजन्ते श्रद्धयान्विताः |", engltranslit : "ye shAstravidhimutsR^ijya yajante shraddhayAnvitAH ."},
                {sanskrit : "तेषां निष्ठा तु का कृष्ण सत्त्वमाहो रजस्तमः ||१७- १||", engltranslit : "teShA.n niShThA tu kA kR^iShNa sattvamAho rajastamaH .. 17\-1.."},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "त्रिविधा भवति श्रद्धा देहिनां सा स्वभावजा |", engltranslit : "trividhA bhavati shraddhA dehinA.n sA svabhAvajA ."},
                {sanskrit : "सात्त्विकी राजसी चैव तामसी चेति तां शृणु ||१७- २||", engltranslit : "sAttvikI rAjasI chaiva tAmasI cheti tA.n shR^iNu .. 17\-2.."},
                {sanskrit : "सत्त्वानुरूपा सर्वस्य श्रद्धा भवति भारत |", engltranslit : "sattvAnurUpA sarvasya shraddhA bhavati bhArata ."},
                {sanskrit : "श्रद्धामयोऽयं पुरुषो यो यच्छ्रद्धः स एव सः ||१७- ३||", engltranslit : "shraddhAmayo.ayaM puruSho yo yachchhraddhaH sa eva saH .. 17\-3.."},
                {sanskrit : "यजन्ते सात्त्विका देवान्यक्षरक्षांसि राजसाः |", engltranslit : "yajante sAttvikA devAnyakSharakShA.nsi rAjasAH ."},
                {sanskrit : "प्रेतान्भूतगणांश्चान्ये यजन्ते तामसा जनाः ||१७- ४||", engltranslit : "pretAnbhUtagaNA.nshchAnye yajante tAmasA janAH .. 17\-4.."},
                {sanskrit : "अशास्त्रविहितं घोरं तप्यन्ते ये तपो जनाः |", engltranslit : "ashAstravihitaM ghoraM tapyante ye tapo janAH ."},
                {sanskrit : "दम्भाहंकारसंयुक्ताः कामरागबलान्विताः ||१७- ५||", engltranslit : "dambhAha.nkArasa.nyuktAH kAmarAgabalAnvitAH .. 17\-5.."},
                {sanskrit : "कर्षयन्तः शरीरस्थं भूतग्राममचेतसः |", engltranslit : "karShayantaH sharIrasthaM bhUtagrAmamachetasaH ."},
                {sanskrit : "मां चैवान्तःशरीरस्थं तान्विद्ध्यासुरनिश्चयान् ||१७- ६||", engltranslit : "mA.n chaivAntaHsharIrastha.n tAnvid.hdhyAsuranishchayAn.h .. 17\-6.."},
                {sanskrit : "आहारस्त्वपि सर्वस्य त्रिविधो भवति प्रियः |", engltranslit : "AhArastvapi sarvasya trividho bhavati priyaH ."},
                {sanskrit : "यज्ञस्तपस्तथा दानं तेषां भेदमिमं शृणु ||१७- ७||", engltranslit : "yaGYastapastathA dAnaM teShAM bhedamimaM shR^iNu .. 17\-7.."},
                {sanskrit : "आयुःसत्त्वबलारोग्यसुखप्रीतिविवर्धनाः |", engltranslit : "AyuHsattvabalArogyasukhaprItivivardhanAH ."},
                {sanskrit : "रस्याः स्निग्धाः स्थिरा हृद्या आहाराः सात्त्विकप्रियाः ||१७- ८||", engltranslit : "rasyAH snigdhAH sthirA hR^idyA AhArAH sAttvikapriyAH .. 17\-8.."},
                {sanskrit : "कट्वम्ललवणात्युष्णतीक्ष्णरूक्षविदाहिनः |", engltranslit : "kaT.hvamlalavaNAtyuShNatIkShNarUkShavidAhinaH ."},
                {sanskrit : "आहारा राजसस्येष्टा दुःखशोकामयप्रदाः ||१७- ९||", engltranslit : "AhArA rAjasasyeShTA duHkhashokAmayapradAH .. 17\-9.."},
                {sanskrit : "यातयामं गतरसं पूति पर्युषितं च यत् |", engltranslit : "yAtayAmaM gatarasaM pUti paryuShita.n cha yat.h ."},
                {sanskrit : "उच्छिष्टमपि चामेध्यं भोजनं तामसप्रियम् ||१७- १०||", engltranslit : "uchchhiShTamapi chAmedhyaM bhojanaM tAmasapriyam.h .. 17\-10.."},
                {sanskrit : "अफलाङ्क्षिभिर्यज्ञो विधिदृष्टो य इज्यते |", engltranslit : "aphalAN^kShibhiryaGYo vidhidR^iShTo ya ijyate ."},
                {sanskrit : "यष्टव्यमेवेति मनः समाधाय स सात्त्विकः ||१७- ११||", engltranslit : "yaShTavyameveti manaH samAdhAya sa sAttvikaH .. 17\-11.."},
                {sanskrit : "अभिसन्धाय तु फलं दम्भार्थमपि चैव यत् |", engltranslit : "abhisandhAya tu phalaM dambhArthamapi chaiva yat.h ."},
                {sanskrit : "इज्यते भरतश्रेष्ठ तं यज्ञं विद्धि राजसम् ||१७- १२||", engltranslit : "ijyate bharatashreShTha taM yaGYa.n viddhi rAjasam.h .. 17\-12.."},
                {sanskrit : "विधिहीनमसृष्टान्नं मन्त्रहीनमदक्षिणम् |", engltranslit : "vidhihInamasR^iShTAnnaM mantrahInamadakShiNam.h ."},
                {sanskrit : "श्रद्धाविरहितं यज्ञं तामसं परिचक्षते ||१७- १३||", engltranslit : "shraddhAvirahitaM yaGYa.n tAmasaM parichakShate .. 17\-13.."},
                {sanskrit : "देवद्विजगुरुप्राज्ञपूजनं शौचमार्जवम् |", engltranslit : "devadvijaguruprAGYapUjanaM shauchamArjavam.h ."},
                {sanskrit : "ब्रह्मचर्यमहिंसा च शारीरं तप उच्यते ||१७- १४||", engltranslit : "brahmacharyamahi.nsA cha shArIraM tapa uchyate .. 17\-14.."},
                {sanskrit : "अनुद्वेगकरं वाक्यं सत्यं प्रियहितं च यत् |", engltranslit : "anudvegakaraM vAkya.n satyaM priyahita.n cha yat.h ."},
                {sanskrit : "स्वाध्यायाभ्यसनं चैव वाङ्मयं तप उच्यते ||१७- १५||", engltranslit : "svAdhyAyAbhyasana.n chaiva vAN^mayaM tapa uchyate .. 17\-15.."},
                {sanskrit : "मनः प्रसादः सौम्यत्वं मौनमात्मविनिग्रहः |", engltranslit : "manaH prasAdaH saumyatvaM maunamAtmavinigrahaH ."},
                {sanskrit : "भावसंशुद्धिरित्येतत्तपो मानसमुच्यते ||१७- १६||", engltranslit : "bhAvasa.nshuddhirityetattapo mAnasamuchyate .. 17\-16.."},
                {sanskrit : "श्रद्धया परया तप्तं तपस्तत्त्रिविधं नरैः |", engltranslit : "shraddhayA parayA tapta.n tapastattrividhaM naraiH ."},
                {sanskrit : "अफलाकाङ्क्षिभिर्युक्तैः सात्त्विकं परिचक्षते ||१७- १७||", engltranslit : "aphalAkAN^kShibhiryuktaiH sAttvikaM parichakShate .. 17\-17.."},
                {sanskrit : "सत्कारमानपूजार्थं तपो दम्भेन चैव यत् |", engltranslit : "satkAramAnapUjArtha.n tapo dambhena chaiva yat.h ."},
                {sanskrit : "क्रियते तदिह प्रोक्तं राजसं चलमध्रुवम् ||१७- १८||", engltranslit : "kriyate tadiha prokta.n rAjasa.n chalamadhruvam.h .. 17\-18.."},
                {sanskrit : "मूढग्राहेणात्मनो यत्पीडया क्रियते तपः |", engltranslit : "mUDhagrAheNAtmano yatpIDayA kriyate tapaH ."},
                {sanskrit : "परस्योत्सादनार्थं वा तत्तामसमुदाहृतम् ||१७- १९||", engltranslit : "parasyotsAdanArtha.n vA tattAmasamudAhR^itam.h .. 17\-19.."},
                {sanskrit : "दातव्यमिति यद्दानं दीयतेऽनुपकारिणे |", engltranslit : "dAtavyamiti yaddAnaM dIyate.anupakAriNe ."},
                {sanskrit : "देशे काले च पात्रे च तद्दानं सात्त्विकं स्मृतम् ||१७- २०||", engltranslit : "deshe kAle cha pAtre cha taddAnaM sAttvikaM smR^itam.h .. 17\-20.."},
                {sanskrit : "यत्तु प्रत्युपकारार्थं फलमुद्दिश्य वा पुनः |", engltranslit : "yattu pratyupakArArthaM phalamuddishya vA punaH ."},
                {sanskrit : "दीयते च परिक्लिष्टं तद्दानं राजसं स्मृतम् ||१७- २१||", engltranslit : "dIyate cha parikliShTa.n taddAnaM rAjasaM smR^itam.h .. 17\-21.."},
                {sanskrit : "अदेशकाले यद्दानमपात्रेभ्यश्च दीयते |", engltranslit : "adeshakAle yaddAnamapAtrebhyashcha dIyate ."},
                {sanskrit : "असत्कृतमवज्ञातं तत्तामसमुदाहृतम् ||१७- २२||", engltranslit : "asatkR^itamavaGYAtaM tattAmasamudAhR^itam.h .. 17\-22.."},
                {sanskrit : "ॐतत्सदिति निर्देशो ब्रह्मणस्त्रिविधः स्मृतः |", engltranslit : "AUMtatsaditi nirdesho brahmaNastrividhaH smR^itaH ."},
                {sanskrit : "ब्राह्मणास्तेन वेदाश्च यज्ञाश्च विहिताः पुरा ||१७- २३||", engltranslit : "brAhmaNAstena vedAshcha yaGYAshcha vihitAH purA .. 17\-23.."},
                {sanskrit : "तस्मादोमित्युदाहृत्य यज्ञदानतपःक्रियाः |", engltranslit : "tasmAdomityudAhR^itya yaGYadAnatapaHkriyAH ."},
                {sanskrit : "प्रवर्तन्ते विधानोक्ताः सततं ब्रह्मवादिनाम् ||१७- २४||", engltranslit : "pravartante vidhAnoktAH satataM brahmavAdinAm.h .. 17\-24.."},
                {sanskrit : "तदित्यनभिसन्धाय फलं यज्ञतपःक्रियाः |", engltranslit : "tadityanabhisandhAya phalaM yaGYatapaHkriyAH ."},
                {sanskrit : "दानक्रियाश्च विविधाः क्रियन्ते मोक्षकाङ्क्षिभिः ||१७- २५||", engltranslit : "dAnakriyAshcha vividhAH kriyante mokShakAN^kShibhiH .. 17\-25.."},
                {sanskrit : "सद्भावे साधुभावे च सदित्येतत्प्रयुज्यते |", engltranslit : "sadbhAve sAdhubhAve cha sadityetatprayujyate ."},
                {sanskrit : "प्रशस्ते कर्मणि तथा सच्छब्दः पार्थ युज्यते ||१७- २६||", engltranslit : "prashaste karmaNi tathA sachchhabdaH pArtha yujyate .. 17\-26.."},
                {sanskrit : "यज्ञे तपसि दाने च स्थितिः सदिति चोच्यते |", engltranslit : "yaGYe tapasi dAne cha sthitiH saditi chochyate ."},
                {sanskrit : "कर्म चैव तदर्थीयं सदित्येवाभिधीयते ||१७- २७||", engltranslit : "karma chaiva tadarthIyaM sadityevAbhidhIyate .. 17\-27.."},
                {sanskrit : "अश्रद्धया हुतं दत्तं तपस्तप्तं कृतं च यत् |", engltranslit : "ashraddhayA hutaM datta.n tapastapta.n kR^ita.n cha yat.h ."},
                {sanskrit : "असदित्युच्यते पार्थ न च तत्प्रेत्य नो इह ||१७- २८||", engltranslit : "asadityuchyate pArtha na cha tatpretya no iha .. 17\-28.."},
                {sanskrit : "ॐ तत्सदिति श्रीमद्भगवद्गीतासूपनिषत्सु", engltranslit : "AUM tatsaditi shrImadbhagavadgItAsUpaniShatsu"},
                {sanskrit : "ब्रह्मविद्यायां योगशास्त्रे श्रीकृष्णार्जुनसंवादे", engltranslit : "brahmavidyAyA.n yogashAstre shrIkR^iShNArjunasa.nvAde"},
                {sanskrit : "श्रद्धात्रयविभागयोगो नाम सप्तदशोऽध्यायः ||१७||", engltranslit : "shraddhAtrayavibhAgayogo nAma saptadasho.adhyAyaH .. 17.."},
                {sanskrit : "अथाष्टादशोऽध्यायः | मोक्षसंन्यासयोगः", engltranslit : "athAShTAdasho.adhyAyaH . mokShasa.nnyAsayogaH"},
                {sanskrit : "अर्जुन उवाच |", engltranslit : "arjuna uvAcha ."},
                {sanskrit : "संन्यासस्य महाबाहो तत्त्वमिच्छामि वेदितुम् |", engltranslit : "sa.nnyAsasya mahAbAho tattvamichchhAmi veditum.h ."},
                {sanskrit : "त्यागस्य च हृषीकेश पृथक्केशिनिषूदन ||१८- १||", engltranslit : "tyAgasya cha hR^iShIkesha pR^ithakkeshiniShUdana .. 18\-1.."},
                {sanskrit : "श्रीभगवानुवाच |", engltranslit : "shrIbhagavAnuvAcha ."},
                {sanskrit : "काम्यानां कर्मणां न्यासं संन्यासं कवयो विदुः |", engltranslit : "kAmyAnA.n karmaNA.n nyAsaM sa.nnyAsaM kavayo viduH ."},
                {sanskrit : "सर्वकर्मफलत्यागं प्राहुस्त्यागं विचक्षणाः ||१८- २||", engltranslit : "sarvakarmaphalatyAgaM prAhustyAgaM vichakShaNAH .. 18\-2.."},
                {sanskrit : "त्याज्यं दोषवदित्येके कर्म प्राहुर्मनीषिणः |", engltranslit : "tyAjya.n doShavadityeke karma prAhurmanIShiNaH ."},
                {sanskrit : "यज्ञदानतपःकर्म न त्याज्यमिति चापरे ||१८- ३||", engltranslit : "yaGYadAnatapaHkarma na tyAjyamiti chApare .. 18\-3.."},
                {sanskrit : "निश्चयं शृणु मे तत्र त्यागे भरतसत्तम |", engltranslit : "nishchayaM shR^iNu me tatra tyAge bharatasattama ."},
                {sanskrit : "त्यागो हि पुरुषव्याघ्र त्रिविधः सम्प्रकीर्तितः ||१८- ४||", engltranslit : "tyAgo hi puruShavyAghra trividhaH samprakIrtitaH .. 18\-4.."},
                {sanskrit : "यज्ञदानतपःकर्म न त्याज्यं कार्यमेव तत् |", engltranslit : "yaGYadAnatapaHkarma na tyAjya.n kAryameva tat.h ."},
                {sanskrit : "यज्ञो दानं तपश्चैव पावनानि मनीषिणाम् ||१८- ५||", engltranslit : "yaGYo dAnaM tapashchaiva pAvanAni manIShiNAm.h .. 18\-5.."},
                {sanskrit : "एतान्यपि तु कर्माणि सङ्गं त्यक्त्वा फलानि च |", engltranslit : "etAnyapi tu karmANi saN^ga.n tyaktvA phalAni cha ."},
                {sanskrit : "कर्तव्यानीति मे पार्थ निश्चितं मतमुत्तमम् ||१८- ६||", engltranslit : "kartavyAnIti me pArtha nishchitaM matamuttamam.h .. 18\-6.."},
                {sanskrit : "नियतस्य तु संन्यासः कर्मणो नोपपद्यते |", engltranslit : "niyatasya tu sa.nnyAsaH karmaNo nopapadyate ."},
                {sanskrit : "मोहात्तस्य परित्यागस्तामसः परिकीर्तितः ||१८- ७||", engltranslit : "mohAttasya parityAgastAmasaH parikIrtitaH .. 18\-7.."},
                {sanskrit : "दुःखमित्येव यत्कर्म कायक्लेशभयात्त्यजेत् |", engltranslit : "duHkhamityeva yatkarma kAyakleshabhayAttyajet.h ."},
                {sanskrit : "स कृत्वा राजसं त्यागं नैव त्यागफलं लभेत् ||१८- ८||", engltranslit : "sa kR^itvA rAjasaM tyAgaM naiva tyAgaphalaM labhet.h .. 18\-8.."},
                {sanskrit : "कार्यमित्येव यत्कर्म नियतं क्रियतेऽर्जुन |", engltranslit : "kAryamityeva yatkarma niyataM kriyate.arjuna ."},
                {sanskrit : "सङ्गं त्यक्त्वा फलं चैव स त्यागः सात्त्विको मतः ||१८- ९||", engltranslit : "saN^ga.n tyaktvA phala.n chaiva sa tyAgaH sAttviko mataH .. 18\-9.."},
                {sanskrit : "न द्वेष्ट्यकुशलं कर्म कुशले नानुषज्जते |", engltranslit : "na dveShTyakushalaM karma kushale nAnuShajjate ."},
                {sanskrit : "त्यागी सत्त्वसमाविष्टो मेधावी छिन्नसंशयः ||१८- १०||", engltranslit : "tyAgI sattvasamAviShTo medhAvI chhinnasa.nshayaH .. 18\-10.."},
                {sanskrit : "न हि देहभृता शक्यं त्यक्तुं कर्माण्यशेषतः |", engltranslit : "na hi dehabhR^itA shakya.n tyaktuM karmANyasheShataH ."},
                {sanskrit : "यस्तु कर्मफलत्यागी स त्यागीत्यभिधीयते ||१८- ११||", engltranslit : "yastu karmaphalatyAgI sa tyAgItyabhidhIyate .. 18\-11.."},
                {sanskrit : "अनिष्टमिष्टं मिश्रं च त्रिविधं कर्मणः फलम् |", engltranslit : "aniShTamiShTaM mishra.n cha trividhaM karmaNaH phalam.h ."},
                {sanskrit : "भवत्यत्यागिनां प्रेत्य न तु संन्यासिनां क्वचित् ||१८- १२||", engltranslit : "bhavatyatyAginAM pretya na tu sa.nnyAsinA.n kvachit.h .. 18\-12.."},
                {sanskrit : "पञ्चैतानि महाबाहो कारणानि निबोध मे |", engltranslit : "pa~nchaitAni mahAbAho kAraNAni nibodha me ."},
                {sanskrit : "साङ्ख्ये कृतान्ते प्रोक्तानि सिद्धये सर्वकर्मणाम् ||१८- १३||", engltranslit : "sAN^khye kR^itAnte proktAni siddhaye sarvakarmaNAm.h .. 18\-13.."},
                {sanskrit : "अधिष्ठानं तथा कर्ता करणं च पृथग्विधम् |", engltranslit : "adhiShThAnaM tathA kartA karaNa.n cha pR^ithagvidham.h ."},
                {sanskrit : "विविधाश्च पृथक्चेष्टा दैवं चैवात्र पञ्चमम् ||१८- १४||", engltranslit : "vividhAshcha pR^ithakcheShTA daiva.n chaivAtra pa~nchamam.h .. 18\-14.."},
                {sanskrit : "शरीरवाङ्मनोभिर्यत्कर्म प्रारभते नरः |", engltranslit : "sharIravAN^manobhiryatkarma prArabhate naraH ."},
                {sanskrit : "न्याय्यं वा विपरीतं वा पञ्चैते तस्य हेतवः ||१८- १५||", engltranslit : "nyAyya.n vA viparItaM vA pa~nchaite tasya hetavaH .. 18\-15.."},
                {sanskrit : "तत्रैवं सति कर्तारमात्मानं केवलं तु यः |", engltranslit : "tatraivaM sati kartAramAtmAnaM kevalaM tu yaH ."},
                {sanskrit : "पश्यत्यकृतबुद्धित्वान्न स पश्यति दुर्मतिः ||१८- १६||", engltranslit : "pashyatyakR^itabuddhitvAnna sa pashyati durmatiH .. 18\-16.."},
                {sanskrit : "यस्य नाहंकृतो भावो बुद्धिर्यस्य न लिप्यते |", engltranslit : "yasya nAha.nkR^ito bhAvo buddhiryasya na lipyate ."},
                {sanskrit : "हत्वाऽपि स इमाँल्लोकान्न हन्ति न निबध्यते ||१८- १७||", engltranslit : "hatvA.api sa imA.NllokAnna hanti na nibadhyate .. 18\-17.."},
                {sanskrit : "ज्ञानं ज्ञेयं परिज्ञाता त्रिविधा कर्मचोदना |", engltranslit : "GYAnaM GYeyaM pariGYAtA trividhA karmachodanA ."},
                {sanskrit : "करणं कर्म कर्तेति त्रिविधः कर्मसंग्रहः ||१८- १८||", engltranslit : "karaNaM karma karteti trividhaH karmasa.ngrahaH .. 18\-18.."},
                {sanskrit : "ज्ञानं कर्म च कर्ताच त्रिधैव गुणभेदतः |", engltranslit : "GYAnaM karma cha kartAcha tridhaiva guNabhedataH ."},
                {sanskrit : "प्रोच्यते गुणसङ्ख्याने यथावच्छृणु तान्यपि ||१८- १९||", engltranslit : "prochyate guNasaN^khyAne yathAvachchhR^iNu tAnyapi .. 18\-19.."},
                {sanskrit : "सर्वभूतेषु येनैकं भावमव्ययमीक्षते |", engltranslit : "sarvabhUteShu yenaikaM bhAvamavyayamIkShate ."},
                {sanskrit : "अविभक्तं विभक्तेषु तज्ज्ञानं विद्धि सात्त्विकम् ||१८- २०||", engltranslit : "avibhakta.n vibhakteShu tajGYAnaM viddhi sAttvikam.h .. 18\-20.."},
                {sanskrit : "पृथक्त्वेन तु यज्ज्ञानं नानाभावान्पृथग्विधान् |", engltranslit : "pR^ithaktvena tu yajGYAnaM nAnAbhAvAnpR^ithagvidhAn.h ."},
                {sanskrit : "वेत्ति सर्वेषु भूतेषु तज्ज्ञानं विद्धि राजसम् ||१८- २१||", engltranslit : "vetti sarveShu bhUteShu tajGYAnaM viddhi rAjasam.h .. 18\-21.."},
                {sanskrit : "यत्तु कृत्स्नवदेकस्मिन्कार्ये सक्तमहैतुकम् |", engltranslit : "yattu kR^itsnavadekasminkArye saktamahaitukam.h ."},
                {sanskrit : "अतत्त्वार्थवदल्पं च तत्तामसमुदाहृतम् ||१८- २२||", engltranslit : "atattvArthavadalpa.n cha tattAmasamudAhR^itam.h .. 18\-22.."},
                {sanskrit : "नियतं सङ्गरहितमरागद्वेषतः कृतम् |", engltranslit : "niyataM saN^garahitamarAgadveShataH kR^itam.h ."},
                {sanskrit : "अफलप्रेप्सुना कर्म यत्तत्सात्त्विकमुच्यते ||१८- २३||", engltranslit : "aphalaprepsunA karma yattatsAttvikamuchyate .. 18\-23.."},
                {sanskrit : "यत्तु कामेप्सुना कर्म साहंकारेण वा पुनः |", engltranslit : "yattu kAmepsunA karma sAha.nkAreNa vA punaH ."},
                {sanskrit : "क्रियते बहुलायासं तद्राजसमुदाहृतम् ||१८- २४||", engltranslit : "kriyate bahulAyAsaM tadrAjasamudAhR^itam.h .. 18\-24.."},
                {sanskrit : "अनुबन्धं क्षयं हिंसामनपेक्ष्य च पौरुषम् |", engltranslit : "anubandha.n kShayaM hi.nsAmanapekShya cha pauruSham.h ."},
                {sanskrit : "मोहादारभ्यते कर्म यत्तत्तामसमुच्यते ||१८- २५||", engltranslit : "mohAdArabhyate karma yattattAmasamuchyate .. 18\-25.."},
                {sanskrit : "मुक्तसङ्गोऽनहंवादी धृत्युत्साहसमन्वितः |", engltranslit : "muktasaN^go.anaha.nvAdI dhR^ityutsAhasamanvitaH ."},
                {sanskrit : "सिद्ध्यसिद्ध्योर्निर्विकारः कर्ता सात्त्विक उच्यते ||१८- २६||", engltranslit : "sid.hdhyasid.hdhyornirvikAraH kartA sAttvika uchyate .. 18\-26.."},
                {sanskrit : "रागी कर्मफलप्रेप्सुर्लुब्धो हिंसात्मकोऽशुचिः |", engltranslit : "rAgI karmaphalaprepsurlubdho hi.nsAtmako.ashuchiH ."},
                {sanskrit : "हर्षशोकान्वितः कर्ता राजसः परिकीर्तितः ||१८- २७||", engltranslit : "harShashokAnvitaH kartA rAjasaH parikIrtitaH .. 18\-27.."},
                {sanskrit : "अयुक्तः प्राकृतः स्तब्धः शठो नैष्कृतिकोऽलसः |", engltranslit : "ayuktaH prAkR^itaH stabdhaH shaTho naiShkR^itiko.alasaH ."},
                {sanskrit : "विषादी दीर्घसूत्री च कर्ता तामस उच्यते ||१८- २८||", engltranslit : "viShAdI dIrghasUtrI cha kartA tAmasa uchyate .. 18\-28.."},
                {sanskrit : "बुद्धेर्भेदं धृतेश्चैव गुणतस्त्रिविधं शृणु |", engltranslit : "buddherbhedaM dhR^iteshchaiva guNatastrividhaM shR^iNu ."},
                {sanskrit : "प्रोच्यमानमशेषेण पृथक्त्वेन धनञ्जय ||१८- २९||", engltranslit : "prochyamAnamasheSheNa pR^ithaktvena dhana~njaya .. 18\-29.."},
                {sanskrit : "प्रवृत्तिं च निवृत्तिं च कार्याकार्ये भयाभये |", engltranslit : "pravR^itti.n cha nivR^itti.n cha kAryAkArye bhayAbhaye ."},
                {sanskrit : "बन्धं मोक्षं च या वेत्ति बुद्धिः सा पार्थ सात्त्विकी ||१८- ३०||", engltranslit : "bandhaM mokSha.n cha yA vetti buddhiH sA pArtha sAttvikI .. 18\-30.."},
                {sanskrit : "यया धर्ममधर्मं च कार्यं चाकार्यमेव च |", engltranslit : "yayA dharmamadharma.n cha kArya.n chAkAryameva cha ."},
                {sanskrit : "अयथावत्प्रजानाति बुद्धिः सा पार्थ राजसी ||१८- ३१||", engltranslit : "ayathAvatprajAnAti buddhiH sA pArtha rAjasI .. 18\-31.."},
                {sanskrit : "अधर्मं धर्ममिति या मन्यते तमसावृता |", engltranslit : "adharma.n dharmamiti yA manyate tamasAvR^itA ."},
                {sanskrit : "सर्वार्थान्विपरीतांश्च बुद्धिः सा पार्थ तामसी ||१८- ३२||", engltranslit : "sarvArthAnviparItA.nshcha buddhiH sA pArtha tAmasI .. 18\-32.."},
                {sanskrit : "धृत्या यया धारयते मनःप्राणेन्द्रियक्रियाः |", engltranslit : "dhR^ityA yayA dhArayate manaHprANendriyakriyAH ."},
                {sanskrit : "योगेनाव्यभिचारिण्या धृतिः सा पार्थ सात्त्विकी ||१८- ३३||", engltranslit : "yogenAvyabhichAriNyA dhR^itiH sA pArtha sAttvikI .. 18\-33.."},
                {sanskrit : "यया तु धर्मकामार्थान्धृत्या धारयतेऽर्जुन |", engltranslit : "yayA tu dharmakAmArthAndhR^ityA dhArayate.arjuna ."},
                {sanskrit : "प्रसङ्गेन फलाकाङ्क्षी धृतिः सा पार्थ राजसी ||१८- ३४||", engltranslit : "prasaN^gena phalAkAN^kShI dhR^itiH sA pArtha rAjasI .. 18\-34.."},
                {sanskrit : "यया स्वप्नं भयं शोकं विषादं मदमेव च |", engltranslit : "yayA svapnaM bhayaM shokaM viShAdaM madameva cha ."},
                {sanskrit : "न विमुञ्चति दुर्मेधा धृतिः सा पार्थ तामसी ||१८- ३५||", engltranslit : "na vimu~nchati durmedhA dhR^itiH sA pArtha tAmasI .. 18\-35.."},
                {sanskrit : "सुखं त्विदानीं त्रिविधं शृणु मे भरतर्षभ |", engltranslit : "sukhaM tvidAnI.n trividhaM shR^iNu me bharatarShabha ."},
                {sanskrit : "अभ्यासाद्रमते यत्र दुःखान्तं च निगच्छति ||१८- ३६||", engltranslit : "abhyAsAdramate yatra duHkhAnta.n cha nigachchhati .. 18\-36.."},
                {sanskrit : "यत्तदग्रे विषमिव परिणामेऽमृतोपमम् |", engltranslit : "yattadagre viShamiva pariNAme.amR^itopamam.h ."},
                {sanskrit : "तत्सुखं सात्त्विकं प्रोक्तमात्मबुद्धिप्रसादजम् ||१८- ३७||", engltranslit : "tatsukhaM sAttvikaM proktamAtmabuddhiprasAdajam.h .. 18\-37.."},
                {sanskrit : "विषयेन्द्रियसंयोगाद्यत्तदग्रेऽमृतोपमम् |", engltranslit : "viShayendriyasa.nyogAdyattadagre.amR^itopamam.h ."},
                {sanskrit : "परिणामे विषमिव तत्सुखं राजसं स्मृतम् ||१८- ३८||", engltranslit : "pariNAme viShamiva tatsukhaM rAjasaM smR^itam.h .. 18\-38.."},
                {sanskrit : "यदग्रे चानुबन्धे च सुखं मोहनमात्मनः |", engltranslit : "yadagre chAnubandhe cha sukhaM mohanamAtmanaH ."},
                {sanskrit : "निद्रालस्यप्रमादोत्थं तत्तामसमुदाहृतम् ||१८- ३९||", engltranslit : "nidrAlasyapramAdottha.n tattAmasamudAhR^itam.h .. 18\-39.."},
                {sanskrit : "न तदस्ति पृथिव्यां वा दिवि देवेषु वा पुनः |", engltranslit : "na tadasti pR^ithivyA.n vA divi deveShu vA punaH ."},
                {sanskrit : "सत्त्वं प्रकृतिजैर्मुक्तं यदेभिः स्यात्त्रिभिर्गुणैः ||१८- ४०||", engltranslit : "sattvaM prakR^itijairmukta.n yadebhiH syAttribhirguNaiH .. 18\-40.."},
                {sanskrit : "ब्राह्मणक्षत्रियविशां शूद्राणां च परन्तप |", engltranslit : "brAhmaNakShatriyavishA.n shUdrANA.n cha parantapa ."},
                {sanskrit : "कर्माणि प्रविभक्तानि स्वभावप्रभवैर्गुणैः ||१८- ४१||", engltranslit : "karmANi pravibhaktAni svabhAvaprabhavairguNaiH .. 18\-41.."},
                {sanskrit : "शमो दमस्तपः शौचं क्षान्तिरार्जवमेव च |", engltranslit : "shamo damastapaH shauchaM kShAntirArjavameva cha ."},
                {sanskrit : "ज्ञानं विज्ञानमास्तिक्यं ब्रह्मकर्म स्वभावजम् ||१८- ४२||", engltranslit : "GYAnaM viGYAnamAstikyaM brahmakarma svabhAvajam.h .. 18\-42.."},
                {sanskrit : "शौर्यं तेजो धृतिर्दाक्ष्यं युद्धे चाप्यपलायनम् |", engltranslit : "shaurya.n tejo dhR^itirdAkShya.n yuddhe chApyapalAyanam.h ."},
                {sanskrit : "दानमीश्वरभावश्च क्षात्रं कर्म स्वभावजम् ||१८- ४३||", engltranslit : "dAnamIshvarabhAvashcha kShAtraM karma svabhAvajam.h .. 18\-43.."},
                {sanskrit : "कृषिगौरक्ष्यवाणिज्यं वैश्यकर्म स्वभावजम् |", engltranslit : "kR^iShigaurakShyavANijya.n vaishyakarma svabhAvajam.h ."},
                {sanskrit : "परिचर्यात्मकं कर्म शूद्रस्यापि स्वभावजम् ||१८- ४४||", engltranslit : "paricharyAtmakaM karma shUdrasyApi svabhAvajam.h .. 18\-44.."},
                {sanskrit : "स्वे स्वे कर्मण्यभिरतः संसिद्धिं लभते नरः |", engltranslit : "sve sve karmaNyabhirataH sa.nsiddhi.n labhate naraH ."},
                {sanskrit : "स्वकर्मनिरतः सिद्धिं यथा विन्दति तच्छृणु ||१८- ४५||", engltranslit : "svakarmanirataH siddhi.n yathA vindati tachchhR^iNu .. 18\-45.."},
                {sanskrit : "यतः प्रवृत्तिर्भूतानां येन सर्वमिदं ततम् |", engltranslit : "yataH pravR^ittirbhUtAnA.n yena sarvamidaM tatam.h ."},
                {sanskrit : "स्वकर्मणा तमभ्यर्च्य सिद्धिं विन्दति मानवः ||१८- ४६||", engltranslit : "svakarmaNA tamabhyarchya siddhi.n vindati mAnavaH .. 18\-46.."},
                {sanskrit : "श्रेयान्स्वधर्मो विगुणः परधर्मात्स्वनुष्ठितात् |", engltranslit : "shreyAnsvadharmo viguNaH paradharmAtsvanuShThitAt.h ."},
                {sanskrit : "स्वभावनियतं कर्म कुर्वन्नाप्नोति किल्बिषम् ||१८- ४७||", engltranslit : "svabhAvaniyataM karma kurvannApnoti kilbiSham.h .. 18\-47.."},
                {sanskrit : "सहजं कर्म कौन्तेय सदोषमपि न त्यजेत् |", engltranslit : "sahajaM karma kaunteya sadoShamapi na tyajet.h ."},
                {sanskrit : "सर्वारम्भा हि दोषेण धूमेनाग्निरिवावृताः ||१८- ४८||", engltranslit : "sarvArambhA hi doSheNa dhUmenAgnirivAvR^itAH .. 18\-48.."},
                {sanskrit : "असक्तबुद्धिः सर्वत्र जितात्मा विगतस्पृहः |", engltranslit : "asaktabuddhiH sarvatra jitAtmA vigataspR^ihaH ."},
                {sanskrit : "नैष्कर्म्यसिद्धिं परमां संन्यासेनाधिगच्छति ||१८- ४९||", engltranslit : "naiShkarmyasiddhiM paramA.n sa.nnyAsenAdhigachchhati .. 18\-49.."},
                {sanskrit : "सिद्धिं प्राप्तो यथा ब्रह्म तथाप्नोति निबोध मे |", engltranslit : "siddhiM prApto yathA brahma tathApnoti nibodha me ."},
                {sanskrit : "समासेनैव कौन्तेय निष्ठा ज्ञानस्य या परा ||१८- ५०||", engltranslit : "samAsenaiva kaunteya niShThA GYAnasya yA parA .. 18\-50.."},
                {sanskrit : "बुद्ध्या विशुद्धया युक्तो धृत्यात्मानं नियम्य च |", engltranslit : "bud.hdhyA vishuddhayA yukto dhR^ityAtmAnaM niyamya cha ."},
                {sanskrit : "शब्दादीन्विषयांस्त्यक्त्वा रागद्वेषौ व्युदस्य च ||१८- ५१||", engltranslit : "shabdAdInviShayA.nstyaktvA rAgadveShau vyudasya cha .. 18\-51.."},
                {sanskrit : "विविक्तसेवी लघ्वाशी यतवाक्कायमानसः |", engltranslit : "viviktasevI laghvAshI yatavAkkAyamAnasaH ."},
                {sanskrit : "ध्यानयोगपरो नित्यं वैराग्यं समुपाश्रितः ||१८- ५२||", engltranslit : "dhyAnayogaparo nitya.n vairAgya.n samupAshritaH .. 18\-52.."},
                {sanskrit : "अहंकारं बलं दर्पं कामं क्रोधं परिग्रहम् |", engltranslit : "aha.nkAraM balaM darpa.n kAmaM krodhaM parigraham.h ."},
                {sanskrit : "विमुच्य निर्ममः शान्तो ब्रह्मभूयाय कल्पते ||१८- ५३||", engltranslit : "vimuchya nirmamaH shAnto brahmabhUyAya kalpate .. 18\-53.."},
                {sanskrit : "ब्रह्मभूतः प्रसन्नात्मा न शोचति न काङ्क्षति |", engltranslit : "brahmabhUtaH prasannAtmA na shochati na kAN^kShati ."},
                {sanskrit : "समः सर्वेषु भूतेषु मद्भक्तिं लभते पराम् ||१८- ५४||", engltranslit : "samaH sarveShu bhUteShu madbhakti.n labhate parAm.h .. 18\-54.."},
                {sanskrit : "भक्त्या मामभिजानाति यावान्यश्चास्मि तत्त्वतः |", engltranslit : "bhaktyA mAmabhijAnAti yAvAnyashchAsmi tattvataH ."},
                {sanskrit : "ततो मां तत्त्वतो ज्ञात्वा विशते तदनन्तरम् ||१८- ५५||", engltranslit : "tato mAM tattvato GYAtvA vishate tadanantaram.h .. 18\-55.."},
                {sanskrit : "सर्वकर्माण्यपि सदा कुर्वाणो मद्व्यपाश्रयः |", engltranslit : "sarvakarmANyapi sadA kurvANo mad.hvyapAshrayaH ."},
                {sanskrit : "मत्प्रसादादवाप्नोति शाश्वतं पदमव्ययम् ||१८- ५६||", engltranslit : "matprasAdAdavApnoti shAshvataM padamavyayam.h .. 18\-56.."},
                {sanskrit : "चेतसा सर्वकर्माणि मयि संन्यस्य मत्परः |", engltranslit : "chetasA sarvakarmANi mayi sa.nnyasya matparaH ."},
                {sanskrit : "बुद्धियोगमुपाश्रित्य मच्चित्तः सततं भव ||१८- ५७||", engltranslit : "buddhiyogamupAshritya machchittaH satataM bhava .. 18\-57.."},
                {sanskrit : "मच्चित्तः सर्वदुर्गाणि मत्प्रसादात्तरिष्यसि |", engltranslit : "machchittaH sarvadurgANi matprasAdAttariShyasi ."},
                {sanskrit : "अथ चेत्त्वमहंकारान्न श्रोष्यसि विनङ्क्ष्यसि ||१८- ५८||", engltranslit : "atha chettvamaha.nkArAnna shroShyasi vinaN^kShyasi .. 18\-58.."},
                {sanskrit : "यदहंकारमाश्रित्य न योत्स्य इति मन्यसे |", engltranslit : "yadaha.nkAramAshritya na yotsya iti manyase ."},
                {sanskrit : "मिथ्यैष व्यवसायस्ते प्रकृतिस्त्वां नियोक्ष्यति ||१८- ५९||", engltranslit : "mithyaiSha vyavasAyaste prakR^itistvA.n niyokShyati .. 18\-59.."},
                {sanskrit : "स्वभावजेन कौन्तेय निबद्धः स्वेन कर्मणा |", engltranslit : "svabhAvajena kaunteya nibaddhaH svena karmaNA ."},
                {sanskrit : "कर्तुं नेच्छसि यन्मोहात्करिष्यस्यवशोपि तत् ||१८- ६०||", engltranslit : "kartuM nechchhasi yanmohAtkariShyasyavashopi tat.h .. 18\-60.."},
                {sanskrit : "ईश्वरः सर्वभूतानां हृद्देशेऽर्जुन तिष्ठति |", engltranslit : "IshvaraH sarvabhUtAnA.n hR^iddeshe.arjuna tiShThati ."},
                {sanskrit : "भ्रामयन्सर्वभूतानि यन्त्रारूढानि मायया ||१८- ६१||", engltranslit : "bhrAmayansarvabhUtAni yantrArUDhAni mAyayA .. 18\-61.."},
                {sanskrit : "तमेव शरणं गच्छ सर्वभावेन भारत |", engltranslit : "tameva sharaNaM gachchha sarvabhAvena bhArata ."},
                {sanskrit : "तत्प्रसादात्परां शान्तिं स्थानं प्राप्स्यसि शाश्वतम् ||१८- ६२||", engltranslit : "tatprasAdAtparAM shAntiM sthAnaM prApsyasi shAshvatam.h .. 18\-62.."},
                {sanskrit : "इति ते ज्ञानमाख्यातं गुह्याद्गुह्यतरं मया |", engltranslit : "iti te GYAnamAkhyAtaM guhyAd.hguhyataraM mayA ."},
                {sanskrit : "विमृश्यैतदशेषेण यथेच्छसि तथा कुरु ||१८- ६३||", engltranslit : "vimR^ishyaitadasheSheNa yathechchhasi tathA kuru .. 18\-63.."},
                {sanskrit : "सर्वगुह्यतमं भूयः शृणु मे परमं वचः |", engltranslit : "sarvaguhyatamaM bhUyaH shR^iNu me paramaM vachaH ."},
                {sanskrit : "इष्टोऽसि मे दृढमिति ततो वक्ष्यामि ते हितम् ||१८- ६४||", engltranslit : "iShTo.asi me dR^iDhamiti tato vakShyAmi te hitam.h .. 18\-64.."},
                {sanskrit : "मन्मना भव मद्भक्तो मद्याजी मां नमस्कुरु |", engltranslit : "manmanA bhava madbhakto madyAjI mA.n namaskuru ."},
                {sanskrit : "मामेवैष्यसि सत्यं ते प्रतिजाने प्रियोऽसि मे ||१८- ६५||", engltranslit : "mAmevaiShyasi satya.n te pratijAne priyo.asi me .. 18\-65.."},
                {sanskrit : "सर्वधर्मान्परित्यज्य मामेकं शरणं व्रज |", engltranslit : "sarvadharmAnparityajya mAmekaM sharaNaM vraja ."},
                {sanskrit : "अहं त्वा सर्वपापेभ्यो मोक्षयिष्यामि मा शुचः ||१८- ६६||", engltranslit : "ahaM tvA sarvapApebhyo mokShayiShyAmi mA shuchaH .. 18\-66.."},
                {sanskrit : "इदं ते नातपस्काय नाभक्ताय कदाचन |", engltranslit : "idaM te nAtapaskAya nAbhaktAya kadAchana ."},
                {sanskrit : "न चाशुश्रूषवे वाच्यं न च मां योऽभ्यसूयति ||१८- ६७||", engltranslit : "na chAshushrUShave vAchya.n na cha mA.n yo.abhyasUyati .. 18\-67.."},
                {sanskrit : "य इदं परमं गुह्यं मद्भक्तेष्वभिधास्यति |", engltranslit : "ya idaM paramaM guhyaM madbhakteShvabhidhAsyati ."},
                {sanskrit : "भक्तिं मयि परां कृत्वा मामेवैष्यत्यसंशयः ||१८- ६८||", engltranslit : "bhaktiM mayi parAM kR^itvA mAmevaiShyatyasa.nshayaH .. 18\-68.."},
                {sanskrit : "न च तस्मान्मनुष्येषु कश्चिन्मे प्रियकृत्तमः |", engltranslit : "na cha tasmAnmanuShyeShu kashchinme priyakR^ittamaH ."},
                {sanskrit : "भविता न च मे तस्मादन्यः प्रियतरो भुवि ||१८- ६९||", engltranslit : "bhavitA na cha me tasmAdanyaH priyataro bhuvi .. 18\-69.."},
                {sanskrit : "अध्येष्यते च य इमं धर्म्यं संवादमावयोः |", engltranslit : "adhyeShyate cha ya imaM dharmya.n sa.nvAdamAvayoH ."},
                {sanskrit : "ज्ञानयज्ञेन तेनाहमिष्टः स्यामिति मे मतिः ||१८- ७०||", engltranslit : "GYAnayaGYena tenAhamiShTaH syAmiti me matiH .. 18\-70.."},
                {sanskrit : "श्रद्धावाननसूयश्च शृणुयादपि यो नरः |", engltranslit : "shraddhAvAnanasUyashcha shR^iNuyAdapi yo naraH ."},
                {sanskrit : "सोऽपि मुक्तः शुभाँल्लोकान्प्राप्नुयात्पुण्यकर्मणाम् ||१८- ७१||", engltranslit : "so.api muktaH shubhA.NllokAnprApnuyAtpuNyakarmaNAm.h .. 18\-71.."},
                {sanskrit : "कच्चिदेतच्छ्रुतं पार्थ त्वयैकाग्रेण चेतसा |", engltranslit : "kachchidetachchhrutaM pArtha tvayaikAgreNa chetasA ."},
                {sanskrit : "कच्चिदज्ञानसम्मोहः प्रनष्टस्ते धनञ्जय ||१८- ७२||", engltranslit : "kachchidaGYAnasammohaH pranaShTaste dhana~njaya .. 18\-72.."},
                {sanskrit : "अर्जुन उवाच |", engltranslit : "arjuna uvAcha ."},
                {sanskrit : "नष्टो मोहः स्मृतिर्लब्धा त्वत्प्रसादान्मयाच्युत |", engltranslit : "naShTo mohaH smR^itirlabdhA tvatprasAdAnmayAchyuta ."},
                {sanskrit : "स्थितोऽस्मि गतसन्देहः करिष्ये वचनं तव ||१८- ७३||", engltranslit : "sthito.asmi gatasandehaH kariShye vachanaM tava .. 18\-73.."},
                {sanskrit : "सञ्जय उवाच |", engltranslit : "sa~njaya uvAcha ."},
                {sanskrit : "इत्यहं वासुदेवस्य पार्थस्य च महात्मनः |", engltranslit : "ityahaM vAsudevasya pArthasya cha mahAtmanaH ."},
                {sanskrit : "संवादमिममश्रौषमद्भुतं रोमहर्षणम् ||१८- ७४||", engltranslit : "sa.nvAdamimamashrauShamadbhutaM romaharShaNam.h .. 18\-74.."},
                {sanskrit : "व्यासप्रसादाच्छ्रुतवानेतद्गुह्यमहं परम् |", engltranslit : "vyAsaprasAdAchchhrutavAnetadguhyamahaM param.h ."},
                {sanskrit : "योगं योगेश्वरात्कृष्णात्साक्षात्कथयतः स्वयम् ||१८- ७५||", engltranslit : "yogaM yogeshvarAtkR^iShNAtsAkShAtkathayataH svayam.h .. 18\-75.."},
                {sanskrit : "राजन्संस्मृत्य संस्मृत्य संवादमिममद्भुतम् |", engltranslit : "rAjansa.nsmR^itya sa.nsmR^itya sa.nvAdamimamadbhutam.h ."},
                {sanskrit : "केशवार्जुनयोः पुण्यं हृष्यामि च मुहुर्मुहुः ||१८- ७६||", engltranslit : "keshavArjunayoH puNyaM hR^iShyAmi cha muhurmuhuH .. 18\-76.."},
                {sanskrit : "तच्च संस्मृत्य संस्मृत्य रूपमत्यद्भुतं हरेः |", engltranslit : "tachcha sa.nsmR^itya sa.nsmR^itya rUpamatyadbhutaM hareH ."},
                {sanskrit : "विस्मयो मे महान् राजन्हृष्यामि च पुनः पुनः ||१८- ७७||", engltranslit : "vismayo me mahAn.h rAjanhR^iShyAmi cha punaH punaH .. 18\-77.."},
                {sanskrit : "यत्र योगेश्वरः कृष्णो यत्र पार्थो धनुर्धरः |", engltranslit : "yatra yogeshvaraH kR^iShNo yatra pArtho dhanurdharaH ."},
                {sanskrit : "तत्र श्रीर्विजयो भूतिर्ध्रुवा नीतिर्मतिर्मम ||१८- ७८||", engltranslit : "tatra shrIrvijayo bhUtirdhruvA nItirmatirmama .. 18\-78.."},
                {sanskrit : "ॐ तत्सदिति श्रीमद्भगवद्गीतासूपनिषत्सु", engltranslit : "AUM tatsaditi shrImadbhagavadgItAsUpaniShatsu"},
                {sanskrit : "ब्रह्मविद्यायां योगशास्त्रे श्रीकृष्णार्जुनसंवादे", engltranslit : "brahmavidyAyA.n yogashAstre shrIkR^iShNArjunasa.nvAde"},
                {sanskrit : "मोक्षसंन्यासयोगो नाम अष्टादशोऽध्यायः ||१८||", engltranslit : "mokShasa.nnyAsayogo nAma aShTAdasho.adhyAyaH .. 18.."},
                {sanskrit : "ॐ", engltranslit : "AUM"},
                {sanskrit : "शान्ताकारं भुजगशयनं पद्मनाभं सुरेशम् |", engltranslit : "shAntAkAraM bhujagashayanaM padmanAbhaM suresham.h ."},
                {sanskrit : "विश्वाधारं गगनसदृशं मेघवर्णं शुभाङ्गम् |", engltranslit : "vishvAdhAraM gaganasadR^ishaM meghavarNaM shubhA~Ngam.h ."},
                {sanskrit : "लक्ष्मीकान्तं कमलनयनं योगिभिर्ध्यानगम्यम् |", engltranslit : "lakShmIkAntaM kamalanayanaM yogibhirdhyAnagamyam.h ."},
                {sanskrit : "वन्दे विष्णुं भवभयहरं सर्वलोकैकनाथम् ||", engltranslit : "vande viShNuM bhavabhayaharaM sarvalokaikanAtham.h .."}

